('error_default', '<!DOCTYPE html>\n<html lang=\"<txp:lang />\" dir=\"<txp:text item=\"lang_dir\" />\">\n\n<head>\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, viewport-fit=cover\">\n    <title><txp:error_status /></title>\n\n    <!-- CSS -->\n    <txp:css format=\"link\" media=\"\" />\n    <!-- ...or you can use (faster) external CSS files e.g. <link rel=\"stylesheet\" href=\"<txp:page_url type=\"theme_path\" />/styles/default.css\"> -->\n\n    <meta name=\"generator\" content=\"Textpattern CMS\">\n    <meta name=\"robots\" content=\"none\">\n\n    <!-- content feeds -->\n    <txp:feed_link flavor=\"atom\" format=\"link\" label=\"Atom\" />\n    <txp:feed_link flavor=\"rss\" format=\"link\" label=\"RSS\" />\n</head>\n\n<body class=\"error-page\" itemscope itemtype=\"https://schema.org/WebPage\">\n\n    <txp:output_form form=\"body_header\" />\n\n    <div class=\"wrapper\">\n        <div class=\"container\">\n\n            <!-- Main content -->\n            <main id=\"main\" aria-label=\"<txp:text item=\"main_content\" />\">\n                <h1 class=\"error-status\"><txp:error_status /></h1>\n                <p class=\"error-msg\"><txp:error_message /></p>\n            </main>\n\n            <txp:output_form form=\"body_aside\" />\n\n        </div> <!-- /.container -->\n    </div> <!-- /.wrapper -->\n\n    <txp:output_form form=\"body_footer\" />\n\n</body>\n</html>', 'four-point-eight', NULL),
('default', '<!DOCTYPE html>\n<html lang=\"<txp:lang />\" dir=\"<txp:text item=\"lang_dir\" />\">\n\n<head>\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, viewport-fit=cover\">\n    <title><txp:page_title /></title>\n\n    <!-- CSS -->\n    <txp:css format=\"link\" media=\"\" />\n    <!-- ...or you can use (faster) external CSS files e.g. <link rel=\"stylesheet\" href=\"<txp:page_url type=\"theme_path\" />/styles/default.css\"> -->\n\n    <meta name=\"generator\" content=\"Textpattern CMS\">\n\n    <txp:if_search>\n        <meta name=\"robots\" content=\"none\">\n    <txp:else />\n        <txp:if_author>\n            <meta name=\"robots\" content=\"noindex, follow\">\n        <txp:else />\n            <txp:meta_description />\n            <txp:if_category>\n                <meta name=\"robots\" content=\"noindex, follow\">\n            <txp:else />\n                <txp:variable name=\"page\" value=\'<txp:page_url type=\"pg\" />\' />\n                <txp:if_variable name=\"page\" value=\"1\">\n                    <meta name=\"robots\" content=\"index, follow\">\n                    <link rel=\"canonical\" href=\"<txp:site_url />\">\n                    <script type=\"application/ld+json\">\n                        {\n                            \"@context\": \"https://schema.org\",\n                            \"@type\": \"WebSite\",\n                            \"headline\": \"<txp:site_name escape=\"json\" />\",\n                            <txp:if_description>\"description\": \"<txp:meta_description format=\"\" escape=\"json\" />\",</txp:if_description>\n                            \"url\": \"<txp:site_url escape=\"json\" />\"\n                        }\n                    </script>\n                <txp:else />\n                    <meta name=\"robots\" content=\"noindex, follow\">\n                </txp:if_variable>\n                <txp:variable name=\"front-page\" value=\"1\" />\n            </txp:if_category>\n        </txp:if_author>\n    </txp:if_search>\n\n    <!-- content feeds -->\n    <txp:feed_link flavor=\"atom\" format=\"link\" label=\"Atom\" />\n    <txp:feed_link flavor=\"rss\" format=\"link\" label=\"RSS\" />\n</head>\n\n<txp:if_variable name=\"front-page\">\n    <body class=\"front-page\" itemscope itemtype=\"https://schema.org/WebPage\">\n<txp:else />\n    <txp:if_search>\n        <body class=\"search-page\" itemscope itemtype=\"https://schema.org/SearchResultsPage\">\n    <txp:else />\n        <body class=\"listing-page\" itemscope itemtype=\"https://schema.org/CollectionPage\">\n    </txp:if_search>\n</txp:if_variable>\n\n    <txp:output_form form=\"body_header\" />\n\n    <div class=\"wrapper\">\n        <div class=\"container\">\n\n            <!-- Main content -->\n            <main id=\"main\" aria-label=\"<txp:text item=\"main_content\" />\" itemscope itemtype=\"https://schema.org/Blog\">\n\n                <!-- is this the search result page? also omits the pagination links below (uses pagination format within search_display.txp instead) -->\n                <txp:if_search>\n                    <txp:output_form form=\"search_display\" />\n\n                <txp:else />\n                    <!-- else is this the front page? -->\n                    <txp:if_variable name=\"front-page\">\n                        <!-- links by default to form: \'default.txp\' unless you specify a different form -->\n                        <txp:article limit=\"5\" />\n\n                    <txp:else />\n                        <!-- else is this an article author list? -->\n                        <txp:if_author>\n                            <h1 itemprop=\"name\"><txp:text item=\"author\" /> <txp:author /></h1>\n\n                        <txp:else />\n                            <!-- else is this an article category list? -->\n                            <txp:if_category>\n                                <h1 itemprop=\"name\"><txp:text item=\"category\" /> <txp:category title /></h1>\n                            </txp:if_category>\n                        </txp:if_author>\n                        <txp:article form=\"article_listing\" limit=\"10\" wraptag=\"ul\" class=\"article-list\" />\n                    </txp:if_variable>\n\n                    <!-- add pagination links to foot of front page/author listings/category listings if there are more articles available -->\n                    <txp:evaluate test=\"newer,older\">\n                        <nav class=\"paginator\" aria-label=\"<txp:text item=\"page_nav\" />\">\n                            <txp:newer rel=\"prev\">\n                                <txp:text item=\"newer\" />\n                            </txp:newer>\n                            <txp:older rel=\"next\">\n                                <txp:text item=\"older\" />\n                            </txp:older>\n                        </nav>\n                    </txp:evaluate>\n                </txp:if_search>\n\n            </main>\n\n            <txp:output_form form=\"body_aside\" />\n\n        </div> <!-- /.container -->\n    </div> <!-- /.wrapper -->\n\n    <txp:output_form form=\"body_footer\" />\n\n</body>\n</html>', 'four-point-eight', NULL),
('archive', '<!DOCTYPE html>\n<html lang=\"<txp:lang />\" dir=\"<txp:text item=\"lang_dir\" />\">\n\n<head>\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, viewport-fit=cover\">\n    <title><txp:page_title /></title>\n\n    <!-- CSS -->\n    <txp:css format=\"link\" media=\"\" />\n    <!-- ...or you can use (faster) external CSS files e.g. <link rel=\"stylesheet\" href=\"<txp:page_url type=\"theme_path\" />/styles/default.css\"> -->\n\n    <meta name=\"generator\" content=\"Textpattern CMS\">\n\n    <txp:if_search>\n        <meta name=\"robots\" content=\"none\">\n    <txp:else />\n        <txp:if_author>\n            <meta name=\"robots\" content=\"noindex, follow\">\n        <txp:else />\n            <txp:meta_description />\n            <txp:if_article_list>\n                <txp:variable name=\"page\" value=\'<txp:page_url type=\"pg\" />\' />\n                <txp:if_variable name=\"page\" value=\"1\">\n                    <meta name=\"robots\" content=\"index, follow\">\n                    <link rel=\"canonical\" href=\"<txp:section url />\">\n                    <script type=\"application/ld+json\">\n                        {\n                            \"@context\": \"https://schema.org\",\n                            \"@type\": \"Blog\",\n                            \"headline\": \"<txp:section title escape=\"json\" />\",\n                            <txp:if_description>\"description\": \"<txp:meta_description format=\"\" escape=\"json\" />\",</txp:if_description>\n                            \"url\": \"<txp:section url escape=\"json\" />\"\n                        }\n                    </script>\n                <txp:else />\n                    <meta name=\"robots\" content=\"noindex, follow\">\n                </txp:if_variable>\n            <txp:else />\n                <txp:meta_keywords separator=\",\" />\n                <txp:meta_author title />\n                <meta name=\"robots\" content=\"<txp:if_expires>unavailable_after: <txp:expires gmt format=\"%d-%b-%y %T\" /> GMT<txp:else />index, follow</txp:if_expires>\">\n                <link rel=\"canonical\" href=\"<txp:permlink />\">\n                <meta property=\"og:site_name\" content=\"<txp:site_name />\">\n                <meta property=\"og:type\" content=\"article\">\n                <meta property=\"og:title\" content=\"<txp:title />\">\n                <txp:if_description><meta property=\"og:description\" content=\"<txp:meta_description format=\"\" />\"></txp:if_description>\n                <meta property=\"og:url\" content=\"<txp:permlink />\">\n                <txp:if_article_image>\n                    <txp:images limit=\"1\">\n                        <meta property=\"og:image\" content=\"<txp:image_url />\">\n                        <meta property=\"og:image:width\" content=\"<txp:image_info type=\"w\" />\">\n                        <meta property=\"og:image:height\" content=\"<txp:image_info type=\"h\" />\">\n                        <meta property=\"og:image:alt\" content=\"<txp:image_info type=\"alt\" />\">\n                    </txp:images>\n                </txp:if_article_image>\n                <script type=\"application/ld+json\">\n                    {\n                        \"@context\": \"https://schema.org\",\n                        \"@type\": \"Blog\",\n                        \"blogPost\": [{\n                            \"@type\": \"BlogPosting\",\n                            \"headline\": \"<txp:title escape=\"json\" />\",\n                            <txp:if_description>\"description\": \"<txp:meta_description format=\"\" escape=\"json\" />\",</txp:if_description>\n                            \"url\": \"<txp:permlink escape=\"json\" />\"\n                        }]\n                    }\n                </script>\n            </txp:if_article_list>\n        </txp:if_author>\n    </txp:if_search>\n\n    <!-- content feeds -->\n    <txp:feed_link flavor=\"atom\" format=\"link\" label=\"Atom\" />\n    <txp:feed_link flavor=\"rss\" format=\"link\" label=\"RSS\" />\n</head>\n\n<txp:if_search>\n    <body class=\"search-page\" itemscope itemtype=\"https://schema.org/SearchResultsPage\">\n<txp:else />\n    <txp:if_article_list>\n        <body class=\"<txp:section />-page\" itemscope itemtype=\"https://schema.org/CollectionPage\">\n    <txp:else />\n        <body class=\"<txp:section />-page\" itemscope itemtype=\"https://schema.org/WebPage\">\n    </txp:if_article_list>\n</txp:if_search>\n\n    <txp:output_form form=\"body_header\" />\n\n    <div class=\"wrapper\">\n        <div class=\"container\">\n\n            <!-- Main content -->\n            <main id=\"main\" aria-label=\"<txp:text item=\"main_content\" />\" itemscope itemtype=\"https://schema.org/Blog\">\n\n                <!-- is this the search result page? also omits the pagination links below (uses pagination format within search_display.txp instead) -->\n                <txp:if_search>\n                    <txp:output_form form=\"search_display\" />\n\n                <txp:else />\n                    <txp:if_article_list>\n\n                        <h1 itemprop=\"name\"><txp:section title /></h1>\n                        <txp:article class=\"article-list\" form=\"article_listing\" limit=\"10\" wraptag=\"ul\" />\n                        <!-- ...or if you want to list all articles from all sections instead, then replace txp:article with txp:article_custom -->\n\n                        <!-- add pagination links to foot of article listings if there are more articles available -->\n                        <txp:evaluate test=\"newer,older\">\n                            <nav class=\"paginator\" aria-label=\"<txp:text item=\"page_nav\" />\">\n                                <txp:newer rel=\"prev\">\n                                    <txp:text item=\"newer\" />\n                                </txp:newer>\n                                <txp:older rel=\"next\">\n                                    <txp:text item=\"older\" />\n                                </txp:older>\n                            </nav>\n                        </txp:evaluate>\n\n                    <txp:else />\n                        <txp:article status />\n\n                        <!-- add pagination links to foot of article if there are more articles available -->\n                        <txp:evaluate test=\"link_to_prev,link_to_next\">\n                            <nav class=\"paginator\" aria-label=\"<txp:text item=\"page_nav\" />\">\n                                <txp:link_to_prev>\n                                    <txp:text item=\"prev\" />\n                                </txp:link_to_prev>\n                                <txp:link_to_next>\n                                    <txp:text item=\"next\" />\n                                </txp:link_to_next>\n                            </nav>\n                        </txp:evaluate>\n                    </txp:if_article_list>\n                </txp:if_search>\n\n            </main>\n\n            <txp:output_form form=\"body_aside\" />\n\n        </div> <!-- /.container -->\n    </div> <!-- /.wrapper -->\n\n    <txp:output_form form=\"body_footer\" />\n\n</body>\n</html>', 'four-point-eight', NULL),
('error_default', '<!DOCTYPE html>\r\n<html lang=\"<txp:lang />\" dir=\"<txp:text item=\"lang_dir\" />\">\r\n\r\n<head>\r\n    <meta charset=\"utf-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, viewport-fit=cover\">\r\n    <title><txp:error_status /></title>\r\n\r\n<link rel=\"stylesheet\" href=\"<txp:site_url />css/wave.css\">\r\n\r\n    <meta name=\"generator\" content=\"Textpattern CMS\">\r\n    <meta name=\"robots\" content=\"none\">\r\n\r\n</head>\r\n\r\n<body class=\"error-page\" itemscope itemtype=\"https://schema.org/WebPage\">\r\n\r\n    <txp:output_form form=\"body_header\" />\r\n\r\n    <div class=\"wrapper\">\r\n        <div class=\"container\">\r\n\r\n            <!-- Main content -->\r\n            <main id=\"main\" aria-label=\"<txp:text item=\"main_content\" />\">\r\n                <h1 class=\"error-status\"><txp:error_status /></h1>\r\n                <p class=\"error-msg\"><txp:error_message /><br /><br /><txp:link_to_home>Home</txp:link_to_home></p>\r\n            </main>\r\n\r\n        </div> <!-- /.container -->\r\n    </div> <!-- /.wrapper -->\r\n\r\n</body>\r\n</html>', 'wave', NULL),
('default', '<!DOCTYPE html>\r\n<html lang=\"<txp:lang />\" dir=\"<txp:text item=\"lang_dir\" />\">\r\n\r\n<head>\r\n    <meta charset=\"utf-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, viewport-fit=cover\">\r\n    <title><txp:page_title /></title>\r\n\r\n<link rel=\"stylesheet\" href=\"<txp:site_url />css/wave.css\">\r\n\r\n    <meta name=\"generator\" content=\"Textpattern CMS\">\r\n\r\n    <txp:if_search>\r\n        <meta name=\"robots\" content=\"none\">\r\n    <txp:else />\r\n            <txp:meta_description />\r\n                    <meta name=\"robots\" content=\"index, nofollow\">\r\n                    <link rel=\"canonical\" href=\"<txp:site_url />\">\r\n                    <script type=\"application/ld+json\">\r\n                        {\r\n                            \"@context\": \"https://schema.org\",\r\n                            \"@type\": \"WebSite\",\r\n                            \"headline\": \"<txp:site_name escape=\"json\" />\",\r\n                            <txp:if_description>\"description\": \"<txp:meta_description format=\"\" escape=\"json\" />\",</txp:if_description>\r\n                            \"url\": \"<txp:site_url escape=\"json\" />\"\r\n                        }\r\n                    </script>\r\n                <txp:variable name=\"front-page\" value=\"1\" />\r\n    </txp:if_search>\r\n<link rel=\"icon\" type=\"image/png\" href=\"/favicon.png\">\r\n<link rel=\"icon\" type=\"image/x-icon\" href=\"/favicon.ico\">\r\n</head>\r\n\r\n<!-- oooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo -->\r\n<!-- oooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo -->\r\n\r\n<txp:if_variable name=\"front-page\">\r\n    <body class=\"front-page\" itemscope itemtype=\"https://schema.org/WebPage\">\r\n<txp:else />\r\n    <txp:if_search>\r\n        <body class=\"search-page\" itemscope itemtype=\"https://schema.org/SearchResultsPage\">\r\n    <txp:else />\r\n        <body class=\"listing-page\" itemscope itemtype=\"https://schema.org/CollectionPage\">\r\n    </txp:if_search>\r\n</txp:if_variable>\r\n\r\n<main id=\"main\" aria-label=\"<txp:text item=\"main_content\" />\" itemscope itemtype=\"https://schema.org/Blog\">\r\n\r\n    <txp:output_form form=\"body_header\" />\r\n\r\n	<hr class=\"top\" />\r\n\r\n                <txp:if_search>\r\n                    <txp:output_form form=\"search_display\" />\r\n                </main>\r\n                <txp:else />\r\n\r\n                    <txp:if_variable name=\"front-page\">\r\n\r\n\r\n<!-- oooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo -->\r\n\r\n<txp:article_custom form=\"big-intro\" id=\"1\" limit=\"1\" />\r\n\r\n<!-- oooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo -->\r\n\r\n<hr />\r\n<section id=\"what\">\r\n<txp:article_custom form=\"intro-what\" id=\"2\" limit=\"1\" />\r\n<txp:article_custom form=\"what\" id=\"3\" limit=\"1\" />\r\n<hr />\r\n<txp:article_custom form=\"what\" id=\"4\" limit=\"1\" />\r\n<hr />\r\n<txp:article_custom form=\"what\" id=\"5\" limit=\"1\" />\r\n</section>\r\n\r\n<!-- oooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo -->\r\n\r\n<hr />\r\n<section id=\"who\">\r\n<txp:article_custom form=\"intro-who\" id=\"2\" limit=\"1\" />\r\n<txp:article_custom id=\"6\" form=\"who\" limit=\"1\" />\r\n<hr />\r\n<txp:article_custom id=\"7\" limit=\"1\" />\r\n<hr />>\r\n<txp:article_custom id=\"8\" limit=\"1\" />\r\n</section>\r\n\r\n<!-- oooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo -->\r\n\r\n\r\n<hr />\r\n<section id=\"why\">\r\n<txp:article_custom form=\"intro-why\" id=\"2\" limit=\"1\" />\r\n<txp:article_custom id=\"3\" limit=\"1\" />\r\n<!-- ooooooo -->\r\n<hr id=\"how\" />\r\n<txp:article_custom id=\"4\" limit=\"1\" />\r\n<!-- ooooooo -->\r\n<hr id=\"how2\" />\r\n<txp:article_custom id=\"5\" limit=\"1\" />\r\n<!-- ooooooo -->\r\n<hr id=\"seo\" />\r\n<txp:article_custom id=\"6\" limit=\"1\" />\r\n</section>\r\n\r\n<!-- oooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo -->\r\n\r\n<txp:hide>\r\n<txp:output_form form=\"body_aside\" />\r\n</txp:hide>\r\n\r\n<hr />\r\n\r\n</main>\r\n\r\n<!-- oooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo -->\r\n\r\n<txp:article_custom id=\"9\" form=\"footer\" limit=\"1\" />\r\n\r\n<!-- oooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooo -->\r\n\r\n                    </txp:if_variable>\r\n\r\n                </txp:if_search>\r\n\r\n</body>\r\n</html>', 'wave', NULL),
('archive', '<!DOCTYPE html>\n<html lang=\"<txp:lang />\" dir=\"<txp:text item=\"lang_dir\" />\">\n\n<head>\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, viewport-fit=cover\">\n    <title><txp:page_title /></title>\n\n    <!-- CSS -->\n    <txp:css format=\"link\" media=\"\" />\n    <!-- ...or you can use (faster) external CSS files e.g. <link rel=\"stylesheet\" href=\"<txp:page_url type=\"theme_path\" />/styles/default.css\"> -->\n\n    <meta name=\"generator\" content=\"Textpattern CMS\">\n\n    <txp:if_search>\n        <meta name=\"robots\" content=\"none\">\n    <txp:else />\n        <txp:if_author>\n            <meta name=\"robots\" content=\"noindex, follow\">\n        <txp:else />\n            <txp:meta_description />\n            <txp:if_article_list>\n                <txp:variable name=\"page\" value=\'<txp:page_url type=\"pg\" />\' />\n                <txp:if_variable name=\"page\" value=\"1\">\n                    <meta name=\"robots\" content=\"index, follow\">\n                    <link rel=\"canonical\" href=\"<txp:section url />\">\n                    <script type=\"application/ld+json\">\n                        {\n                            \"@context\": \"https://schema.org\",\n                            \"@type\": \"Blog\",\n                            \"headline\": \"<txp:section title escape=\"json\" />\",\n                            <txp:if_description>\"description\": \"<txp:meta_description format=\"\" escape=\"json\" />\",</txp:if_description>\n                            \"url\": \"<txp:section url escape=\"json\" />\"\n                        }\n                    </script>\n                <txp:else />\n                    <meta name=\"robots\" content=\"noindex, follow\">\n                </txp:if_variable>\n            <txp:else />\n                <txp:meta_keywords separator=\",\" />\n                <txp:meta_author title />\n                <meta name=\"robots\" content=\"<txp:if_expires>unavailable_after: <txp:expires gmt format=\"%d-%b-%y %T\" /> GMT<txp:else />index, follow</txp:if_expires>\">\n                <link rel=\"canonical\" href=\"<txp:permlink />\">\n                <meta property=\"og:site_name\" content=\"<txp:site_name />\">\n                <meta property=\"og:type\" content=\"article\">\n                <meta property=\"og:title\" content=\"<txp:title />\">\n                <txp:if_description><meta property=\"og:description\" content=\"<txp:meta_description format=\"\" />\"></txp:if_description>\n                <meta property=\"og:url\" content=\"<txp:permlink />\">\n                <txp:if_article_image>\n                    <txp:images limit=\"1\">\n                        <meta property=\"og:image\" content=\"<txp:image_url />\">\n                        <meta property=\"og:image:width\" content=\"<txp:image_info type=\"w\" />\">\n                        <meta property=\"og:image:height\" content=\"<txp:image_info type=\"h\" />\">\n                        <meta property=\"og:image:alt\" content=\"<txp:image_info type=\"alt\" />\">\n                    </txp:images>\n                </txp:if_article_image>\n                <script type=\"application/ld+json\">\n                    {\n                        \"@context\": \"https://schema.org\",\n                        \"@type\": \"Blog\",\n                        \"blogPost\": [{\n                            \"@type\": \"BlogPosting\",\n                            \"headline\": \"<txp:title escape=\"json\" />\",\n                            <txp:if_description>\"description\": \"<txp:meta_description format=\"\" escape=\"json\" />\",</txp:if_description>\n                            \"url\": \"<txp:permlink escape=\"json\" />\"\n                        }]\n                    }\n                </script>\n            </txp:if_article_list>\n        </txp:if_author>\n    </txp:if_search>\n\n    <!-- content feeds -->\n    <txp:feed_link flavor=\"atom\" format=\"link\" label=\"Atom\" />\n    <txp:feed_link flavor=\"rss\" format=\"link\" label=\"RSS\" />\n</head>\n\n<txp:if_search>\n    <body class=\"search-page\" itemscope itemtype=\"https://schema.org/SearchResultsPage\">\n<txp:else />\n    <txp:if_article_list>\n        <body class=\"<txp:section />-page\" itemscope itemtype=\"https://schema.org/CollectionPage\">\n    <txp:else />\n        <body class=\"<txp:section />-page\" itemscope itemtype=\"https://schema.org/WebPage\">\n    </txp:if_article_list>\n</txp:if_search>\n\n    <txp:output_form form=\"body_header\" />\n\n    <div class=\"wrapper\">\n        <div class=\"container\">\n\n            <!-- Main content -->\n            <main id=\"main\" aria-label=\"<txp:text item=\"main_content\" />\" itemscope itemtype=\"https://schema.org/Blog\">\n\n                <!-- is this the search result page? also omits the pagination links below (uses pagination format within search_display.txp instead) -->\n                <txp:if_search>\n                    <txp:output_form form=\"search_display\" />\n\n                <txp:else />\n                    <txp:if_article_list>\n\n                        <h1 itemprop=\"name\"><txp:section title /></h1>\n                        <txp:article class=\"article-list\" form=\"article_listing\" limit=\"10\" wraptag=\"ul\" />\n                        <!-- ...or if you want to list all articles from all sections instead, then replace txp:article with txp:article_custom -->\n\n                        <!-- add pagination links to foot of article listings if there are more articles available -->\n                        <txp:evaluate test=\"newer,older\">\n                            <nav class=\"paginator\" aria-label=\"<txp:text item=\"page_nav\" />\">\n                                <txp:newer rel=\"prev\">\n                                    <txp:text item=\"newer\" />\n                                </txp:newer>\n                                <txp:older rel=\"next\">\n                                    <txp:text item=\"older\" />\n                                </txp:older>\n                            </nav>\n                        </txp:evaluate>\n\n                    <txp:else />\n                        <txp:article status />\n\n                        <!-- add pagination links to foot of article if there are more articles available -->\n                        <txp:evaluate test=\"link_to_prev,link_to_next\">\n                            <nav class=\"paginator\" aria-label=\"<txp:text item=\"page_nav\" />\">\n                                <txp:link_to_prev>\n                                    <txp:text item=\"prev\" />\n                                </txp:link_to_prev>\n                                <txp:link_to_next>\n                                    <txp:text item=\"next\" />\n                                </txp:link_to_next>\n                            </nav>\n                        </txp:evaluate>\n                    </txp:if_article_list>\n                </txp:if_search>\n\n            </main>\n\n            <txp:output_form form=\"body_aside\" />\n\n        </div> <!-- /.container -->\n    </div> <!-- /.wrapper -->\n\n    <txp:output_form form=\"body_footer\" />\n\n</body>\n</html>', 'wave', NULL);
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `textpack`, `data`, `type`, `load_order`, `flags`) VALUES
('com_connect', 0, 'Textpattern Community', 'https://forum.textpattern.io/viewtopic.php?id=47913', '4.6.0', 'Form and contact mailer for Textpattern', '<h1>com_connect</h1>\n\n<h2>Introduction</h2>\n\n<p>A Textpattern <span class=\"caps\">CMS</span> form mailer plugin. <code>&lt;txp:com_connect /&gt;</code> produces a flexible, customisable email contact form. It is intended for use as an enquiry form for commercial and private sites, and includes several features to help reduce common problems with such forms (invalid email addresses, missing information).</p>\n\n<p>Please report bugs and problems with this plugin at <a href=\"https://github.com/textpattern/com_connect/issues\" rel=\"nofollow\">the GitHub project&#8217;s issues page</a>.</p>\n\n<h3>Features</h3>\n\n<ul>\n	<li>Arbitrary HTML5 text fields can be specified, with min/max/required settings for validation.</li>\n	<li>Email address validation, including a check for a valid MX record (Unix only).</li>\n	<li>Safe escaping of input data.</li>\n	<li><span class=\"caps\">UTF</span>-8 safe.</li>\n	<li>Accessible form layout, including <code>&lt;label&gt;</code>, <code>&lt;legend&gt;</code> and <code>&lt;fieldset&gt;</code> tags.</li>\n	<li>Various classes and ids to allow easy styling of all parts of the form.</li>\n	<li>Spam prevention <span class=\"caps\">API</span> (used by Tranquillo&#8217;s <code>pap_contact_cleaner</code> plugin) and delivery <span class=\"caps\">API</span> for altering or extending the plugin&#8217;s capabilities.</li>\n</ul>\n\n<h3>History</h3>\n\n<p>Please see the <a href=\"https://github.com/textpattern/com_connect/blob/master/CHANGELOG.textile\" rel=\"nofollow\">changelog on GitHub</a>.</p>\n\n<h2>Installing and upgrading</h2>\n\n<p><strong>Requires Textpattern 4.5.0+</strong></p>\n\n<p>Download the latest release of the plugin from <a href=\"https://github.com/textpattern/com_connect/releases\" rel=\"nofollow\">the GitHub project page</a>, paste the code into the Textpattern Plugins administration panel, install and enable the plugin. Visit the <a href=\"https://forum.textpattern.io/viewtopic.php?id=47913\" rel=\"nofollow\">forum thread</a> for more info or to report on the success or otherwise of the plugin.</p>\n\n<p>To uninstall, delete from the Plugins administration panel.</p>\n\n<p>Alternatively, this plugin can be installed using <a href=\"https://getcomposer.org\" rel=\"nofollow\">Composer</a>:</p>\n\n<pre><code>$ composer require textpattern/com_connect:*</code></pre>\n\n<h2>Migrating from zem_contact_reborn</h2>\n\n<p>If upgrading from zem_contact_reborn (the previous incarnation of this plugin), please note these differences:</p>\n\n<ul>\n	<li>Tags have been globally renamed from <code>&lt;txp:zem_contact ... /&gt;</code> to <code>&lt;txp:com_connect ... /&gt;</code> &#8211; please adjust your code accordingly.</li>\n	<li>Classes <code>zemConnectForm</code>, <code>zemError</code>, <code>zemRequired</code>, <code>zemThanks</code>, <code>zemText</code>, <code>zemEmail</code>, <code>zemTextarea</code>, <code>zemSubmit</code>, <code>zemSelect</code>, <code>zemOption</code>, <code>zemCheckbox</code> and <code>zemRadio</code> have been renamed to <code>comConnectForm</code>, <code>comError</code>, <code>comRequired</code>, <code>comThanks</code>, <code>comText</code>, <code>comEmail</code>, <code>comTextarea</code>, <code>comSubmit</code>, <code>comSelect</code>, <code>comOption</code>, <code>comCheckbox</code> and <code>comRadio</code> respectively &#8211; please adjust your code accordingly.</li>\n	<li>Disable or remove the zem_contact_lang plugin. Language strings are now bundled as part of the plugin itself. If you have a translation Textpack available that is not yet bundled, please submit it for inclusion.</li>\n	<li>Classes based on the input element <code>name</code> are no longer automatically applied. Only default class names beginning with <code>com</code> are set. To employ custom classes, use the <code>class</code> attribute for each tag, or the global <code>classes</code> attribute to set names for error and information messages.</li>\n	<li>If your site&#8217;s ‘Doctype’ preference is set to <code>html5</code> you may use HTML5 attributes in your tags. Otherwise, they will be ignored.</li>\n	<li>Validation of required elements and min/max constraints is done by the browser first, and the plugin second. So if you specify a field is required and it is left empty, the browser will usually prevent the form being submitted. To bypass (most of) the browser checks, specify <code>browser_validate=&quot;0&quot;</code> in your <code>&lt;txp:com_connect /&gt;</code> tag.</li>\n</ul>\n\n<h2>Usage</h2>\n\n<h3>Contact form</h3>\n\n<p>The simplest form is shown below, which produces a default form with ‘Name’, ‘Email’ and ‘Message’ fields. Email will be delivered to &lt;code&gt;recipient@example.com&lt;/code&gt;, with the user&#8217;s supplied email as the <code>From:</code> address.</p>\n\n<pre><code>&lt;txp:com_connect to=&quot;recipient@example.com&quot; /&gt;</code></pre>\n\n<p>To specify fields explicitly, use something like this:</p>\n\n<pre><code>&lt;txp:com_connect to=&quot;recipient@example.com&quot;&gt;\n    &lt;txp:com_connect_email /&gt;\n    &lt;txp:com_connect_text label=&quot;Phone&quot; min=&quot;7&quot; max=&quot;15&quot; /&gt;\n    &lt;txp:com_connect_textarea label=&quot;Your question&quot; /&gt;\n    &lt;txp:com_connect_submit label=&quot;Send&quot; /&gt;\n&lt;/txp:com_connect&gt;</code></pre>\n\n<p>Alternatively, place the field specifications in a Textpattern form, and call it like this:</p>\n\n<pre><code>&lt;txp:com_connect to=&quot;recipient@example.com&quot; form=&quot;my-contact-form&quot; /&gt;</code></pre>\n\n<h3>Send article</h3>\n\n<p>Within the context of an individual article, this plugin can be used to send the article (or excerpt, if it exists) to an email address specified by the visitor. This requires at least two tags:</p>\n\n<ol>\n	<li><code>com_connect</code>, to create form that is initially hidden by setting the <code>send_article</code> attribute.</li>\n	<li><code>com_connect_send_article</code>, to create a ‘Send article’ link which reveals the aforementioned form when clicked.</li>\n</ol>\n\n<pre><code>&lt;txp:com_connect send_article=&quot;1&quot; /&gt;\n&lt;txp:com_connect_send_article /&gt;</code></pre>\n\n<p>By default the form contains fields for your name and email address, the recipient&#8217;s email address and a personal message, but similar to contact forms you can create your own form layout. Some things you need to know:</p>\n\n<ol>\n	<li>Set the <code>send_article</code> attribute to <code>1</code> in the <code>com_connect</code> tag.</li>\n	<li>Use a <code>com_connect_email</code> tag with the <code>send_article</code> attribute set to <code>1</code>. This field will be used as the recipient email address.</li>\n</ol>\n\n<pre><code>&lt;txp:com_connect to=&quot;you@example.com&quot; send_article=&quot;1&quot;&gt;\n    &lt;txp:com_connect_email label=&quot;Recipient Email&quot; send_article=&quot;1&quot; /&gt;\n    &lt;txp:com_connect_email label=&quot;Your Email&quot; /&gt;\n    &lt;txp:com_connect_submit label=&quot;Send Article&quot; /&gt;\n&lt;/txp:com_connect&gt;\n\n&lt;txp:com_connect_send_article /&gt;</code></pre>\n\n<h2>Tags</h2>\n\n<p><code>&lt;txp:com_connect_send_article /&gt;</code> can be used to create a ‘Send article’ link within an article form, connecting it to the contact form.</p>\n\n<p>All other tags provided by this plugin can only be used inside a <code>&lt;txp:com_connect&gt;</code> &#8211; <code>&lt;/txp:com_connect&gt;</code> container tag or in a Textpattern form used as the <code>form</code> attribute in the <code>&lt;txp:com_connect /&gt;</code> tag.</p>\n\n<p>In addition to the tags detailed in the following sections, every tag accepts a core set of common attributes. These are:</p>\n\n<ul>\n	<li><code>accesskey=&quot;character&quot;</code>&lt;br /&gt;Shortcut key to set focus on the field.</li>\n	<li><code>autofocus=&quot;boolean&quot;</code>&lt;br /&gt;To automatically focus the cursor in this field on page load. Only one field may have this property.</li>\n	<li><code>dir=&quot;value&quot;</code>&lt;br /&gt;Text direction (<code>ltr</code>, <code>rtl</code> or <code>auto</code>).</li>\n	<li><code>disabled=&quot;boolean&quot;</code>&lt;br /&gt;Whether the input control accepts user input. If set, the element does not get submitted with the form, nor is it subject to any <code>checkValidity()</code> JavaScript calls.</li>\n	<li><code>hidden=&quot;boolean&quot;</code>&lt;br /&gt;The visibility of the input control.</li>\n	<li><code>id=&quot;id&quot;</code>&lt;br /&gt;The <span class=\"caps\">HTML</span> identifier for the control.</li>\n	<li><code>lang=&quot;value&quot;</code>&lt;br /&gt;The <span class=\"caps\">ISO</span> 639 language short code (e.g. <code>en-gb</code>, <code>de-de</code>) that govern the field.</li>\n	<li><code>list=&quot;id&quot;</code>&lt;br /&gt;Used in conjunction with the <code>&lt;datalist&gt;</code> tag to specify a set of options. The id is the reference to the datalist to use.</li>\n	<li><code>readonly=&quot;boolean&quot;</code>&lt;br /&gt;Control does not accept user input, but will be processed on form submission and can be validated.</li>\n	<li><code>spellcheck=&quot;value&quot;</code>&lt;br /&gt;Whether the field is subject to spell checking (<code>true</code> = yes, <code>default</code> = browser decides, or <code>false</code> = no).</li>\n	<li><code>style=&quot;style rules&quot;</code>&lt;br /&gt;Inline <span class=\"caps\">CSS</span> <code>style</code> rules to apply to the input control.</li>\n	<li><code>tabindex=&quot;number&quot;</code>&lt;br /&gt;The order in which the cursor jumps between elements when using the tab key.</li>\n	<li><code>title=&quot;value&quot;</code>&lt;br /&gt;Usually used for hover tooltip describing the input control&#8217;s use in your application.</li>\n	<li><code>translate=&quot;boolean&quot;</code>&lt;br /&gt;Whether to subject the attribute content to language translation.</li>\n</ul>\n\n<h3>com_connect tag</h3>\n\n<pre><code>&lt;txp:com_connect /&gt;</code></pre>\n\n<p>May be used as a single (self-closing) or container tag. Place this where you want the input form to go. Status and error messages, if any, will be displayed before the form.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>body_form=&quot;form name&quot;</code>&lt;br /&gt;Use specified form for the message body text.</li>\n	<li><code>class=&quot;space-separated values&quot;</code>&lt;br /&gt;Set the <span class=\"caps\">CSS</span> <code>class</code> name of the tag. Default: <code>comConnectForm</code>. To remove <code>class</code> attribute from the element entirely, use <code>class=&quot;&quot;</code>.</li>\n	<li><code>classes=&quot;comma-separated key:value pairs&quot;</code>&lt;br /&gt;Set the <span class=\"caps\">CSS</span> classes for error / information conditions. Specify each as a pair of values separated by a colon, e.g. <code>classes=&quot;required: req_field, element: warn_field&quot;</code>. There are up to four available to customise:\n	<ul>\n		<li><code>element</code>: Set for each form field that fails validation for any reason. Default: <code>errorElement</code>.</li>\n		<li><code>wrapper</code>: The class to surround the list of errors shown above the form. Default: <code>comError</code>.</li>\n		<li><code>required</code>: Class assigned when a required element is not completed. Default: <code>comRequired</code>.</li>\n		<li><code>thanks</code>: Class applied to the wrapper around the <code>thanks_form</code>. Default: <code>comThanks</code>.</li>\n	</ul></li>\n	<li><code>copysender=&quot;boolean&quot;</code>&lt;br /&gt;Whether to send a copy of the email to the sender&#8217;s address. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is <code>0</code>.</li>\n	<li><code>expire=&quot;number&quot;</code>&lt;br /&gt;Number of seconds after which the form will expire, thus requiring a page refresh before sending. Default is <code>600</code>.</li>\n	<li><code>form=&quot;form name&quot;</code>&lt;br /&gt;Use specified form, containing the layout of the contact form fields.</li>\n	<li><code>from=&quot;email address&quot;</code>&lt;br /&gt;Email address used in the “From:” field when sending email. Defaults to the sender&#8217;s email address. If specified, the sender&#8217;s email address will be placed in the “Reply-To:” field instead.</li>\n	<li><code>from_form=&quot;form name&quot;</code>&lt;br /&gt;Use specified form (overrides <code>from</code> attribute).</li>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;Label for the contact form. If set to an empty string, display of the fieldset and legend tags will be suppressed. Default is <code>Contact</code>.</li>\n	<li><code>lang=&quot;lang-code&quot;</code>&lt;br /&gt;Override the language strings that would normally be used from the current admin-side language in force. e.g. <code>lang=&quot;fr-fr&quot;</code> would load the French language strings. A Textpack must already exist for the chosen language.</li>\n	<li><code>browser_validate=&quot;boolean&quot;</code>&lt;br /&gt;Set to 0 if you wish to stop the browser from validating form field values and ‘required’ status of input elements. The plugin itself is then solely responsible for validation and will indicate error conditions after submission. Default is <code>1</code>.</li>\n	<li><code>redirect=&quot;URL&quot;</code>&lt;br /&gt;Redirect to specified <span class=\"caps\">URL</span> (overrides <code>thanks</code> and <code>thanks_form</code> attributes). <span class=\"caps\">URL</span> must be relative to the Textpattern site <span class=\"caps\">URL</span>. Example: <code>redirect=&quot;monkey&quot;</code> would redirect to <code>http://example.com/monkey</code>.</li>\n	<li><code>required=&quot;boolean&quot;</code>&lt;br /&gt;Whether to require all tags in this contact form to be completed before the form can be submitted. Can be overridden on a field-by-field basis by using the <code>required</code> attribute in the relevant tag. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is <code>1</code>.</li>\n	<li><code>send_article=&quot;boolean&quot;</code>&lt;br /&gt;Whether to use this form to send an article. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is <code>0</code>.</li>\n	<li><code>show_error=&quot;boolean&quot;</code>&lt;br /&gt;Whether to display error and status messages. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is <code>1</code>.</li>\n	<li><code>show_input=&quot;boolean&quot;</code>&lt;br /&gt;Whether to display the form <code>&lt;input&gt;</code> fields. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is <code>1</code>.</li>\n	<li><code>subject=&quot;subject text&quot;</code>&lt;br /&gt;Subject used when sending an email. Default is the site name.</li>\n	<li><code>subject_form=&quot;form name&quot;</code>&lt;br /&gt;Use specified form (overrides <code>subject</code> attribute).</li>\n	<li><code>thanks=&quot;text&quot;</code>&lt;br /&gt;Message shown after successfully submitting a message. Default is <code>Thank you, your message has been sent</code>.</li>\n	<li><code>thanks_form=&quot;form name&quot;</code>&lt;br /&gt;Use specified form (overrides <code>thanks</code> attribute).</li>\n	<li><code>to=&quot;email address&quot;</code> %(warning)required%&lt;br /&gt;Recipient email address. Multiple recipients can be specified, separated by commas.</li>\n	<li><code>to_form=&quot;form name&quot;</code>&lt;br /&gt;Use specified form (overrides <code>to</code> attribute).</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>When used as a single tag, produces a default form with ‘Name’, ‘Email’ and ‘Message’ fields. Email will be delivered to &lt;code&gt;recipient@example.com&lt;/code&gt;, with the user&#8217;s supplied email as the <code>From:</code> address:</p>\n\n<pre><code>&lt;txp:com_connect to=&quot;recipient@example.com&quot; /&gt;</code></pre>\n\n<h5>Example 2</h5>\n\n<p>When used as a container tag, much more flexibility is allowed, for example:</p>\n\n<pre><code>&lt;txp:com_connect to=&quot;recipient@example.com&quot;&gt;\n    &lt;txp:com_connect_email /&gt;\n    &lt;txp:com_connect_text type=&quot;tel&quot; label=&quot;Phone&quot; min=&quot;7&quot; max=&quot;15&quot; /&gt;\n    &lt;txp:com_connect_textarea label=&quot;Your question&quot; /&gt;\n    &lt;txp:com_connect_submit label=&quot;Send&quot; /&gt;\n&lt;/txp:com_connect&gt;</code></pre>\n\n<h5>Example 3</h5>\n\n<p>Example with custom email message formatting, called via the <code>body_form</code> attribute:</p>\n\n<pre><code>&lt;txp:com_connect to=&quot;recipient@example.com&quot; body_form=&quot;message-formatting&quot; /&gt;</code></pre>\n\n<p>And the <code>body_form</code> form template named <code>message-formatting</code> is as follows:</p>\n\n<pre><code>============\nEmail received.\n\n&lt;txp:com_connect_if name=&quot;email&quot;&gt;&lt;txp:com_connect_label name=&quot;email&quot; /&gt;: &lt;txp:com_connect_value name=&quot;email&quot; /&gt;&lt;txp:else /&gt;Mr. Nobody&lt;/txp:com_connect_if&gt; wrote:\n\n&lt;txp:com_connect_if name=&quot;message&quot;&gt;&lt;txp:com_connect_value name=&quot;message&quot; /&gt;&lt;txp:else /&gt;Nothing much :(&lt;/txp:com_connect_if&gt;\n\n============</code></pre>\n\n<h3>com_connect_text tag</h3>\n\n<pre><code>&lt;txp:com_connect_text /&gt;</code></pre>\n\n<p>Creates a text <code>&lt;input&gt;</code> field and corresponding <code>&lt;label&gt;</code> tag. The input value will be included in the email, preceded by the label.<br />\nCreates a text <code>&lt;input&gt;</code> field. The input value will be included in the email, preceded by the label.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>autocomplete=&quot;value&quot;</code>&lt;br /&gt;Indicates whether the value of the control can be automatically completed by the browser. This attribute is ignored if the value of the type attribute is <code>password</code>. Possible values are: <code>off</code> (the browser does not automatically complete the entry) or <code>on</code> (the browser can automatically complete the value based on values that the user has entered during previous uses). Default is unset.</li>\n	<li><code>break=&quot;tag&quot;</code>&lt;br /&gt;Break tag between the <code>&lt;label&gt;</code> and <code>&lt;input&gt;</code> field. Default is <code>&lt;br /&gt;</code>. Use <code>break=&quot;&quot;</code> to put the label and input field on the same line.</li>\n	<li><code>class=&quot;space-separated values&quot;</code>&lt;br /&gt; Set the <span class=\"caps\">CSS</span> <code>class</code> name of the tag. Default: <code>comText</code>. To remove <code>class</code> attribute from the element entirely, use <code>class=&quot;&quot;</code>.</li>\n	<li><code>default=&quot;value&quot;</code>&lt;br /&gt;Default value when no input is provided.</li>\n	<li><code>html_form=&quot;id&quot;</code>&lt;br /&gt;The <span class=\"caps\">HTML</span> <code>id</code> of the <code>&lt;form&gt;</code> tag to which the field is attached. Associated with the contained form by default.</li>\n	<li><code>inputmode=&quot;value&quot;</code>&lt;br /&gt;A hint to the browser for which keyboard to display. This attribute applies when the value of the type attribute is <code>text</code>, <code>password</code> or <code>url</code>. Possible values are:\n	<ul>\n		<li><code>verbatim</code>: Alphanumeric, non-prose content such as usernames and passwords.</li>\n		<li><code>latin</code>: Latin-script input in the user&#8217;s preferred language with typing aids such as text prediction enabled. For human-to-computer communication such as search boxes.</li>\n		<li><code>latin-name</code>: As latin, but for human names.</li>\n		<li><code>latin-prose</code>: As latin, but with more aggressive typing aids. For human-to-human communication such as instant messaging for email.</li>\n		<li><code>full-width-latin</code>: As latin-prose, but for the user&#8217;s secondary languages.</li>\n		<li><code>kana</code>: Kana or romaji input, typically hiragana input, using full-width characters, with support for converting to kanji. Intended for Japanese text input.</li>\n		<li><code>katakana</code>: Katakana input, using full-width characters, with support for converting to kanji. Intended for Japanese text input.</li>\n		<li><code>numeric</code>: Numeric input, including keys for the digits 0 to 9, the user&#8217;s preferred thousands separator character, and the character for indicating negative numbers. Intended for numeric codes, e.g. credit card numbers. For actual numbers, prefer using <code>type=&quot;number&quot;</code>.</li>\n		<li><code>tel</code>: Telephone input, including asterisk and pound key. Use <code>type=&quot;tel&quot;</code> if possible instead.</li>\n		<li><code>email</code>: Email input. Use <code>&lt;txp:com_connect_email /&gt;</code> if possible instead.</li>\n		<li><code>url</code>: <span class=\"caps\">URL</span> input. Use <code>type=&quot;url&quot;</code> if possible instead.</li>\n	</ul></li>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;Text label displayed to the user. Default is <code>Text</code>.</li>\n	<li><code>label_position=&quot;text&quot;</code>&lt;br /&gt;Position of the label in relation to the <code>&lt;input&gt;</code> field. Available values: <code>before</code> or <code>after</code>. Default is <code>before</code>.</li>\n	<li><code>max=value&quot;</code>&lt;br /&gt;For character-based inputs, the maximum input value length in characters, using the HTML5 <code>maxlength</code> attribute. To remove <code>maxlength</code> attribute from the element entirely (not recommended), use <code>max=&quot;&quot;</code>. For numeric-based inputs, the maximum input value the field accepts, using the HTML5 <code>max</code> attribute (can be a negative value). Default is <code>100</code>. To remove <code>max</code> attribute from the numerical input element entirely, use <code>max=&quot;&quot;</code>.</li>\n	<li><code>min=&quot;value&quot;</code>&lt;br /&gt;For character-based inputs, the minimum input value length in characters, using the HTML5 <code>minlength</code> attribute. Default is unset, i.e., no minimum limit. For numeric-based inputs, the minimum input value the field accepts, using the HTML5 <code>min</code> attribute (can be a negative value). Default is <code>0</code>. To remove <code>min</code> attribute from the numerical input element entirely, use <code>min=&quot;&quot;</code>.</li>\n	<li><code>name=&quot;value&quot;</code>&lt;br /&gt;Field name, as used in the <span class=\"caps\">HTML</span> <code>&lt;input&gt;</code> tag.</li>\n	<li><code>pattern=&quot;regex&quot;</code>&lt;br /&gt;Regular expression that governs the format in which the field data is expected. Only used for character-based inputs.</li>\n	<li><code>placeholder=&quot;text&quot;</code>&lt;br /&gt;Text to show as a guide, when the <code>&lt;input&gt;</code> field is empty.</li>\n	<li><code>required=&quot;boolean&quot;</code>&lt;br /&gt;Whether this field must be filled out. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is whatever is set in the <code>&lt;txp:com_connect&gt;</code> tag&#8217;s <code>required</code> attribute &#8211; if neither attribute is set then default is <code>1</code>.</li>\n	<li><code>size=&quot;value&quot;</code>&lt;br/&gt;The size, in characters, of the <code>&lt;input&gt;</code> field.</li>\n	<li><code>step=&quot;value&quot;</code>&lt;br /&gt;For numeric-based inputs, the interval between min and max.</li>\n	<li><code>type=&quot;value&quot;</code>&lt;br /&gt;Type of text input. Default is <code>text</code>. Choose from:\n	<ul>\n		<li><code>color</code></li>\n		<li><code>date</code></li>\n		<li><code>datetime</code></li>\n		<li><code>datetime-local</code></li>\n		<li><code>month</code></li>\n		<li><code>number</code></li>\n		<li><code>password</code></li>\n		<li><code>range</code></li>\n		<li><code>search</code></li>\n		<li><code>tel</code></li>\n		<li><code>text</code></li>\n		<li><code>time</code></li>\n		<li><code>url</code></li>\n		<li><code>week</code></li>\n	</ul></li>\n	</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<pre><code>&lt;txp:com_connect_text label=&quot;Your name&quot; /&gt;</code></pre>\n\n<h5>Example 2</h5>\n\n<pre><code>&lt;txp:com_connect_text type=&quot;range&quot; label=&quot;UK shoe size&quot; min=&quot;1&quot; max=&quot;15&quot; /&gt;</code></pre>\n\n<h5>Example 3</h5>\n\n<p>Create a telephone field with a <a href=\"http://html5pattern.com/Phones\" rel=\"nofollow\">validation pattern for UK telephone number</a> format:</p>\n\n<pre><code>&lt;txp:com_connect_text type=&quot;tel&quot; label=&quot;Telephone&quot; pattern=&quot;^\\s*\\(?(020[7,8]{1}\\)?[ ]?[1-9]{1}[0-9{2}[ ]?[0-9]{4})|(0[1-8]{1}[0-9]{3}\\)?[ ]?[1-9]{1}[0-9]{2}[ ]?[0-9]{3})\\s*$&quot; required=&quot;1&quot; /&gt;</code></pre>\n\n<h3>com_connect_email tag</h3>\n\n<pre><code>&lt;txp:com_connect_email /&gt;</code></pre>\n\n<p><code>&lt;input&gt;</code> field for user&#8217;s email address.</p>\n\n<p>The entered email address will automatically be validated to make sure it is of the form “abc@xxx.yyy[.zzz]”. On non-Windows servers, a test will be done to verify that an A or MX record exists for the domain. Neither test prevents spam, but it does help detect accidental typing errors.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>autocomplete=&quot;value&quot;</code>&lt;br /&gt;Indicates whether the value of the control can be automatically completed by the browser. Possible values are: <code>off</code> (the browser does not automatically complete the entry) or <code>on</code> (the browser can automatically complete the value based on values that the user has entered during previous uses). Default is unset.</li>\n	<li><code>break=&quot;tag&quot;</code>&lt;br /&gt;Break tag between the <code>&lt;label&gt;</code> and <code>&lt;input&gt;</code> field. Default is <code>&lt;br /&gt;</code>. Use <code>break=&quot;&quot;</code> to put the label and input field on the same line.</li>\n	<li><code>class=&quot;space-separated values&quot;</code>&lt;br /&gt; Set the <span class=\"caps\">CSS</span> <code>class</code> name of the tag. Default: <code>comEmail</code>. To remove <code>class</code> attribute from the element entirely, use <code>class=&quot;&quot;</code>.</li>\n	<li><code>default=&quot;value&quot;</code>&lt;br /&gt;Default value when no input is provided.</li>\n	<li><code>html_form=&quot;id&quot;</code>&lt;br /&gt;The <span class=\"caps\">HTML</span> <code>id</code> of the <code>&lt;form&gt;</code> tag to which the field is attached. Associated with the contained form by default.</li>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;Text label displayed to the user. Default is <code>Email</code>.</li>\n	<li><code>label_position=&quot;text&quot;</code>&lt;br /&gt;Position of the label in relation to the <code>&lt;input&gt;</code> field. Available values: <code>before</code> or <code>after</code>. Default is <code>before</code>.</li>\n	<li><code>max=&quot;integer&quot;</code>&lt;br /&gt;Maximum input value length in characters, using the HTML5 <code>maxlength</code> attribute. Default is <code>100</code>. To remove <code>maxlength</code> attribute from the element entirely (not recommended), use <code>max=&quot;&quot;</code>.</li>\n	<li><code>min=&quot;integer&quot;</code>&lt;br /&gt;Minimum input value length in characters, using the HTML5 <code>minlength</code> attribute. Default is unset, i.e., no minimum limit.</li>\n	<li><code>name=&quot;value&quot;</code>&lt;br /&gt;Field name, as used in the <span class=\"caps\">HTML</span> <code>&lt;input&gt;</code> tag.</li>\n	<li><code>placeholder=&quot;text&quot;</code>&lt;br /&gt;Text to show as a guide, when the <code>&lt;input&gt;</code> field is empty.</li>\n	<li><code>required=&quot;boolean&quot;</code>&lt;br /&gt;Whether this field must be filled out. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is whatever is set in the <code>&lt;txp:com_connect&gt;</code> tag&#8217;s <code>required</code> attribute &#8211; if neither attribute is set then default is <code>1</code>.</li>\n	<li><code>send_article=&quot;boolean&quot;</code>&lt;br /&gt;Whether this field is used as the recipient email address when using the send_article function. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is <code>0</code>.</li>\n	<li><code>size=&quot;value&quot;</code>&lt;br/&gt;The size, in characters, of the <code>&lt;input&gt;</code> field.</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<pre><code>&lt;txp:com_connect_email label=&quot;Your email address&quot; /&gt;</code></pre>\n\n<h3>com_connect_textarea tag</h3>\n\n<pre><code>&lt;txp:com_connect_textarea /&gt;</code></pre>\n\n<p>Creates a <code>&lt;textarea&gt;</code>.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>autocomplete=&quot;value&quot;</code>&lt;br /&gt;Indicates whether the value of the control can be automatically completed by the browser. Possible values are: <code>off</code> (the browser does not automatically complete the entry) or <code>on</code> (the browser can automatically complete the value based on values that the user has entered during previous uses). Default is unset.</li>\n	<li><code>break=&quot;tag&quot;</code>&lt;br /&gt;Break tag between the <code>&lt;label&gt;</code> and <code>&lt;textarea&gt;</code>. Default is <code>&lt;br /&gt;</code>. Use <code>break=&quot;&quot;</code> to put the <code>&lt;label&gt;</code> and <code>&lt;input&gt;</code> field on the same line.</li>\n	<li><code>class=&quot;space-separated values&quot;</code>&lt;br /&gt; Set the <span class=\"caps\">CSS</span> <code>class</code> name of the  <code>&lt;textarea&gt;</code>. Default: <code>comTextarea</code>. To remove <code>class</code> attribute from the element entirely, use <code>class=&quot;&quot;</code>.</li>\n	<li><code>cols=&quot;integer&quot;</code>&lt;br/&gt;Number of columns, in characters, of the <code>&lt;textarea&gt;</code>. Default is <code>58</code>.</li>\n	<li><code>default=&quot;value&quot;</code>&lt;br /&gt;Default value when no input is provided.</li>\n	<li><code>html_form=&quot;id&quot;</code>&lt;br /&gt;The <span class=\"caps\">HTML</span> <code>id</code> of the <code>&lt;form&gt;</code> tag to which the <code>&lt;textarea&gt;</code> is attached. Associated with the contained form by default.</li>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;Text label displayed to the user. Default is <code>Message</code>.</li>\n	<li><code>label_position=&quot;text&quot;</code>&lt;br /&gt;Position of the label in relation to the <code>&lt;textarea&gt;</code> field. Available values: <code>before</code> or <code>after</code>. Default is <code>before</code>.</li>\n	<li><code>max=&quot;integer&quot;</code>&lt;br /&gt;Maximum input value length in characters, using the HTML5 <code>maxlength</code> attribute. Default is <code>10000</code>. To remove <code>maxlength</code> attribute from the element entirely (not recommended), use <code>max=&quot;&quot;</code>.</li>\n	<li><code>min=&quot;integer&quot;</code>&lt;br /&gt;Minimum input value length in characters, using the HTML5 <code>minlength</code> attribute. Default is unset, i.e., no minimum limit.</li>\n	<li><code>name=&quot;value&quot;</code>&lt;br /&gt;Field name, as used in the <span class=\"caps\">HTML</span> <code>&lt;input&gt;</code> tag.</li>\n	<li><code>placeholder=&quot;text&quot;</code>&lt;br /&gt;Text to show as a guide to users, when the  <code>&lt;textarea&gt;</code> is empty.</li>\n	<li><code>required=&quot;boolean&quot;</code>&lt;br /&gt;Whether this field must be filled out. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is whatever is set in the <code>&lt;txp:com_connect&gt;</code> tag&#8217;s <code>required</code> attribute &#8211; if neither attribute is set then default is <code>1</code>.</li>\n	<li><code>rows=&quot;integer&quot;</code>&lt;br/&gt;Number of rows, in characters, of the <code>&lt;textarea&gt;</code>. Default is <code>8</code>.</li>\n	<li><code>wrap=&quot;value&quot;</code>&lt;br/&gt;Governs word-wrap. Available values: <code>hard</code> or <code>soft</code>. If this attribute is not specified, <code>soft</code> is its default value.</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>Create a text area that is 40 characters wide, 10 lines high, with a customised label:</p>\n\n<pre><code>&lt;txp:com_connect_textarea cols=&quot;40&quot; rows=&quot;10&quot; label=&quot;Your question&quot; /&gt;</code></pre>\n\n<h3>com_connect_submit tag</h3>\n\n<pre><code>&lt;txp:com_connect_submit /&gt;</code></pre>\n\n<p>Creates a submit button. When used as a container tag, a <code>&lt;button&gt;</code> element will be used instead of an <code>&lt;input&gt;</code> element.</p>\n\n<h4>Attributes:</h4>\n\n<ul>\n	<li><code>class=&quot;space-separated values&quot;</code>&lt;br /&gt; Set the <span class=\"caps\">CSS</span> <code>class</code> name of the tag. Default: <code>comSubmit</code>. To remove <code>class</code> attribute from the element entirely, use <code>class=&quot;&quot;</code>.</li>\n	<li><code>html_form=&quot;id&quot;</code>&lt;br /&gt;The <span class=\"caps\">HTML</span> <code>id</code> of the <code>&lt;form&gt;</code> tag to which the button is attached. Associated with the contained form by default.</li>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;Text shown on the submit button. Default is <code>Send</code>.</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>Standard submit button:</p>\n\n<pre><code>&lt;txp:com_connect_submit /&gt;</code></pre>\n\n<h5>Example 2</h5>\n\n<p>Submit button with your own text:</p>\n\n<pre><code>&lt;txp:com_connect_submit label=&quot;To the moooon&quot; /&gt;</code></pre>\n\n<h5>Example 3</h5>\n\n<p>Usage as a container tag, which allows you to use Textpattern tags and <span class=\"caps\">HTML</span> markup in the submit button:</p>\n\n<pre><code>&lt;txp:com_connect_submit&gt;&lt;strong&gt;Send&lt;/strong&gt; question&lt;/txp:com_connect_submit&gt;</code></pre>\n\n<h5>Example 4</h5>\n\n<p>As example 3 above, but using an image as the button:</p>\n\n<pre><code>&lt;txp:com_connect_submit&gt;\n    &lt;img src=&quot;path/to/img.png&quot; alt=&quot;submit&quot;&gt;\n&lt;/txp:com_connect_submit&gt;</code></pre>\n\n<h3>com_connect_select tag</h3>\n\n<pre><code>&lt;txp:com_connect_select /&gt;</code></pre>\n\n<p>Container tag that creates a drop-down selection <code>&lt;select&gt;</code> list, or scrolled <code>&lt;select&gt;</code> list box (by utilising the <code>size</code> attribute).</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>break=&quot;tag&quot;</code>&lt;br /&gt;Break tag between the label and <code>&lt;select&gt;</code> field. Default is <code>&lt;br /&gt;</code>. Use <code>break=&quot;&quot;</code> to put the label and input field on the same line.</li>\n	<li><code>class=&quot;space-separated values&quot;</code>&lt;br /&gt; Set the <span class=\"caps\">CSS</span> <code>class</code> name of the list. Default: <code>comSelect</code>. To remove <code>class</code> attribute from the element entirely, use <code>class=&quot;&quot;</code>.</li>\n	<li><code>delimiter=&quot;character&quot;</code>&lt;br /&gt;Separator character between list items if using the <code>options</code> attribute. Default is <code>,</code> (comma). Ignored if this tag is used as a container.</li>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;Text label displayed to the user. Default is <code>Option</code>.</li>\n	<li><code>label_position=&quot;text&quot;</code>&lt;br /&gt;Position of the label in relation to the <code>&lt;select&gt;</code> field. Available values: <code>before</code> or <code>after</code>. Default is <code>before</code>.</li>\n	<li><code>html_form=&quot;id&quot;</code>&lt;br /&gt;The <span class=\"caps\">HTML</span> <code>id</code> of the <code>&lt;form&gt;</code> tag to which the <code>&lt;select&gt;</code> is attached. Associated with the contained form by default.</li>\n	<li><code>name=&quot;value&quot;</code>&lt;br /&gt;Field name, as used in the <span class=\"caps\">HTML</span> <code>&lt;select&gt;</code> tag.</li>\n	<li><code>options=&quot;comma-separated values&quot;</code>&lt;br /&gt; List of items (previously <code>list</code>) to show in the select box. May also use the <code>&lt;txp:com_connect_option /&gt;</code> tag inside this tag&#8217;s container.</li>\n	<li><code>required=&quot;boolean&quot;</code>&lt;br /&gt;Whether this field must be filled out. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is whatever is set in the <code>&lt;txp:com_connect&gt;</code> tag&#8217;s <code>required</code> attribute &#8211; if neither attribute is set then default is <code>1</code>.</li>\n	<li><code>selected=&quot;value&quot;</code>&lt;br /&gt;List item that is selected by default.</li>\n	<li><code>size=&quot;value&quot;</code>&lt;br/&gt;If the <code>&lt;select&gt;</code> is to be presented as a scrolled list box, this attribute represents the number of rows in the list that should be visible at one time. Default is unset (i.e., a drop-down selection <code>&lt;select&gt;</code> list).</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>Drop-down selection list labeled ‘Department’, containing three options and a blank option (due to the comma before ‘Marketing’) shown by default, forcing the user to make a selection.</p>\n\n<pre><code>&lt;txp:com_connect_select label=&quot;Department&quot; options=&quot;,Marketing,Sales,Support&quot; /&gt;</code></pre>\n\n<h5>Example 2</h5>\n\n<pre><code>&lt;txp:com_connect_select label=&quot;Department&quot; selected=&quot;Sales&quot;&gt;\n    &lt;txp:com_connect_option /&gt;\n    &lt;txp:com_connect_option label=&quot;Marketing&quot; /&gt;\n    &lt;txp:com_connect_option label=&quot;Sales&quot; /&gt;\n    &lt;txp:com_connect_option label=&quot;Support&quot; /&gt;\n&lt;/txp:com_connect_select&gt;</code></pre>\n\n<p>Drop-down selection list containing three options plus a blank option (also see <code>com_connect_option</code> tag below), with ‘Sales’ selected by default.</p>\n\n<h3>com_connect_option tag</h3>\n\n<pre><code>&lt;txp:com_connect_option /&gt;</code></pre>\n\n<p>Creates a drop-down selection option. May be used as a single (self-closing) or container tag. Also see <code>com_connect_select</code> tag above.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>class=&quot;space-separated values&quot;</code>&lt;br /&gt; Set the <span class=\"caps\">CSS</span> <code>class</code> name of the option. Default: <code>comOption</code>. To remove <code>class</code> attribute from the element entirely, use <code>class=&quot;&quot;</code>.</li>\n	<li><code>label=&quot;text&quot;</code> %(warning)required%&lt;br /&gt;Text label of this option displayed to the user.</li>\n	<li><code>selected=&quot;boolean&quot;</code>&lt;br /&gt;Whether this item is selected, May also be specified in the container tag&#8217;s <code>selected</code> attribute. Available values: <code>1</code> (yes) or <code>0</code> (no).</li>\n	<li><code>value=&quot;text&quot;</code>&lt;br /&gt;The value associated with this option when submitted. Default is the label.</li>\n</ul>\n\n<h5>Example 1</h5>\n\n<pre><code>&lt;txp:com_connect_select label=&quot;Department&quot;&gt;\n    &lt;txp:com_connect_option label=&quot;Marketing&quot; /&gt;\n    &lt;txp:com_connect_option label=&quot;Sales&quot; /&gt;\n    &lt;txp:com_connect_option label=&quot;Support&quot; /&gt;\n&lt;/txp:com_connect_select&gt;</code></pre>\n\n<p>Drop-down selection list containing three options as single tags.</p>\n\n<h5>Example 2</h5>\n\n<pre><code>&lt;txp:com_connect_select label=&quot;Department&quot;&gt;\n    &lt;txp:com_connect_option value=&quot;contact-marketing&quot;&gt;Marketing&lt;/txp:com_connect_option&gt;\n    &lt;txp:com_connect_option value=&quot;contact-sales&quot; selected=&quot;1&quot;&gt;Sales&lt;/txp:com_connect_option&gt;\n    &lt;txp:com_connect_option value=&quot;contact-support&quot;&gt;Support&lt;/txp:com_connect_option&gt;\n&lt;/txp:com_connect_select&gt;</code></pre>\n\n<p>Drop-down selection list containing three options as container tags with ‘Sales’ selected by default.</p>\n\n<h3>com_connect_checkbox tag</h3>\n\n<pre><code>&lt;txp:com_connect_checkbox /&gt;</code></pre>\n\n<p>Creates a checkbox.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>break=&quot;tag&quot;</code>&lt;br /&gt;Break tag between the checkbox button and label. Default is one space. Use <code>break=&quot;br&quot;</code> to put the checkbox and label on separate lines.</li>\n	<li><code>checked=&quot;boolean&quot;</code>&lt;br /&gt;Whether this box is checked when first displayed. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is <code>0</code>.</li>\n	<li><code>class=&quot;space-separated values&quot;</code>&lt;br /&gt; Set the <span class=\"caps\">CSS</span> <code>class</code> name of the option. Default: <code>comCheckbox</code>. To remove <code>class</code> attribute from the element entirely, use <code>class=&quot;&quot;</code>.</li>\n	<li><code>html_form=&quot;id&quot;</code>&lt;br /&gt;The <span class=\"caps\">HTML</span> <code>id</code> of the <code>&lt;form&gt;</code> tag to which the checkbox is attached. Associated with the contained form by default.</li>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;Text label displayed to the user. Default is <code>Checkbox</code>.</li>\n	<li><code>label_position=&quot;text&quot;</code>&lt;br /&gt;Position of the label in relation to the <code>&lt;input&gt;</code> field. Available values: <code>before</code> or <code>after</code>. Default is <code>after</code>.</li>\n	<li><code>name=&quot;value&quot;</code>&lt;br /&gt;Field name, as used in the <span class=\"caps\">HTML</span> <code>&lt;input&gt;</code> tag.</li>\n	<li><code>required=&quot;boolean&quot;</code>&lt;br /&gt;Whether this checkbox must be filled out. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is whatever is set in the <code>&lt;txp:com_connect&gt;</code> tag&#8217;s <code>required</code> attribute &#8211; if neither attribute is set then default is <code>1</code>.</li>\n	<li><code>value=&quot;text&quot;</code>&lt;br /&gt;Value to send in the email if the option is checked. Uses yes/no if not set.</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>Shrink-wrap agreement which must be checked by the user before the email will be sent.</p>\n\n<pre><code>&lt;txp:com_connect_checkbox label=&quot;I accept the terms and conditions&quot; /&gt;</code></pre>\n\n<h5>Example 2</h5>\n\n<p>Optional checkboxes:</p>\n\n<pre><code>With which operating systems are you familiar?&lt;br /&gt;\n&lt;txp:com_connect_checkbox label=&quot;Windows&quot; required=&quot;0&quot; /&gt;&lt;br /&gt;\n&lt;txp:com_connect_checkbox label=&quot;Unix/Linux/BSD&quot; required=&quot;0&quot; /&gt;&lt;br /&gt;\n&lt;txp:com_connect_checkbox label=&quot;MacOS&quot; required=&quot;0&quot; /&gt;</code></pre>\n\n<h3>com_connect_radio tag</h3>\n\n<pre><code>&lt;txp:com_connect_radio /&gt;</code></pre>\n\n<p>Creates a radio button.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>break=&quot;tag&quot;</code>&lt;br /&gt;Break tag between the radio button and label. Default is one space. Use <code>break=&quot;br&quot;</code> to put the radio button and label on separate lines.</li>\n	<li><code>checked=&quot;boolean&quot;</code>&lt;br /&gt;Whether this radio option is checked when the form is first displayed. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is <code>0</code>.</li>\n	<li><code>class=&quot;space-separated values&quot;</code>&lt;br /&gt; Set the <span class=\"caps\">CSS</span> <code>class</code> name of the radio button. Default: <code>comRadio</code>. To remove <code>class</code> attribute from the element entirely, use <code>class=&quot;&quot;</code>.</li>\n	<li><code>group=&quot;text&quot;</code> %(warning)required%&lt;br /&gt;Text used in the email to describe this group of radio buttons. This attribute value is remembered for subsequent radio buttons, so you only have to set it on the first radio button of a group. Default is <code>Radio</code>.</li>\n	<li><code>html_form=&quot;id&quot;</code>&lt;br /&gt;The <span class=\"caps\">HTML</span> <code>id</code> of the <code>&lt;form&gt;</code> tag to which the radio button is attached. Associated with the contained form by default.</li>\n	<li><code>label=&quot;text&quot;</code> %(warning)required%&lt;br /&gt;Text label displayed to the user as radio button option.</li>\n	<li><code>name=&quot;value&quot;</code> %(warning)recommended%&lt;br /&gt;Field name, as used in the <span class=\"caps\">HTML</span> <code>&lt;input&gt;</code> tag. This attribute value is remembered for subsequent radio buttons, so you only have to set it on the first radio button of a group. If it hasn&#8217;t been set at all, it will be derived from the <code>group</code> attribute.</li>\n	<li><code>required=&quot;boolean&quot;</code>&lt;br /&gt;Whether this radio set must be filled out. Available values: <code>1</code> (yes) or <code>0</code> (no). Default is whatever is set in the <code>&lt;txp:com_connect&gt;</code> tag&#8217;s <code>required</code> attribute &#8211; if neither attribute is set then default is <code>1</code>. <strong>You should set the <code>required</code> attribute on only the first radio button of the group, or set the same identical attribute value on all radio buttons in the group.</strong></li>\n	<li><code>value=&quot;text&quot;</code>&lt;br /&gt;Value to send in the email if the option is checked. Uses label if not set.</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>Group mutually exclusive radio buttons by setting the <code>group</code> attribute on the first radio button in a group. Only the chosen radio button from each group will be used in the email message. The message will be output in the form <code>group: label</code> for each of the chosen radio buttons.</p>\n\n<pre><code>&lt;txp:com_connect_radio label=&quot;Medium&quot; group=&quot;I like my steak&quot; /&gt;\n&lt;txp:com_connect_radio label=&quot;Rare&quot; /&gt;\n&lt;txp:com_connect_radio label=&quot;Well done&quot; /&gt;\n&lt;txp:com_connect_radio label=&quot;Wine&quot; group=&quot;With a glass of&quot; /&gt;\n&lt;txp:com_connect_radio label=&quot;Beer&quot; /&gt;\n&lt;txp:com_connect_radio label=&quot;Water&quot; /&gt;</code></pre>\n\n<h3>com_connect_secret tag</h3>\n\n<pre><code>&lt;txp:com_connect_secret /&gt;</code></pre>\n\n<p>This tag has no effect on the form or <span class=\"caps\">HTML</span> output, but will include additional information in the email. It can be used as a single (self-closing) tag or as a container tag.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;Used to identify the field in the email. Default is <code>Secret</code>.</li>\n	<li><code>name=&quot;text&quot;</code>&lt;br /&gt;Used internally. Set this only if you have multiple ‘secret’ form elements with identical labels.</li>\n	<li><code>value=&quot;value&quot;</code>&lt;br /&gt;Some text you want to add to the email.</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>Usage as a single (self-closing) tag:</p>\n\n<pre><code>&lt;txp:com_connect_secret value=&quot;The answer is 42&quot; /&gt;</code></pre>\n\n<h5>Example 2</h5>\n\n<p>Usage as a container tag:</p>\n\n<pre><code>&lt;txp:com_connect_secret label=&quot;Dear user&quot;&gt;\n    Please provide a useful example for this tag!\n&lt;/txp:com_connect_secret&gt;</code></pre>\n\n<h3>com_connect_serverinfo tag</h3>\n\n<pre><code>&lt;txp:com_connect_serverinfo /&gt;</code></pre>\n\n<p>This tag has no effect on the form or <span class=\"caps\">HTML</span> output, but will include additional information in the email based on the <span class=\"caps\">PHP</span> <code>$_SERVER</code> variable.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;Used to identify the field in the email. Defaults to the value of the <code>name</code> attribute.</li>\n	<li><code>name=&quot;value&quot;</code> %(warning)required%&lt;br /&gt;Name of the server variable. See the <a href=\"https://php.net/manual/reserved.variables.php#reserved.variables.server\" rel=\"nofollow\"><span class=\"caps\">PHP</span> manual</a> for a full list.</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>Add the IP address of the visitor to the email:</p>\n\n<pre><code>&lt;txp:com_connect_serverinfo name=&quot;REMOTE_ADDR&quot; label=&quot;IP number&quot; /&gt;</code></pre>\n\n<h5>Example 2</h5>\n\n<p>Add the name of the visitor&#8217;s browser to the email:</p>\n\n<pre><code>&lt;txp:com_connect_serverinfo name=&quot;HTTP_USER_AGENT&quot; label=&quot;Browser&quot; /&gt;</code></pre>\n\n<h3>com_connect_send_article tag</h3>\n\n<pre><code>&lt;txp:com_connect_send_article /&gt;</code></pre>\n\n<p>Use this tag in your individual article form, where you want the “send article” link to be displayed.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>linktext=&quot;text&quot;</code>&lt;br /&gt;Text displayed for the link. Default is <code>send article</code>.</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>On an article form:</p>\n\n<pre><code>&lt;txp:com_connect_send_article linktext=&quot;Send this article&quot; /&gt;</code></pre>\n\n<h3>com_connect_label tag</h3>\n\n<pre><code>&lt;txp:com_connect_label /&gt;</code></pre>\n\n<p>Return the label for the given attribute name.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>name=&quot;text&quot;</code>&lt;br /&gt;The name of the field for which you wish to retrieve the label.</li>\n</ul>\n\n<h3>com_connect_value tag</h3>\n\n<pre><code>&lt;txp:com_connect_value /&gt;</code></pre>\n\n<p>Return the value of the given attribute, by name or its label.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;The label of the field for which you wish to retrieve the value.</li>\n	<li><code>name=&quot;text&quot;</code>&lt;br /&gt;The name of the field for which you wish to retrieve the value.</li>\n</ul>\n\n<h3>com_connect_if tag</h3>\n\n<pre><code>&lt;txp:com_connect_if /&gt;</code></pre>\n\n<p>Conditional tag for checking variable conditions, either by name or label.</p>\n\n<h4>Attributes</h4>\n\n<ul>\n	<li><code>label=&quot;text&quot;</code>&lt;br /&gt;The label of the field you wish to check.</li>\n	<li><code>name=&quot;text&quot;</code>&lt;br /&gt;The name of the field you wish to check.</li>\n	<li><code>value=&quot;text&quot;</code>&lt;br /&gt;The value against which to test the given field. Leave blank to just test if there is any value assigned to the field.</li>\n</ul>\n\n<h4>Examples</h4>\n\n<h5>Example 1</h5>\n\n<p>Take action if the visitor has entered a particular value.</p>\n\n<pre><code>&lt;txp:com_connect_if name=&quot;delivery&quot; value=&quot;courier&quot;&gt;</code></pre>\n\n<p>Please note, this option incurs an additional charge, <code>&lt;/txp:com_connect_if&gt;</code>.</p>\n\n<h2>Advanced examples</h2>\n\n<h3>Separate input and error forms</h3>\n\n<p>Using <code>show_input</code> and <code>show_error</code> to display the form and error messages on different parts of a page. A form is used to make sure the contents of both forms are identical, otherwise they would be seen as two independent forms. The first form only shows errors (no input), the second form only shows the input fields (no errors).</p>\n\n<pre><code>&lt;div id=&quot;error&quot;&gt;\n    &lt;txp:com_connect form=&quot;contact_form&quot; show_input=&quot;0&quot; /&gt;\n&lt;/div&gt;\n&lt;div id=&quot;inputform&quot;&gt;\n    &lt;txp:com_connect form=&quot;contact_form&quot; show_error=&quot;0&quot; /&gt;\n&lt;/div&gt;</code></pre>\n\n<p>Apart from the <code>show_error</code> and <code>show_input</code> attributes, all other attributes must be 100% identical in both forms, otherwise they would be seen as two unrelated forms.</p>\n\n<h3>User selectable subject field</h3>\n\n<p>Specify the <code>subject_form</code> attribute and create a form which includes a <code>com_connect_select</code> tag:</p>\n\n<pre><code>&lt;txp:com_connect to=&quot;you@example.com&quot; subject_form=&quot;my_subject_form&quot; /&gt;\n    &lt;txp:com_connect_text label=&quot;Name&quot; /&gt;&lt;br /&gt;\n    &lt;txp:com_connect_email /&gt;&lt;br /&gt;\n    &lt;txp:com_connect_select label=&quot;Choose Subject&quot; options=&quot;,Question,Feedback&quot; /&gt;&lt;br /&gt;\n    &lt;txp:com_connect_textarea label=&quot;Message&quot; /&gt;&lt;br /&gt;\n&lt;/txp:com_connect&gt;</code></pre>\n\n<p>Create a Textpattern form called “my_subject_form”, containing:</p>\n\n<pre><code>&lt;txp:php&gt;\n    global $com_connect_form;\n    echo $com_connect_form[\'Choose Subject\'];\n&lt;/txp:php&gt;</code></pre>\n\n<p>The <code>label</code> used in the <code>com_connect_select</code> tag must be identical to the corresponding variable in the <code>subject_form</code>. Here we used <code>Choose subject</code>.</p>\n\n<p>If you&#8217;d prefer to add a common prefix for all subjects, use a <code>subject_form</code> containing:</p>\n\n<pre><code>&lt;txp:php&gt;\n    global $com_connect_form;\n    echo \'My common prefix - \' . $com_connect_form[\'Choose Subject\'];\n&lt;/txp:php&gt;</code></pre>\n\n<h3>User selectable recipient, without showing email address</h3>\n\n<p>Specify the <code>to_form</code> attribute and create a form which includes a <code>com_connect_select</code> tag:</p>\n\n<pre><code>&lt;txp:com_connect to_form=&quot;my_com_connect_to_form&quot;&gt;\n    &lt;txp:com_connect_text label=&quot;Name&quot; /&gt;&lt;br /&gt;\n    &lt;txp:com_connect_email /&gt;&lt;br /&gt;\n    &lt;txp:com_connect_select label=&quot;Department&quot; options=&quot;,Support,Sales&quot; /&gt;&lt;br /&gt;\n    &lt;txp:com_connect_textarea label=&quot;Message&quot; /&gt;&lt;br /&gt;\n&lt;/txp:com_connect&gt;</code></pre>\n\n<p>Create a Textpattern form called “my_com_connect_to_form”, containing:</p>\n\n<pre><code>&lt;txp:php&gt;\n    global $com_connect_form;\n    switch($com_connect_form[\'Department\'])\n    {\n        case \'Support\':\n            echo \'crew@example.com\';\n            break;\n        case \'Sales\':\n            echo \'showmethemoney@example.com\';\n            break;\n        default:\n            echo \'someone@example.com\';\n    }\n&lt;/txp:php&gt;</code></pre>\n\n<p>The <code>label</code> used in the <code>com_connect_select</code> tag must be identical to the corresponsing variable in the <code>to_form</code>. Here we used <code>Department</code>.</p>\n\n<p>A ‘default’ email address in the <code>to_form</code> is specified to ensure that a valid email address is used in cases where you add or change a select/radio option and forget to update the <code>to_form</code>.</p>\n\n<p><strong>Warning:</strong> Never use tags like <code>com_connect_text</code>, <code>com_connect_email</code> or <code>com_connect_textarea</code> for setting the recipient address, otherwise your form can be abused to send spam to any email address!</p>\n\n<h2>Styling</h2>\n\n<p>The form itself has a default <code>class</code> of <code>comConnectForm</code> set on the <code>&lt;form&gt;</code> <span class=\"caps\">HTML</span> tag.</p>\n\n<p>If a <code>thanks_form</code> template is used, the wrapper around that template has a default <code>class</code> of <code>comThanks</code>.</p>\n\n<p>The list of error messages (if any) has a default <code>class</code> of <code>comError</code> set on the <code>ul</code> <span class=\"caps\">HTML</span> tag that encloses the list of errors. This class name may be overridden using the <code>classes</code> attribute of the <code>com_connect</code> tag.</p>\n\n<p>All form elements and corresponding labels have the following classes (or ids) set by default:</p>\n\n<ol>\n	<li>One of <code>comText</code>, <code>comEmail</code>, <code>comTextarea</code>, <code>comSelect</code>, <code>comOption</code>, <code>comRadio</code>, <code>comCheckbox</code> or <code>comSubmit</code>. By default, it should be obvious which <code>class</code> is used for which form element (and corresponding label). You can override these names by using your own <code>class</code> attribute.</li>\n	<li><code>comRequired</code> and/or <code>errorElement</code>, depending on whether the form element is required, an error was found in whatever the visitor entered, or both. Override these using the <code>classes</code> attribute in the <code>com_connect</code> tag.</li>\n	<li>An individual <code>id</code> or <code>class</code> set to the value of the <code>name</code> attribute of the corresponding tag. When styling forms based on this <code>class</code>, you should explicitly set the <code>name</code> attribute because automatically generated names may change in newer com_connect versions.</li>\n</ol>\n\n<h2>Com Connect&#8217;s <span class=\"caps\">API</span></h2>\n\n<p>The plugin <span class=\"caps\">API</span> of com_connect, originally developed by Tranquillo, allows other plugins to interact with contact forms. This permits extra functionality such as combatting comment cpam, <span class=\"caps\">HTML</span> email, newsletter delivery and so forth to be bolted onto the base plugin.</p>\n\n<p>Four callback events exist in com_connect:</p>\n\n<ul>\n	<li><code>comconnect.submit</code> is called after the form is submitted and the values are checked if empty or valid email addresses, but before the mail is sent.</li>\n	<li><code>comconnect.form</code> lets you inject content (fields) in the contact form as displayed to the visitor.</li>\n	<li><code>comconnect.render</code> lets you inject or alter markup of the entire <code>&lt;form&gt;</code>. Useful for editing things like <code>enctype</code> (e.g. for file attachment modules that link into this plugin).</li>\n	<li><code>comconnect.deliver</code> is called immediately prior to delivery and advertises the intended payload so you may manipulate it. For example, you could do something as simple as adding CC: or <span class=\"caps\">BCC</span>: fields. Or change the <span class=\"caps\">MIME</span> type header to <code>text/html</code> and add some <span class=\"caps\">HTML</span> content based on the given body data, then let com_connect handle the mailing. Or you could intercept the entire mail process, handle mailing yourself with a third party system, and tell com_connect to skip its internal mailing process.</li>\n</ul>\n\n<p>For reference here are the commands that will be interesting to plugin developers:</p>\n\n<pre><code>// This will call your function before the form is submitted so you can analyse the submitted data\nregister_callback(\'abc_myfunction\', \'comconnect.submit\');\n\n// This will call your function and add the output (use @return $mystuff;@) to the contact-form.\nregister_callback(\'abc_myotherfunction2\', \'comconnect.form\');\n\n// To get hold of the form-variables you can use global com_connect_form;\n// With the following two lines you can tell com_connect if your plugin found spam.\n$evaluator =&amp; get_comconnect_evaluator();\n\n// The passed value must be non-zero to mark the content as spam.\n// Value must be a number between 0 and 1.\n$evaluator -&gt; add_comconnect_status(1);</code></pre>\n\n<p>Multiple plugins can be active at the same time and each of them can mark the submitted content as spam and prevent the form from being submitted.</p>\n\n<h4>Examples</h4>\n\n<pre><code>register_callback(\'pap_comconnect_form\',\'comconnect.form\');\nregister_callback(\'pap_comconnect_submit\',\'comconnect.submit\');\n\nfunction pap_comconnect_form()\n{\n    $field = \'&lt;div style=&quot;display:none&quot;&gt;\'.\n        finput(\'text\',\'phone\',ps(\'phone\'),\'\',\'\',\'\',\'\',\'\',\'phone\').\'&lt;br /&gt;\'.\n        finput(\'text\',\'mail\',ps(\'mail\'),\'\',\'\',\'\',\'\',\'\',\'mail\').\'&lt;/div&gt;\';\n    return $field;&lt;/code&gt;\n}\n\nfunction pap_comconnect_submit()\n{\n    $checking_mail_field = trim(ps(\'mail\'));\n    $checking_phone_field = trim(ps(\'phone\'));\n    $evaluation =&amp; get_comconnect_evaluator();\n\n    // If the hidden fields are filled out, the contact form won\'t be submitted!\n    if ($checking_mail_field != \'\' or $checking_phone_field != \'\') {\n        $evaluation -&gt; add_comconnect_status(1);\n    }\n\n    return;\n}</code></pre>\n\n<p>For the delivery callback, you signal back to the plugin your intentions so that Com Connect knows what to do after your delivery plugin has executed. Return the following strings:</p>\n\n<ul>\n	<li><code>comconnect.send</code> (or no return value) to allow Com Connect to continue mailing the content.</li>\n	<li><code>comconnect.skip</code> to skip com_connect&#8217;s mailing (i.e., the third party handles the mail process) and return ‘success’ to the visitor.</li>\n	<li><code>comconnect.fail</code> to skip com_connect&#8217;s mailing and return ‘fail’ to the visitor.</li>\n</ul>\n\n<p>Or simply <code>exit</code> your plugin to halt the entire operation so no Com Connect feedback is given.</p>\n\n<h2>Frequently asked questions</h2>\n\n<h5>How do I remove the legend and fieldset surrounding the contact form?</h5>\n\n<p>Set the <code>label</code> attribute to an empty value (<code>label=&quot;&quot;</code>) in the <code>com_connect</code> tag.</p>\n\n<h5>No email is sent. How do I diagnose and fix the problem?</h5>\n\n<p>First try a simple contact form, using only the <code>com_connect</code> tag with the <code>to</code> attribute set to a valid email address. If that doesn&#8217;t send email, fill out the ‘SMTP envelope sender address’ field in Textpattern&#8217;s Preferences administration panel on the admin tab. If that doesn&#8217;t help either, take a look at your mail server log files to see what the problem is.</p>\n\n<h5>Which tag do I use to create the submit button?</h5>\n\n<p>Just use normal <span class=\"caps\">HTML</span> code to create a submit button. For historical reasons this plugin still provides the <code>com_connect_submit</code> tag, but it provides little extra functionality.</p>\n\n<h5>How can I get a unique (order) number in the subject of each email?</h5>\n\n<p>Try using the <a href=\"http://vanmelick.com/txp\" rel=\"nofollow\">rvm_counter</a> tag in the <code>subject</code> attribute of the <code>com_connect</code> tag.</p>\n\n<h5>I want to use the contact form in an article list (one form for each article), but how do I make each form unique?</h5>\n\n<p>You can make each form unique by making one of its attribute values unique. See previous question for an example of how to do this with the <code>subject</code> attribute.</p>\n\n<h5>Send article: can I let people specify multiple recipients?</h5>\n\n<p>No. The ‘send article’ functionality is spammy enough as it is right now.</p>\n\n<h5>Send article: can I show the contact form without having to click a link first?</h5>\n\n<p>Sure, put this just above the <code>com_connect</code> tag:</p>\n\n<pre><code>&lt;txp:php&gt;$_GET[\'com_connect_send_article\']=\'yes\';&lt;/txp:php&gt;</code></pre>\n\n<h5>How can I use this form to upload files?</h5>\n\n<p>You can&#8217;t, but have a look at the <a href=\"https://github.com/Bloke/ext_file_attach/releases/\" rel=\"nofollow\">ext_file_attach</a> module, or <a href=\"https://bitbucket.org/Manfre/txp-plugins/downloads/\" rel=\"nofollow\">mem_form</a> plugins.</p>\n\n<h5>Can I use this plugin to send <span class=\"caps\">HTML</span> email?</h5>\n\n<p>Not without a plugin like <a href=\"https://bitbucket.org/Manfre/txp-plugins/downloads\" rel=\"nofollow\">mem_form</a> or using the delivery callback.</p>\n\n<h5>Can I use this plugin to send newsletters?</h5>\n\n<p>Not without a plugin, such as <a href=\"https://bitbucket.org/Manfre/txp-plugins/downloads/\" rel=\"nofollow\">mem_postmaster</a>.</p>\n\n<h5>I have a question that&#8217;s not listed here</h5>\n\n<p>First read the plugin documentation (the page you&#8217;re on right now) once more. If that doesn&#8217;t answer your question, visit the <a href=\"https://forum.textpattern.io\" rel=\"nofollow\">Textpattern forum</a>.</p>\n\n<h2>Authors/credits</h2>\n\n<ul>\n	<li><strong>zem</strong> wrote the zem_contact 0.6 plugin on which this plugin was initially based.</li>\n	<li><strong>Mary</strong> completely revised the plugin code.</li>\n	<li><strong>Stuart</strong> turned it into a plugin, added a revised help text and additional code. Maintained all plugin versions until 4.0.3.17.</li>\n	<li><strong>wet</strong> added the <code>com_connect_radio</code> tag.</li>\n	<li><strong>Tranquillo</strong> added the anti-spam <span class=\"caps\">API</span> and <code>zem_contact_send_article</code> functionality.</li>\n	<li><strong>aslsw66</strong>, <strong>jdykast</strong> and others provided additional code.</li>\n	<li><strong>Ruud</strong> cleaned up and audited the code to weed out bugs and completely revised the help text. Maintained all versions until 4.0.3.20.</li>\n	<li><strong>Bloke</strong> was the maintainer of v4.5.0.0.</li>\n	<li>Adopted as a community plugin and now maintained by the core development team.</li>\n	<li>Supported and tested to destruction by the Textpattern community. Many thanks to <a href=\"https://github.com/textpattern/com_connect/graphs/contributors\" rel=\"nofollow\">all additional contributors</a>.</li>\n</ul>', '//<?php\n/**\n * com_connect: A Textpattern CMS plugin for mail delivery of contact forms.\n */\n\n// Register tags if necessary.\nif (class_exists(\'\\Textpattern\\Tag\\Registry\')) {\n    Txp::get(\'\\Textpattern\\Tag\\Registry\')\n        ->register(\'com_connect\')\n        ->register(\'com_connect_text\')\n        ->register(\'com_connect_email\')\n        ->register(\'com_connect_textarea\')\n        ->register(\'com_connect_select\')\n        ->register(\'com_connect_option\')\n        ->register(\'com_connect_checkbox\')\n        ->register(\'com_connect_radio\')\n        ->register(\'com_connect_serverinfo\')\n        ->register(\'com_connect_secret\')\n        ->register(\'com_connect_submit\')\n        ->register(\'com_connect_send_article\')\n        ->register(\'com_connect_value\')\n        ->register(\'com_connect_label\')\n        ->register(\'com_connect_if\');\n}\n\n/**\n * Tag: encapsulate a contact form.\n *\n * Triggers the following callbacks:\n *  -> \'comconnect.form\' during form rendering so additional fields (e.g. spam honeypots) can be injected.\n *  -> \'comconnect.render\' immediately prior to form rendering so other parts of the form content may be altered.\n *  -> \'comconnect.submit\' on successful posting of form data. Primarily of use for spam\n *     plugins: they can return a non-zero value to signal that the form should NOT be sent.\n *\n * @param array  $atts  Tag attributes\n * @param string $thing Tag\'s container content\n */\nfunction com_connect($atts, $thing = null)\n{\n    global $sitename, $textarray, $com_connect_flags, $com_connect_from,\n        $com_connect_recipient, $com_connect_error, $com_connect_submit,\n        $com_connect_form, $com_connect_labels, $com_connect_values;\n\n    extract(com_connect_lAtts(array(\n        \'body_form\'        => \'\',\n        \'class\'            => \'comConnectForm\',\n        \'classes\'          => \'\',\n        \'copysender\'       => 0,\n        \'expire\'           => 600,\n        \'form\'             => \'\',\n        \'from\'             => \'\',\n        \'from_form\'        => \'\',\n        \'label\'            => null,\n        \'browser_validate\' => 1,\n        \'redirect\'         => \'\',\n        \'required\'         => \'1\',\n        \'show_error\'       => 1,\n        \'show_input\'       => 1,\n        \'send_article\'     => 0,\n        \'subject\'          => null,\n        \'subject_form\'     => \'\',\n        \'to\'               => \'\',\n        \'to_form\'          => \'\',\n        \'thanks\'           => null,\n        \'thanks_form\'      => \'\'\n    ), $atts));\n\n    if (!empty($lang)) {\n        $strings = com_connect_load_lang($lang);\n        $textarray = array_merge($textarray, $strings);\n    }\n\n    // Set defaults, in the local language if necessary.\n    if ($label === null) {\n        $label = gTxt(\'com_connect_contact\');\n    }\n\n    if ($subject === null) {\n        $subject = gTxt(\'com_connect_email_subject\', array(\'{site}\' => html_entity_decode($sitename,ENT_QUOTES)));\n    }\n\n    if ($thanks === null) {\n        $thanks = graf(gTxt(\'com_connect_email_thanks\'));\n    }\n\n    unset($atts[\'show_error\'], $atts[\'show_input\']);\n\n    $defaultClassNames = array(\n        \'element\'  => \'errorElement\',\n        \'wrapper\'  => \'comError\',\n        \'required\' => \'comRequired\',\n        \'thanks\'   => \'comThanks\',\n        );\n\n    $com_connect_form_id = md5(serialize($atts) . preg_replace(\'/[\\t\\s\\r\\n]/\', \'\', $thing));\n    $com_connect_submit = (ps(\'com_connect_form_id\') == $com_connect_form_id);\n    $override_email_charset = (get_pref(\'override_emailcharset\') && is_callable(\'utf8_decode\'));\n    $userClassNames = do_list($classes);\n\n    foreach (array_merge($defaultClassNames, $userClassNames) as $classKey => $classValue) {\n        if (strpos($classValue, \':\') !== false) {\n            $classParts = do_list($classValue, \':\');\n\n            if (count($classParts) === 2) {\n                $com_connect_flags[\'cls_\' . $classParts[0]] = $classParts[1];\n            }\n        } elseif ($classKey && $classValue) {\n            $com_connect_flags[\'cls_\' . $classKey] = $classValue;\n        }\n    }\n\n    // The $com_connect_flags[\'this_form\'] global is set if an id is supplied for the <form>.\n    // This value then becomes the default value for all html_form (a.k.a. form=)\n    // attributes for any input tags in this tag\'s container, providing HTML5 is in use.\n    $com_connect_flags[\'this_form\'] = \'com\' . $com_connect_form_id;\n\n    // Global toggle for required attribute.\n    $com_connect_flags[\'required\'] = $required;\n\n    $now = time();\n    $now_date = date(\'Y-m-d H:i:s\', $now);\n\n    $expire = abs(assert_int($expire));\n\n    static $headers_sent = false;\n    if (!$headers_sent) {\n        header(\'Last-Modified: \' . gmdate(\'D, d M Y H:i:s\', $now - (3600 * 24 * 7)) . \' GMT\');\n        header(\'Expires: \' . gmdate(\'D, d M Y H:i:s\', $now + $expire) . \' GMT\');\n        header(\'Cache-Control: no-cache, must-revalidate\');\n        $headers_sent = true;\n    }\n\n    $nonce = doSlash(ps(\'com_connect_nonce\'));\n    $renonce = false;\n\n    if ($com_connect_submit) {\n        // Could use \"interval $expire second\" but multiple com_connect forms could delete data\n        // that might be in use by other forms.\n        // TODO: use max(600, $expire)? Not perfect, but ensures a safe minimum deletion rate.\n        safe_delete(\'txp_discuss_nonce\', \"issue_time < date_sub(\'$now_date\', interval 10 minute)\");\n        if ($rs = safe_row(\'used\', \'txp_discuss_nonce\', \"nonce = \'$nonce\'\")) {\n            if ($rs[\'used\']) {\n                unset($com_connect_error);\n                $com_connect_error[] = gTxt(\'com_connect_form_used\');\n                $renonce = true;\n                $_POST = array();\n                $_POST[\'com_connect_submit\'] = true;\n                $_POST[\'com_connect_form_id\'] = $com_connect_form_id;\n                $_POST[\'com_connect_nonce\'] = $nonce;\n            }\n        } else {\n            $com_connect_error[] = gTxt(\'com_connect_form_expired\');\n            $renonce = true;\n        }\n    }\n\n    if ($com_connect_submit && $nonce && !$renonce) {\n        $com_connect_nonce = $nonce;\n    } elseif (!$show_error || $show_input) {\n        $com_connect_nonce = md5(uniqid(rand(), true));\n        safe_insert(\'txp_discuss_nonce\', \"issue_time = \'\" . $now_date . \"\', nonce = \'$com_connect_nonce\'\");\n    }\n\n    $form = ($form) ? fetch_form($form) : $thing;\n\n    if (empty($form)) {\n        $form = \'\n<txp:com_connect_text label=\"\'.gTxt(\'com_connect_name\').\'\" /><br />\n<txp:com_connect_email /><br />\'.\n($send_article ? \'<txp:com_connect_email send_article=\"1\" label=\"\'.gTxt(\'com_connect_recipient\').\'\" /><br />\' : \'\').\n\'<txp:com_connect_textarea /><br />\n<txp:com_connect_submit />\n\';\n    }\n\n    $form = parse($form);\n\n    // Perform aggregate functions for checking radio sets.\n    if ($com_connect_submit) {\n        com_connect_group_validate();\n    }\n\n    if ($to_form) {\n        $to = parse_form($to_form);\n    }\n\n    if (!$to && !$send_article) {\n        return gTxt(\'com_connect_to_missing\');\n    }\n\n    $out = \'\';\n\n    if (!$com_connect_submit) {\n        // Don\'t show errors or send mail.\n    } elseif (!empty($com_connect_error)) {\n        if ($show_error || !$show_input) {\n            $out .= n.doWrap(array_unique($com_connect_error), \'ul\', \'li\', $com_connect_flags[\'cls_wrapper\']).n;\n\n            if (!$show_input) {\n                return $out;\n            }\n        }\n    } elseif ($show_input && is_array($com_connect_form)) {\n        // Load and check spam plugins.\n        callback_event(\'comconnect.submit\');\n        $evaluation =& get_comconnect_evaluator();\n        $clean = $evaluation->get_comconnect_status();\n\n        if ($clean != 0) {\n            return gTxt(\'com_connect_spam\');\n        }\n\n        if ($from_form) {\n            $from = parse_form($from_form);\n        }\n\n        if ($subject_form) {\n            $subject = parse_form($subject_form);\n        }\n\n        $sep = IS_WIN ? \"\\r\\n\" : \"\\n\";\n        $msg = array();\n        $fields = array();\n\n        foreach ($com_connect_labels as $name => $lbl) {\n            if (!is_array($com_connect_values[$name])) {\n                if (trim($com_connect_values[$name]) === false) {\n                    continue;\n                }\n\n                $msg[] = $lbl . \': \' . $com_connect_values[$name];\n            }\n\n            $fields[$name] = $com_connect_values[$name];\n        }\n\n        if ($send_article) {\n            global $thisarticle;\n\n            $subject = str_replace(\'&#38;\', \'&\', $thisarticle[\'title\']);\n            $msg[] = permlinkurl($thisarticle);\n            $msg[] = $subject;\n            $s_ar = array(\'&#8216;\', \'&#8217;\', \'&#8220;\', \'&#8221;\', \'&#8217;\', \'&#8242;\', \'&#8243;\', \'&#8230;\', \'&#8211;\', \'&#8212;\', \'&#215;\', \'&#8482;\', \'&#174;\', \'&#169;\', \'&lt;\', \'&gt;\', \'&quot;\', \'&amp;\', \'&#38;\', \"\\t\", \'<p\');\n\n            if ($override_email_charset) {\n                $r_ar = array(\"\'\", \"\'\", \'\"\', \'\"\', \"\'\", \"\'\", \'\"\', \'...\', \'-\', \'--\', \'x\', \'[tm]\', \'(r)\', \'(c)\', \'<\', \'>\', \'\"\', \'&\', \'&\', \' \', \"\\n<p\");\n            } else {\n                $r_ar = array(\'‘\', \'’\', \'“\', \'”\', \'’\', \'?\', \'?\', \'…\', \'–\', \'—\', \'×\', \'™\', \'®\', \'©\', \'<\', \'>\', \'\"\', \'&\', \'&\', \' \', \"\\n<p\");\n            }\n\n            $msg[] = trim(strip_tags(str_replace($s_ar, $r_ar, (trim(strip_tags($thisarticle[\'excerpt\'])) ? $thisarticle[\'excerpt\'] : $thisarticle[\'body\']))));\n\n            if (empty($com_connect_recipient)) {\n                return gTxt(\'com_connect_field_missing\', array(\'{field}\' => gTxt(\'com_connect_recipient\')));\n            } else {\n                $to = $com_connect_recipient;\n            }\n        }\n\n        $msg = implode(\"\\n\\n\", $msg);\n\n        if ($body_form) {\n            $msg = parse_form($body_form);\n        }\n\n        $msg = str_replace(array(\"\\r\\n\", \"\\r\", \"\\n\"), array(\"\\n\", \"\\n\", $sep), $msg);\n\n        $reply   = com_connect_strip($from ? $com_connect_from : \'\');\n        $from    = com_connect_strip($from ? $from : $com_connect_from);\n        $to      = com_connect_strip($to);\n        $subject = com_connect_strip($subject);\n        $msg     = com_connect_strip($msg, false);\n\n        if ($override_email_charset) {\n            $charset = \'ISO-8859-1\';\n            $subject = utf8_decode($subject);\n            $msg     = utf8_decode($msg);\n        } else {\n            $charset = \'UTF-8\';\n        }\n\n        // TODO: function deprecated in 4.6.0.\n        $subject = encode_mailheader($subject, \'text\');\n\n        $headers = array(\n            \'from\'          => $from,\n            \'separator\'     => $sep,\n            \'reply\'         => $reply,\n            \'charset\'       => $charset,\n            \'content_type\'  => \'text/plain\',\n            \'xfer_encoding\' => \'8bit\',\n        );\n\n        safe_update(\'txp_discuss_nonce\', \"used = \'1\', issue_time = \'$now_date\'\", \"nonce = \'$nonce\'\");\n\n        if (com_connect_deliver($to, $subject, $msg, $headers, $fields, array(\'isCopy\' => false))) {\n            $_POST = array();\n\n            if ($copysender && $com_connect_from) {\n                com_connect_deliver(com_connect_strip($com_connect_from), $subject, $msg, $headers, $fields, array(\'isCopy\' => true));\n            }\n\n            if ($redirect) {\n                while (@ob_end_clean());\n                $uri = hu.ltrim($redirect,\'/\');\n\n                if (empty($_SERVER[\'FCGI_ROLE\']) && empty($_ENV[\'FCGI_ROLE\'])) {\n                    txp_status_header(\'303 See Other\');\n                    header(\'Location: \'.$uri);\n                    header(\'Connection: close\');\n                    header(\'Content-Length: 0\');\n                } else {\n                    $uri = txpspecialchars($uri);\n                    $refresh = gTxt(\'com_connect_refresh\');\n                    echo <<<END\n<!DOCTYPE html>\n<html>\n<head>\n    <meta charset=\"utf-8\">\n    <title>$sitename</title>\n    <meta http-equiv=\"refresh\" content=\"0;url=$uri\">\n</head>\n<body>\n    <a href=\"$uri\">$refresh</a>\n</body>\n</html>\nEND;\n                }\n\n                exit;\n            } else {\n                return \'<div class=\"\' . $com_connect_flags[\'cls_thanks\'] . \'\" id=\"com\'.$com_connect_form_id.\'\">\' .\n                    ($thanks_form ? parse_form($thanks_form) : $thanks) .\n                    \'</div>\';\n            }\n        } else {\n            // Plugin modules may have set error messages: display if appropriate.\n            if ($com_connect_error) {\n                $out .= n.doWrap(array_unique($com_connect_error), \'ul\', \'li\', $com_connect_flags[\'cls_wrapper\']).n;\n            } else {\n                $out .= graf(gTxt(\'com_connect_mail_sorry\'));\n            }\n        }\n    }\n\n    if ($show_input && !$send_article || gps(\'com_connect_send_article\')) {\n        $contactForm = \'<form method=\"post\"\' . ((!$show_error && $com_connect_error) ? \'\' : \' id=\"com\' . $com_connect_form_id . \'\"\') .\n            ($class ? \' class=\"\' . $class . \'\"\' : \'\') .\n            ($browser_validate ? \'\' : \' novalidate\') .\n            \' action=\"\' . txpspecialchars(serverSet(\'REQUEST_URI\')) . \'#com\' . $com_connect_form_id . \'\">\' .\n            ($label ? n . \'<fieldset>\' : \'\') .\n            ($label ? n . \'<legend>\' . txpspecialchars($label) . \'</legend>\' : \'\') .\n            $out .\n            n . \'<input type=\"hidden\" name=\"com_connect_nonce\" value=\"\' . $com_connect_nonce . \'\" />\' .\n            n . \'<input type=\"hidden\" name=\"com_connect_form_id\" value=\"\' . $com_connect_form_id . \'\" />\' .\n            $form .\n            callback_event(\'comconnect.form\') .\n            ($label ? (n . \'</fieldset>\') : \'\') .\n            n . \'</form>\';\n\n        callback_event_ref(\'comconnect.render\', \'\', 0, $contactForm, $atts);\n\n        return $contactForm;\n    }\n\n    return \'\';\n}\n\n/**\n * Tag: Render a text input field.\n *\n * Many different $types are allowed. In fact, the $type attribute is not\n * restricted in any way to allow for any future types. This does bring some\n * developer responsibility if you wish your document to validate because\n * not all attributes will be valid for all input types, despite the plugin\n * faithfully rendering whatever attributes it is given.\n *\n * A lot of invalid situations are recognised (e.g. only permitting max/min/step\n * to occur on \'numeric\' input types) but some are not easily trappable so they\n * are left to user discretion.\n *\n * @param  array  $atts Tag attributes\n * @return string HTML\n */\nfunction com_connect_text($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_flags;\n\n    extract(com_connect_lAtts(array(\n        \'autocomplete\'   => \'\',\n        \'break\'          => br,\n        \'class\'          => \'comText\',\n        \'default\'        => \'\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'inputmode\'      => \'\',\n        \'label\'          => gTxt(\'com_connect_text\'),\n        \'label_position\' => \'before\',\n        \'max\'            => null,\n        \'min\'            => null,\n        \'name\'           => \'\',\n        \'pattern\'        => \'\',\n        \'placeholder\'    => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'size\'           => \'\',\n        \'step\'           => \'\',\n        \'type\'           => \'text\',\n    ), $atts));\n\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    $datetime_types = array(\n        \'date\',\n        \'datetime\',\n        \'datetime-local\',\n        \'month\',\n        \'time\',\n        \'week\',\n    );\n\n    $numeric_types = array(\n        \'number\',\n        \'range\',\n    );\n\n    $is_datetime = (in_array($type, $datetime_types));\n    $is_numeric = (in_array($type, $numeric_types));\n\n    // Dates / times get special treatment: no default min/max if not set by tag.\n    if (!$is_datetime && $min === null) {\n        $min = 0;\n    }\n\n    if (!$is_datetime && $max === null) {\n        $max = 100;\n    }\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    if ($com_connect_submit) {\n        $value = trim(ps($name));\n        $utf8len = preg_match_all(\"/./su\", $value, $utf8ar);\n        $hlabel = txpspecialchars($label);\n        $datetime_ok = true;\n\n        if ($is_datetime) {\n            $minval = $min;\n            $maxval = $max;\n            $cmpval = $value;\n\n            try {\n                $dt = new DateTime($cmpval);\n                $cmpval = $dt->format(\'U\');\n\n                if ($min) {\n                    $dt = new DateTime($min);\n                    $minval = $dt->format(\'U\');\n                }\n\n                if ($max) {\n                    $dt = new DateTime($max);\n                    $maxval = $dt->format(\'U\');\n                }\n            } catch (Exception $e) {\n                $datetime_ok = false;\n            }\n        }\n\n        if (strlen($value)) {\n            if (!$utf8len) {\n                $com_connect_error[] = gTxt(\'com_connect_invalid_utf8\', array(\'{field}\' => $hlabel));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($is_datetime && !$datetime_ok) {\n                $com_connect_error[] = gTxt(\'com_connect_format_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $value));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($min && !$is_numeric && !$is_datetime && $utf8len < $min) {\n                $com_connect_error[] = gTxt(\'com_connect_min_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $min));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($max && !$is_numeric && !$is_datetime && $utf8len > $max) {\n                $com_connect_error[] = gTxt(\'com_connect_max_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $max));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($min && $is_datetime && $cmpval < $minval) {\n                $com_connect_error[] = gTxt(\'com_connect_minval_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $min));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($max && $is_datetime && $cmpval > $maxval) {\n                $com_connect_error[] = gTxt(\'com_connect_maxval_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $max));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($min && $is_numeric && $value < $min) {\n                $com_connect_error[] = gTxt(\'com_connect_minval_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $min));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($max && $is_numeric && $value > $max) {\n                $com_connect_error[] = gTxt(\'com_connect_maxval_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $max));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($pattern and !preg_match(\'/^\'.$pattern.\'$/\', $value)) {\n                $com_connect_error[] = gTxt(\'com_connect_pattern_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $pattern));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } else {\n                com_connect_store($name, $label, $value);\n            }\n        } elseif ($required) {\n            $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => $hlabel));\n            $isError = $com_connect_flags[\'cls_element\'];\n        }\n    } else {\n        $value = $default;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'    => (isset($id) ? $id : $name),\n        \'name\'  => $name,\n        \'type\'  => $type,\n        \'value\' => $value,\n    ));\n\n    if ($size && !$is_numeric) {\n        $attr[\'size\'] = \'size=\"\' . intval($size) . \'\"\';\n    }\n\n    if ($min && !$is_numeric) {\n        $attr[\'minlength\'] = \'minlength=\"\' . intval($min) . \'\"\';\n    }\n\n    if ($max && !$is_numeric) {\n        $attr[\'maxlength\'] = \'maxlength=\"\' . intval($max) . \'\"\';\n    }\n\n    if ($doctype !== \'xhtml\' && ($is_numeric || $is_datetime)) {\n        // Not using intval() because min/max/step can be floating point values.\n        $attr += com_connect_build_atts(array(\n            \'min\'  => $min,\n            \'max\'  => $max,\n            \'step\' => $step,\n        ));\n    }\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'autocomplete\' => $autocomplete,\n            \'form\'         => $html_form,\n            \'inputmode\'    => $inputmode,\n            \'pattern\'      => $pattern,\n            \'placeholder\'  => $placeholder,\n            \'required\'     => $required,\n        ));\n\n        if ($isError) {\n            $attr += com_connect_build_atts(array(\n                \'aria-invalid\' => \'true\',\n            ));\n        }\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $name . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        \'<input\' . $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \' />\' .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Render an email input field.\n *\n * @param  array  $atts Tag attributes\n * @return string HTML\n */\nfunction com_connect_email($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_from, $com_connect_recipient, $com_connect_flags;\n\n    // TODO: \'multiple\' attribute?\n    $defaults = array(\n        \'autocomplete\'   => \'\',\n        \'break\'          => br,\n        \'class\'          => \'comEmail\',\n        \'default\'        => \'\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_email\'),\n        \'label_position\' => \'before\',\n        \'max\'            => 100,\n        \'min\'            => 0,\n        \'name\'           => \'\',\n        \'pattern\'        => \'\',\n        \'placeholder\'    => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'send_article\'   => 0,\n        \'size\'           => \'\',\n        \'type\'           => \'email\',\n    );\n\n    extract(com_connect_lAtts($defaults, $atts));\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    $email = $com_connect_submit ? trim(ps($name)) : $default;\n\n    if ($com_connect_submit && strlen($email)) {\n        if (!is_valid_email($email)) {\n            $com_connect_error[] = gTxt(\'com_connect_invalid_email\', array(\'{email}\' => txpspecialchars($email)));\n            $isError = $com_connect_flags[\'cls_element\'];\n        } else {\n            preg_match(\"/@(.+)$/\", $email, $match);\n            $domain = $match[1];\n\n            if (is_callable(\'checkdnsrr\') && checkdnsrr(\'textpattern.com.\',\'A\') && !checkdnsrr($domain.\'.\',\'MX\') && !checkdnsrr($domain.\'.\',\'A\')) {\n                $com_connect_error[] = gTxt(\'com_connect_invalid_host\', array(\'{host}\' => txpspecialchars($domain)));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } else {\n                if ($send_article) {\n                    $com_connect_recipient = $email;\n                } else {\n                    $com_connect_from = $email;\n                }\n            }\n        }\n    }\n\n    $passed_atts = array();\n\n    foreach ($defaults as $key => $value) {\n        $passed_atts[$key] = $$key;\n    }\n\n    $passed_atts[\'isError\'] = $isError;\n    unset ($passed_atts[\'send_article\']);\n\n    return com_connect_text($passed_atts);\n}\n\n/**\n * Tag: Render a textarea input field.\n *\n * @param  array  $atts Tag attributes\n * @return string HTML\n */\nfunction com_connect_textarea($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_flags;\n\n    extract(com_connect_lAtts(array(\n        \'autocomplete\'   => \'\',\n        \'break\'          => br,\n        \'class\'          => \'comTextarea\',\n        \'cols\'           => 58,\n        \'default\'        => \'\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_message\'),\n        \'label_position\' => \'before\',\n        \'max\'            => 10000,\n        \'min\'            => 0,\n        \'name\'           => \'\',\n        \'placeholder\'    => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'rows\'           => 8,\n        \'wrap\'           => \'\',\n    ), $atts));\n\n    $min = intval($min);\n    $max = intval($max);\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    if ($com_connect_submit) {\n        $value = preg_replace(\'/^\\s*[\\r\\n]/\', \'\', rtrim(ps($name)));\n        $utf8len = preg_match_all(\"/./su\", ltrim($value), $utf8ar);\n        $hlabel = txpspecialchars($label);\n\n        if (strlen(ltrim($value))) {\n            if (!$utf8len) {\n                $com_connect_error[] = gTxt(\'com_connect_invalid_utf8\', array(\'{field}\' => $hlabel));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($min && $utf8len < $min) {\n                $com_connect_error[] = gTxt(\'com_connect_min_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $min));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($max && $utf8len > $max) {\n                $com_connect_error[] = gTxt(\'com_connect_max_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $max));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } else {\n                com_connect_store($name, $label, $value);\n            }\n        } elseif ($required) {\n            $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => $hlabel));\n            $isError = $com_connect_flags[\'cls_element\'];\n        }\n    } else {\n        $value = $default;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'        => (isset($id) ? $id : $name),\n        \'name\'      => $name,\n        \'cols\'      => intval($cols),\n        \'rows\'      => intval($rows),\n        \'maxlength\' => $max,\n    ));\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'autocomplete\' => $autocomplete,\n            \'form\'         => $html_form,\n            \'placeholder\'  => $placeholder,\n            \'required\'     => $required,\n        ));\n\n        if ($isError) {\n            $attr += com_connect_build_atts(array(\n                \'aria-invalid\' => \'true\',\n            ));\n        }\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $name . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        \'<textarea\' . $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \'>\' . txpspecialchars($value) . \'</textarea>\' .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Render a select/option input list.\n *\n * @param  array  $atts Tag attributes\n * @return string HTML\n */\nfunction com_connect_select($atts, $thing = null)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_flags;\n\n    // TODO: multiple attribute?\n    extract(com_connect_lAtts(array(\n        \'break\'          => br,\n        \'class\'          => \'comSelect\',\n        \'delimiter\'      => \',\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_option\'),\n        \'label_position\' => \'before\',\n        \'list\'           => \'\', // TODO: remove from here in favour of the global list attribute.\n        \'options\'        => gTxt(\'com_connect_general_inquiry\'),\n        \'name\'           => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'selected\'       => \'\',\n        \'size\'           => \'\',\n    ), $atts));\n\n    // Detect old-school use of the list attribute. Note that deprecated_function_with\n    // isn\'t strictly the correct error to throw, but it\'s close enough until core has\n    // a dedicated deprecated_attribute_with string.\n    if (!empty($list) && strpos($list, $delimiter) !== false) {\n        $options = $list;\n        trigger_error(gTxt(\'deprecated_function_with\', array(\'{name}\' => \'list\', \'{with}\' => \'options\')), E_USER_NOTICE);\n        unset($list, $atts[\'list\']);\n    }\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    $value = ($com_connect_submit) ? trim(ps($name)) : $selected;\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    if ($thing) {\n        com_connect_option(null, $value);\n        $out = parse($thing);\n        $options = com_connect_option(null, null);\n    } else {\n        $options = array_map(\'trim\', explode($delimiter, preg_replace(\'/[\\r\\n\\t\\s]+/\', \' \', $options)));\n        $out = \'\';\n\n        foreach ($options as $item) {\n            $out .= n.t.\'<option\' . ($item == $value ? \' selected\' . (($doctype === \'xhtml\') ? \'=\"selected\"\' : \'\') . \'>\' : \'>\') . (strlen($item) ? txpspecialchars($item) : \' \') . \'</option>\';\n        }\n    }\n\n    if ($com_connect_submit) {\n        if (strlen($value)) {\n            if (in_array($value, $options)) {\n                com_connect_store($name, $label, $value);\n            } else {\n                $com_connect_error[] = gTxt(\'com_connect_invalid_value\', array(\'{field}\' => txpspecialchars($label), \'{value}\' => txpspecialchars($value)));\n                $isError = $com_connect_flags[\'cls_element\'];\n            }\n        } elseif ($required) {\n            $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => txpspecialchars($label)));\n            $isError = $com_connect_flags[\'cls_element\'];\n        }\n    } else {\n        $value = $selected;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'   => (isset($id) ? $id : $name),\n        \'name\' => $name,\n    ));\n\n    if ($size && is_numeric($size)) {\n        $attr[\'size\'] = \'size=\"\' . intval($size) . \'\"\';\n    }\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'form\'     => $html_form,\n            \'required\' => $required,\n        ));\n\n        if ($isError) {\n            $attr += com_connect_build_atts(array(\n                \'aria-invalid\' => \'true\',\n            ));\n        }\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $name . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        n . \'<select\' . $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \'>\' .\n            $out .\n        n . \'</select>\' .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Render a set of select options.\n *\n * @param  array  $atts  Tag attributes\n * @param  string $thing Tag\'s container\n * @return string HTML\n */\nfunction com_connect_option($atts, $thing = null)\n{\n    static $options;\n    static $match;\n\n    if ($atts === null) {\n        if ($thing === null) {\n            return $options;\n        } else {\n            $match = $thing;\n            return;\n        }\n    }\n\n    global $com_connect_error, $com_connect_submit;\n\n    extract(com_connect_lAtts(array(\n        \'class\'    => \'comOption\',\n        \'label\'    => null,\n        \'selected\' => null,\n        \'value\'    => null,\n    ), $atts));\n\n    $val = ($value === null) ? (!empty($thing) ? $thing : (!empty($label) ? $label : null)) : $value;\n    $label = ($label === null) ? (!empty($thing) ? $thing : (!empty($value) ? $value : null)) : $label;\n\n    $attr = array();\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    if ($com_connect_submit) {\n        $options[] = $val;\n\n        if ($val !== null && ((string)$val === (string)$match)) {\n            $attr[] = \'selected\' . (($doctype === \'xhtml\') ? \'=\"selected\"\' : \'\');\n        }\n    } elseif ($selected || ($val !== null && ((string)$val === (string)$match))) {\n        $attr[] = \'selected\' . (($doctype === \'xhtml\') ? \'=\"selected\"\' : \'\');\n    }\n\n    // Core attributes.\n    $attr += com_connect_build_atts(array(\n        \'value\' => $val,\n    ));\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classStr = (($class) ? \' class=\"\' . $class . \'\"\' : \'\');\n\n    return \'<option\' . $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \'>\' . txpspecialchars($label) . \'</option>\';\n}\n\n/**\n * Tag: Render a checkbox.\n *\n * @param  array  $atts  Tag attributes\n * @return string HTML\n * @todo checkbox groups?\n */\nfunction com_connect_checkbox($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_flags;\n\n    extract(com_connect_lAtts(array(\n        \'break\'          => \' \',\n        \'checked\'        => 0,\n        \'class\'          => \'comCheckbox\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_checkbox\'),\n        \'label_position\' => \'after\',\n        \'name\'           => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'value\'          => null,\n    ), $atts));\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    if ($com_connect_submit) {\n        $theValue = (bool) ps($name);\n\n        if ($required && !$theValue) {\n            $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => txpspecialchars($label)));\n            $isError = $com_connect_flags[\'cls_element\'];\n        } else {\n            $toStore = (($value !== null && $theValue) ? $value : ($theValue ? gTxt(\'yes\') : gTxt(\'no\')));\n            com_connect_store($name, $label, $toStore);\n        }\n    } else {\n        $theValue = $checked;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'    => (isset($id) ? $id : $name),\n        \'name\'  => $name,\n    ));\n\n    if ($value !== null) {\n        $attr += com_connect_build_atts(array(\n            \'value\' => $value,\n        ));\n    }\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'form\'     => $html_form,\n            \'required\' => $required,\n        ));\n\n        if ($isError) {\n            $attr += com_connect_build_atts(array(\n                \'aria-invalid\' => \'true\',\n            ));\n        }\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $name . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        \'<input type=\"checkbox\"\' . $classStr .\n            ($theValue ? \' checked\' . (($doctype === \'xhtml\') ? \'=\"checked\"\' : \'\') : \'\') . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \' />\' .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Render a radio button.\n *\n * @param  array  $atts  Tag attributes\n * @return string HTML\n */\nfunction com_connect_radio($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_values, $com_connect_flags, $com_connect_group;\n\n    extract(com_connect_lAtts(array(\n        \'break\'          => \' \',\n        \'checked\'        => 0,\n        \'class\'          => \'comRadio\',\n        \'group\'          => \'\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_option\'),\n        \'label_position\' => \'after\',\n        \'name\'           => \'\',\n        \'required\'       => null,\n        \'value\'          => null,\n    ), $atts));\n\n    static $cur_name = array();\n    static $cur_req = array();\n    static $cur_group = null;\n\n    if (!$group) {\n        if ($cur_group === null) {\n            $group = gTxt(\'com_connect_radio\');\n        } else {\n            $group = $cur_group;\n        }\n    } else {\n        if (!$name) {\n            $name = $group;\n        }\n    }\n\n    if (isset($cur_name[$group])) {\n        if (!$name) {\n            $name = $cur_name[$group];\n        }\n    } else {\n        if ($name) {\n            $cur_name[$group] = $name;\n        } else {\n            $name = $cur_name[$group] = gTxt(\'com_connect_radio\');\n        }\n    }\n\n    if (isset($cur_req[$group])) {\n        if ($required === null) {\n            $required = $cur_req[$group];\n        }\n    } else {\n        if ($required === null) {\n            $required = $cur_req[$group] = $com_connect_flags[\'required\'];\n        } else {\n            $cur_req[$group] = $required;\n        }\n    }\n\n    $cur_group = $group;\n\n    $id = \'q\' . md5($name . \'=>\' . $label);\n    $name = com_connect_label2name($name);\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n    $com_connect_group[$name][$id][\'req\'] = $required;\n    $com_connect_group[$name][$id][\'label\'] = $group;\n\n    if ($com_connect_submit) {\n        $toCompare = ($value === null ? $id : $value);\n        $is_checked = (ps($name) == $toCompare);\n        $com_connect_group[$name][$id][\'isSet\'] = $is_checked;\n\n        if ($is_checked || $checked && !isset($com_connect_values[$name])) {\n            com_connect_store($name, $group, ($value !== null ? $value : $label));\n        }\n    } else {\n        $is_checked = $checked;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'    => $id,\n        \'name\'  => $name,\n        \'value\' => ($value !== null ? $value : $id),\n    ));\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'form\'     => $html_form,\n            \'required\' => $required,\n        ));\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $id . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        \'<input type=\"radio\"\'. $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') .\n            ( $is_checked ? \' checked\' . (($doctype === \'xhtml\') ? \'=\"checked\"\' : \'\'). \' />\' : \' />\') .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Store server information in the payload.\n *\n * @param  array  $atts  Tag attributes\n */\nfunction com_connect_serverinfo($atts)\n{\n    global $com_connect_submit;\n\n    extract(com_connect_lAtts(array(\n        \'label\' => \'\',\n        \'name\'  => \'\',\n    ), $atts));\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    if (strlen($name) && $com_connect_submit) {\n        if (!$label) {\n            $label = $name;\n        }\n\n        com_connect_store($name, $label, serverSet($name));\n    }\n}\n\n/**\n * Tag: Store a secret value in the payload.\n *\n * @param  array  $atts  Tag attributes\n */\nfunction com_connect_secret($atts, $thing = null)\n{\n    global $com_connect_submit;\n\n    extract(com_connect_lAtts(array(\n        \'label\' => gTxt(\'com_connect_secret\'),\n        \'name\'  => \'\',\n        \'value\' => \'\',\n    ), $atts));\n\n    $name = com_connect_label2name($name ? $name : $label);\n\n    if ($com_connect_submit) {\n        if ($thing) {\n            $value = trim(parse($thing));\n        }\n\n        com_connect_store($name, $label, $value);\n    }\n\n    return \'\';\n}\n\n/**\n * Tag: Render a submit button.\n *\n * If the container is used, a button tag is rendered, otherwise a\n * standard input type=submit button is rendered.\n *\n * @param  array  $atts  Tag attributes\n * @param  string $thing Tag\'s container\n * @return string HTML\n */\nfunction com_connect_submit($atts, $thing = null)\n{\n    global $com_connect_flags;\n\n    extract(com_connect_lAtts(array(\n        \'class\'     => \'comSubmit\',\n        \'html_form\' => $com_connect_flags[\'this_form\'],\n        \'label\'     => gTxt(\'com_connect_send\'),\n    ), $atts));\n\n    $label = txpspecialchars($label);\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    $attr = array();\n\n    // HTML5 attributes.\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'form\' => $html_form,\n        ));\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classStr = ($class ? \' class=\"\' . $class . \'\"\' : \'\');\n\n    if (strlen($thing)) {\n        return \'<button type=\"submit\"\' . $classStr . \' name=\"com_connect_submit\" value=\"\' . $label . \'\"\' . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \'>\' . ($thing ? trim(parse($thing)) : $label) . \'</button>\';\n    } else {\n        return \'<input type=\"submit\"\' . $classStr . \' name=\"com_connect_submit\" value=\"\' . $label . \'\"\' . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \' />\';\n    }\n}\n\n/**\n * Tag: Render a link to allow someone to forward a page to a friend.\n *\n * @param  array  $atts  Tag attributes\n * @return string HTML\n */\nfunction com_connect_send_article($atts)\n{\n    if (!isset($_REQUEST[\'com_connect_send_article\'])) {\n        $linktext = (empty($atts[\'linktext\'])) ? gTxt(\'com_connect_send_article\') : $atts[\'linktext\'];\n        $join = (empty($_SERVER[\'QUERY_STRING\'])) ? \'?\' : \'&\';\n        $href = $_SERVER[\'REQUEST_URI\'] . $join . \'com_connect_send_article=yes\';\n\n        return \'<a href=\"\' . txpspecialchars($href) . \'\" rel=\"nofollow\">\' . txpspecialchars($linktext) . \'</a>\';\n    }\n\n    return;\n}\n\n/**\n * Perform post-processing for aggregate (group) controls like radio sets.\n *\n * @todo Can this be done any neater?\n * @todo Should this be exposed as a callback to allow plugins to extend the functionality?\n */\nfunction com_connect_group_validate()\n{\n    global $com_connect_group, $com_connect_error;\n\n    $flags = array();\n\n    if ($com_connect_group) {\n        foreach ($com_connect_group as $key => $grp) {\n            foreach ($grp as $id => $atts) {\n                $flags[$key][\'label\'] = $atts[\'label\'];\n\n                if (!empty($atts[\'req\']) && !isset($flags[$key][\'req\'])) {\n                    $flags[$key][\'req\'] = 1;\n                }\n\n                if (!empty($atts[\'isSet\']) && !isset($flags[$key][\'isSet\'])) {\n                    $flags[$key][\'isSet\'] = 1;\n                }\n            }\n        }\n\n        foreach ($flags as $key => $data) {\n            if (!empty($data[\'req\']) && !isset($data[\'isSet\'])) {\n                $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => txpspecialchars($data[\'label\'])));\n            }\n        }\n    }\n}\n\n/**\n * Process tag attributes and set defaults.\n *\n * Handles HTML \'global\' attributes too, though what we consider global differs slightly\n * from the W3C: any attributes that apply to most of the <input> tags is considered\n * global. Just saves repetitive code in each tag handler.\n *\n * Note that \'class\' is omitted, because that has different default values per input\n * widget, so it is dealt with explicitly in each tag handler.\n *\n * @param  array $arr  Acceptable tag attributes\n * @param  array $atts User-submitted attribute values\n * @return array\n */\nfunction com_connect_lAtts($pairs, $atts)\n{\n    lAtts(array(\'label\' => \'\'), $atts, false);\n\n    foreach (array(\'button\', \'copysender\', \'checked\', \'required\', \'send_article\', \'show_input\', \'show_error\') as $key) {\n        if (isset($atts[$key])) {\n            $atts[$key] = ($atts[$key] === \'yes\' || intval($atts[$key])) ? 1 : 0;\n        }\n    }\n\n    if (isset($atts[\'break\']) && $atts[\'break\'] === \'br\') {\n        $atts[\'break\'] = \'<br />\';\n    }\n\n    $com_connect_globals = array(\n        \'accesskey\'  => \'\',\n        \'autofocus\'  => \'\',\n        \'dir\'        => \'\',\n        \'disabled\'   => \'\',\n        \'hidden\'     => \'\',\n        \'id\'         => \'\',\n        \'lang\'       => \'\',\n        \'list\'       => \'\',\n        \'readonly\'   => \'\',\n        \'spellcheck\' => \'\',\n        \'style\'      => \'\',\n        \'tabindex\'   => \'\',\n        \'title\'      => \'\',\n        \'translate\'  => \'\',\n    );\n\n    foreach ($atts as $name => $value) {\n        if (array_key_exists($name, $pairs) || substr($name, 0, 2) === \'on\' || array_key_exists($name, $com_connect_globals)) {\n            $pairs[$name] = $value;\n        } elseif (get_pref(\'production_status\') != \'live\') {\n            trigger_error(gTxt(\'unknown_attribute\', array(\'{att}\' => $name)));\n        }\n    }\n\n    $pairs[\'com_connect_globals\'] = $com_connect_globals;\n\n    return ($pairs) ? $pairs : false;\n}\n\n/**\n * Build a sanitized, unique attribute array from the given pairs.\n *\n * If called multiple times, earlier values supersede later values\n * if the keys clash.\n *\n * @param  array $pairs    Attribute tuples to compile\n * @param  array $defaults Attribute values to use if the $pairs tuple value is empty\n * @return array\n */\nfunction com_connect_build_atts($pairs, $defaults = array())\n{\n    $attr = array();\n\n    $booleans = array(\n        \'autofocus\',\n        \'checked\',\n        \'disabled\',\n        \'hidden\',\n        \'multiple\',\n        \'readonly\',\n        \'required\',\n        \'selected\',\n        );\n\n    foreach ($pairs as $key => $value) {\n        if ($value !== \'\' && $value !== null) {\n            $attr[$key] = $key . (in_array($key, $booleans) ? \'\' : \'=\"\' . txpspecialchars($value) . \'\"\');\n        } else {\n            if (isset($defaults[$key])) {\n                $attr[$key] = $key . (in_array($key, $booleans) ? \'\' : \'=\"\' . txpspecialchars($defaults[$key]) . \'\"\');\n            }\n        }\n    }\n\n    return $attr;\n}\n\n/**\n * Replace nulls and newlines in content with spaces.\n *\n * @param string $str    String to sanitize\n * @param bool   $header Whether the string is a header or not\n */\nfunction com_connect_strip($str, $header = true)\n{\n    if ($header) {\n        // TODO: strip_rn will be deprecated in 4.6.0.\n        $str = strip_rn($str);\n    }\n\n    return preg_replace(\'/[\\x00]/\', \' \', $str);\n}\n\n/**\n * Handle content delivery of payload.\n *\n * Triggers a \'comconnect.deliver\' callback event to override or augment the\n * delivery mechanism. Third party plugins can make alterations to the $payload,\n * then return one of the strings:\n *  -> \"comconnect.send\" (or no return value) to allow the plugin to continue mailing after the 3rd party plugin completes\n *  -> \"comconnect.skip\" to skip com_connect\'s mailing (i.e. the 3rd party handles mailing) and return \'success\'\n *  -> \"comconnect.fail\" to skip com_connect\'s mailing and return \'fail\'\n *\n * By hooking into the callback\'s step you can target either the main \'send\'\n * process or the \'copysender\' process. Examples of things you could do:\n * -> Add Multi-part MIME headers for HTML emails.\n * -> Add CC: or BCC: fields.\n * -> Subscribe people to mailing lists.\n * -> Handle the mailing process independently of Textpattern.\n *\n * @param string $to      Delivery address\n * @param string $subject Subject of message\n * @param string $body    Message content\n * @param array  $headers Message headers as tuples\n * @param array  $fields  Message field names and content as tuples\n * @param array  $flags   Signals to govern delivery / callback behaviour\n */\nfunction com_connect_deliver($to, $subject, $body, $headers, $fields, $flags)\n{\n    $payload = array(\n        \'to\'      => $to,\n        \'subject\' => $subject,\n        \'headers\' => $headers,\n        \'body\'    => $body,\n        \'fields\'  => $fields,\n    );\n\n    $flavour = ($flags[\'isCopy\'] === true) ? \'copysender\' : \'send\';\n\n    // Allow plugins to override or alter default action (mail) if required.\n    // TODO: use has_handler() from 4.6.0+.\n    $ret = callback_event_ref(\'comconnect.deliver\', $flavour, 0, $payload);\n\n    if (in_array(\'comconnect.fail\', $ret)) {\n        return false;\n    } elseif (in_array(\'comconnect.skip\', $ret)) {\n        return true;\n    }\n\n    extract($payload);\n\n    $smtp_from = get_pref(\'smtp_from\');\n\n    if (!is_callable(\'mail\')) {\n        return (get_pref(\'production_status\') === \'live\')\n            ? gTxt(\'com_connect_mail_sorry\')\n            : gTxt(\'warn_mail_unavailable\');\n    }\n\n    $sep = (!empty($headers[\'separator\'])) ? $headers[\'separator\'] : (IS_WIN ? \"\\r\\n\" : \"\\n\");\n    $xfer_encoding = (!empty($headers[\'xfer_encoding\'])) ? $headers[\'xfer_encoding\'] : \'8bit\';\n    $content_type = (!empty($headers[\'content_type\'])) ? $headers[\'content_type\'] : \'text/plain\';\n    $reply = (!empty($headers[\'reply\'])) ? $headers[\'reply\'] : \'\';\n    $charset = (!empty($headers[\'charset\'])) ? $headers[\'charset\'] : \'UTF-8\';\n    $x_mailer = (!empty($headers[\'x_mailer\'])) ? $headers[\'x_mailer\'] : \'Textpattern (com_connect)\';\n\n    $header_string = \'From: \' . $headers[\'from\'] .\n        ($reply ? ($sep . \'Reply-To: \' . $reply) : \'\') .\n        $sep . \'X-Mailer: \' . $x_mailer .\n        $sep . \'X-Originating-IP: \' . com_connect_strip((!empty($_SERVER[\'HTTP_X_FORWARDED_FOR\']) ? $_SERVER[\'HTTP_X_FORWARDED_FOR\'] . \' via \' : \'\') . $_SERVER[\'REMOTE_ADDR\']) .\n        $sep . \'Content-Transfer-Encoding: \' . $xfer_encoding .\n        $sep . \'Content-Type: \' . $content_type . \'; charset=\"\' . $charset . \'\"\';\n\n    // Remove all the header entries that have already been handled.\n    unset(\n        $headers[\'separator\'],\n        $headers[\'xfer_encoding\'],\n        $headers[\'content_type\'],\n        $headers[\'reply\'],\n        $headers[\'charset\'],\n        $headers[\'x_mailer\'],\n        $headers[\'from\']\n    );\n\n    // Any remaining headers set by plugins are appended as-is.\n    foreach ($headers as $name => $value) {\n        $header_string .= $sep . $name . \': \' . $value;\n    }\n\n    if (is_valid_email($smtp_from)) {\n        if (IS_WIN) {\n            ini_set(\'sendmail_from\', $smtp_from);\n        } elseif (!ini_get(\'safe_mode\')) {\n            return mail($to, $subject, $body, $header_string, \'-f\'.$smtp_from);\n        }\n    }\n\n    return mail($to, $subject, $body, $header_string);\n}\n\n/**\n * Evaluate return values from plugins.\n */\nclass comconnect_evaluation\n{\n    private $status;\n\n    /**\n     * Constructor.\n     */\n    function __construct()\n    {\n        $this->status = 0;\n    }\n\n    /**\n     * Append the given status to the counter.\n     */\n    function add_comconnect_status($check)\n    {\n        $this->status += $check;\n    }\n\n    /**\n     * Fetch the current evaluator status.\n     */\n    function get_comconnect_status()\n    {\n        return $this->status;\n    }\n}\n\n/**\n * Evaluator singleton for checking return values from plugins.\n */\nfunction &get_comconnect_evaluator()\n{\n    static $instance;\n\n    if (!isset($instance)) {\n        $instance = new comconnect_evaluation();\n    }\n\n    return $instance;\n}\n\n/**\n * Convert the given label to a suitably unique name.\n *\n * @param string $label Label to convert to name.\n */\nfunction com_connect_label2name($label)\n{\n    $label = trim($label);\n\n    if (strlen($label) == 0) {\n        return \'invalid\';\n    }\n\n    if (strlen($label) <= 32 && preg_match(\'/^[a-zA-Z][A-Za-z0-9:_-]*$/\', $label)) {\n        return $label;\n    } else {\n        return \'q\' . md5($label);\n    }\n}\n\n/**\n * Store the given names/values in the global arrays.\n *\n * @param  string $name  Parameter name\n * @param  string $label Parameter label\n * @param  string $value Parameter value\n */\nfunction com_connect_store($name, $label, $value)\n{\n    global $com_connect_form, $com_connect_labels, $com_connect_values;\n\n    $com_connect_form[$label] = $value;\n    $com_connect_labels[$name] = $label;\n    $com_connect_values[$name] = $value;\n}\n\n/**\n * Override the language strings if necessary.\n *\n * @param  string $lang Language designator (e.g. fr-fr)\n * @return array        Partial language array to merge with $textarray\n */\nfunction com_connect_load_lang($lang = LANG)\n{\n    $out = array();\n\n    if ($lang != LANG) {\n\n        $rs = safe_rows(\"name, data\", \'txp_lang\', \"lang = \'\" . doSlash($lang) . \"\' AND name like \'com\\_connect\\_%\'\");\n\n        if (!empty($rs)) {\n            foreach ($rs as $a) {\n                $out[$a[\'name\']] = $a[\'data\'];\n            }\n        }\n    }\n\n    return $out;\n}\n\n/**\n * Return the value of the given attribute, by name or its label.\n *\n * @param  array $atts Attribute to return\n * @return string\n */\nfunction com_connect_value($atts)\n{\n    global $com_connect_values, $com_connect_form;\n\n    extract(lAtts(array(\n        \'label\' => \'\',\n        \'name\'  => \'\',\n    ), $atts));\n\n    $str = \'\';\n\n    if ($name) {\n        $str = isset($com_connect_values[$name]) ? $com_connect_values[$name] : \'\';\n    } elseif ($label) {\n        $str = isset($com_connect_form[$name]) ? $com_connect_form[$name] : \'\';\n    }\n\n    return trim($str);\n}\n\n/**\n * Return the label for the given attribute.\n *\n * @param  array $atts Attribute name to return\n * @return string\n */\nfunction com_connect_label($atts)\n{\n    global $com_connect_labels;\n\n    extract(lAtts(array(\n        \'name\' => \'\',\n    ), $atts));\n\n    if ($name) {\n        return isset($com_connect_labels[$name]) ? $com_connect_labels[$name] : \'\';\n    }\n\n    return \'\';\n}\n\n/**\n * Conditional tag for checking variable conditions.\n *\n * @param  array  $atts  Attributes to check\n * @param  string $thing Tag\'s container content\n * @return string\n */\nfunction com_connect_if($atts, $thing = null)\n{\n    extract(lAtts(array(\n        \'label\' => \'\',\n        \'name\'  => \'\',\n        \'value\' => null,\n    ), $atts));\n\n    $val = com_connect_value(array(\n        \'label\' => $label,\n        \'name\'  => $name,\n    ));\n\n    if ($val) {\n        $cond = ($value === null || $val == $value);\n    } else {\n        $cond = false;\n    }\n\n    return parse(EvalElse($thing, $cond));\n}', '//<?php\n/**\n * com_connect: A Textpattern CMS plugin for mail delivery of contact forms.\n */\n\n// Register tags if necessary.\nif (class_exists(\'\\Textpattern\\Tag\\Registry\')) {\n    Txp::get(\'\\Textpattern\\Tag\\Registry\')\n        ->register(\'com_connect\')\n        ->register(\'com_connect_text\')\n        ->register(\'com_connect_email\')\n        ->register(\'com_connect_textarea\')\n        ->register(\'com_connect_select\')\n        ->register(\'com_connect_option\')\n        ->register(\'com_connect_checkbox\')\n        ->register(\'com_connect_radio\')\n        ->register(\'com_connect_serverinfo\')\n        ->register(\'com_connect_secret\')\n        ->register(\'com_connect_submit\')\n        ->register(\'com_connect_send_article\')\n        ->register(\'com_connect_value\')\n        ->register(\'com_connect_label\')\n        ->register(\'com_connect_if\');\n}\n\n/**\n * Tag: encapsulate a contact form.\n *\n * Triggers the following callbacks:\n *  -> \'comconnect.form\' during form rendering so additional fields (e.g. spam honeypots) can be injected.\n *  -> \'comconnect.render\' immediately prior to form rendering so other parts of the form content may be altered.\n *  -> \'comconnect.submit\' on successful posting of form data. Primarily of use for spam\n *     plugins: they can return a non-zero value to signal that the form should NOT be sent.\n *\n * @param array  $atts  Tag attributes\n * @param string $thing Tag\'s container content\n */\nfunction com_connect($atts, $thing = null)\n{\n    global $sitename, $textarray, $com_connect_flags, $com_connect_from,\n        $com_connect_recipient, $com_connect_error, $com_connect_submit,\n        $com_connect_form, $com_connect_labels, $com_connect_values;\n\n    extract(com_connect_lAtts(array(\n        \'body_form\'        => \'\',\n        \'class\'            => \'comConnectForm\',\n        \'classes\'          => \'\',\n        \'copysender\'       => 0,\n        \'expire\'           => 600,\n        \'form\'             => \'\',\n        \'from\'             => \'\',\n        \'from_form\'        => \'\',\n        \'label\'            => null,\n        \'browser_validate\' => 1,\n        \'redirect\'         => \'\',\n        \'required\'         => \'1\',\n        \'show_error\'       => 1,\n        \'show_input\'       => 1,\n        \'send_article\'     => 0,\n        \'subject\'          => null,\n        \'subject_form\'     => \'\',\n        \'to\'               => \'\',\n        \'to_form\'          => \'\',\n        \'thanks\'           => null,\n        \'thanks_form\'      => \'\'\n    ), $atts));\n\n    if (!empty($lang)) {\n        $strings = com_connect_load_lang($lang);\n        $textarray = array_merge($textarray, $strings);\n    }\n\n    // Set defaults, in the local language if necessary.\n    if ($label === null) {\n        $label = gTxt(\'com_connect_contact\');\n    }\n\n    if ($subject === null) {\n        $subject = gTxt(\'com_connect_email_subject\', array(\'{site}\' => html_entity_decode($sitename,ENT_QUOTES)));\n    }\n\n    if ($thanks === null) {\n        $thanks = graf(gTxt(\'com_connect_email_thanks\'));\n    }\n\n    unset($atts[\'show_error\'], $atts[\'show_input\']);\n\n    $defaultClassNames = array(\n        \'element\'  => \'errorElement\',\n        \'wrapper\'  => \'comError\',\n        \'required\' => \'comRequired\',\n        \'thanks\'   => \'comThanks\',\n        );\n\n    $com_connect_form_id = md5(serialize($atts) . preg_replace(\'/[\\t\\s\\r\\n]/\', \'\', $thing));\n    $com_connect_submit = (ps(\'com_connect_form_id\') == $com_connect_form_id);\n    $override_email_charset = (get_pref(\'override_emailcharset\') && is_callable(\'utf8_decode\'));\n    $userClassNames = do_list($classes);\n\n    foreach (array_merge($defaultClassNames, $userClassNames) as $classKey => $classValue) {\n        if (strpos($classValue, \':\') !== false) {\n            $classParts = do_list($classValue, \':\');\n\n            if (count($classParts) === 2) {\n                $com_connect_flags[\'cls_\' . $classParts[0]] = $classParts[1];\n            }\n        } elseif ($classKey && $classValue) {\n            $com_connect_flags[\'cls_\' . $classKey] = $classValue;\n        }\n    }\n\n    // The $com_connect_flags[\'this_form\'] global is set if an id is supplied for the <form>.\n    // This value then becomes the default value for all html_form (a.k.a. form=)\n    // attributes for any input tags in this tag\'s container, providing HTML5 is in use.\n    $com_connect_flags[\'this_form\'] = \'com\' . $com_connect_form_id;\n\n    // Global toggle for required attribute.\n    $com_connect_flags[\'required\'] = $required;\n\n    $now = time();\n    $now_date = date(\'Y-m-d H:i:s\', $now);\n\n    $expire = abs(assert_int($expire));\n\n    static $headers_sent = false;\n    if (!$headers_sent) {\n        header(\'Last-Modified: \' . gmdate(\'D, d M Y H:i:s\', $now - (3600 * 24 * 7)) . \' GMT\');\n        header(\'Expires: \' . gmdate(\'D, d M Y H:i:s\', $now + $expire) . \' GMT\');\n        header(\'Cache-Control: no-cache, must-revalidate\');\n        $headers_sent = true;\n    }\n\n    $nonce = doSlash(ps(\'com_connect_nonce\'));\n    $renonce = false;\n\n    if ($com_connect_submit) {\n        // Could use \"interval $expire second\" but multiple com_connect forms could delete data\n        // that might be in use by other forms.\n        // TODO: use max(600, $expire)? Not perfect, but ensures a safe minimum deletion rate.\n        safe_delete(\'txp_discuss_nonce\', \"issue_time < date_sub(\'$now_date\', interval 10 minute)\");\n        if ($rs = safe_row(\'used\', \'txp_discuss_nonce\', \"nonce = \'$nonce\'\")) {\n            if ($rs[\'used\']) {\n                unset($com_connect_error);\n                $com_connect_error[] = gTxt(\'com_connect_form_used\');\n                $renonce = true;\n                $_POST = array();\n                $_POST[\'com_connect_submit\'] = true;\n                $_POST[\'com_connect_form_id\'] = $com_connect_form_id;\n                $_POST[\'com_connect_nonce\'] = $nonce;\n            }\n        } else {\n            $com_connect_error[] = gTxt(\'com_connect_form_expired\');\n            $renonce = true;\n        }\n    }\n\n    if ($com_connect_submit && $nonce && !$renonce) {\n        $com_connect_nonce = $nonce;\n    } elseif (!$show_error || $show_input) {\n        $com_connect_nonce = md5(uniqid(rand(), true));\n        safe_insert(\'txp_discuss_nonce\', \"issue_time = \'\" . $now_date . \"\', nonce = \'$com_connect_nonce\'\");\n    }\n\n    $form = ($form) ? fetch_form($form) : $thing;\n\n    if (empty($form)) {\n        $form = \'\n<txp:com_connect_text label=\"\'.gTxt(\'com_connect_name\').\'\" /><br />\n<txp:com_connect_email /><br />\'.\n($send_article ? \'<txp:com_connect_email send_article=\"1\" label=\"\'.gTxt(\'com_connect_recipient\').\'\" /><br />\' : \'\').\n\'<txp:com_connect_textarea /><br />\n<txp:com_connect_submit />\n\';\n    }\n\n    $form = parse($form);\n\n    // Perform aggregate functions for checking radio sets.\n    if ($com_connect_submit) {\n        com_connect_group_validate();\n    }\n\n    if ($to_form) {\n        $to = parse_form($to_form);\n    }\n\n    if (!$to && !$send_article) {\n        return gTxt(\'com_connect_to_missing\');\n    }\n\n    $out = \'\';\n\n    if (!$com_connect_submit) {\n        // Don\'t show errors or send mail.\n    } elseif (!empty($com_connect_error)) {\n        if ($show_error || !$show_input) {\n            $out .= n.doWrap(array_unique($com_connect_error), \'ul\', \'li\', $com_connect_flags[\'cls_wrapper\']).n;\n\n            if (!$show_input) {\n                return $out;\n            }\n        }\n    } elseif ($show_input && is_array($com_connect_form)) {\n        // Load and check spam plugins.\n        callback_event(\'comconnect.submit\');\n        $evaluation =& get_comconnect_evaluator();\n        $clean = $evaluation->get_comconnect_status();\n\n        if ($clean != 0) {\n            return gTxt(\'com_connect_spam\');\n        }\n\n        if ($from_form) {\n            $from = parse_form($from_form);\n        }\n\n        if ($subject_form) {\n            $subject = parse_form($subject_form);\n        }\n\n        $sep = IS_WIN ? \"\\r\\n\" : \"\\n\";\n        $msg = array();\n        $fields = array();\n\n        foreach ($com_connect_labels as $name => $lbl) {\n            if (!is_array($com_connect_values[$name])) {\n                if (trim($com_connect_values[$name]) === false) {\n                    continue;\n                }\n\n                $msg[] = $lbl . \': \' . $com_connect_values[$name];\n            }\n\n            $fields[$name] = $com_connect_values[$name];\n        }\n\n        if ($send_article) {\n            global $thisarticle;\n\n            $subject = str_replace(\'&#38;\', \'&\', $thisarticle[\'title\']);\n            $msg[] = permlinkurl($thisarticle);\n            $msg[] = $subject;\n            $s_ar = array(\'&#8216;\', \'&#8217;\', \'&#8220;\', \'&#8221;\', \'&#8217;\', \'&#8242;\', \'&#8243;\', \'&#8230;\', \'&#8211;\', \'&#8212;\', \'&#215;\', \'&#8482;\', \'&#174;\', \'&#169;\', \'&lt;\', \'&gt;\', \'&quot;\', \'&amp;\', \'&#38;\', \"\\t\", \'<p\');\n\n            if ($override_email_charset) {\n                $r_ar = array(\"\'\", \"\'\", \'\"\', \'\"\', \"\'\", \"\'\", \'\"\', \'...\', \'-\', \'--\', \'x\', \'[tm]\', \'(r)\', \'(c)\', \'<\', \'>\', \'\"\', \'&\', \'&\', \' \', \"\\n<p\");\n            } else {\n                $r_ar = array(\'‘\', \'’\', \'“\', \'”\', \'’\', \'?\', \'?\', \'…\', \'–\', \'—\', \'×\', \'™\', \'®\', \'©\', \'<\', \'>\', \'\"\', \'&\', \'&\', \' \', \"\\n<p\");\n            }\n\n            $msg[] = trim(strip_tags(str_replace($s_ar, $r_ar, (trim(strip_tags($thisarticle[\'excerpt\'])) ? $thisarticle[\'excerpt\'] : $thisarticle[\'body\']))));\n\n            if (empty($com_connect_recipient)) {\n                return gTxt(\'com_connect_field_missing\', array(\'{field}\' => gTxt(\'com_connect_recipient\')));\n            } else {\n                $to = $com_connect_recipient;\n            }\n        }\n\n        $msg = implode(\"\\n\\n\", $msg);\n\n        if ($body_form) {\n            $msg = parse_form($body_form);\n        }\n\n        $msg = str_replace(array(\"\\r\\n\", \"\\r\", \"\\n\"), array(\"\\n\", \"\\n\", $sep), $msg);\n\n        $reply   = com_connect_strip($from ? $com_connect_from : \'\');\n        $from    = com_connect_strip($from ? $from : $com_connect_from);\n        $to      = com_connect_strip($to);\n        $subject = com_connect_strip($subject);\n        $msg     = com_connect_strip($msg, false);\n\n        if ($override_email_charset) {\n            $charset = \'ISO-8859-1\';\n            $subject = utf8_decode($subject);\n            $msg     = utf8_decode($msg);\n        } else {\n            $charset = \'UTF-8\';\n        }\n\n        // TODO: function deprecated in 4.6.0.\n        $subject = encode_mailheader($subject, \'text\');\n\n        $headers = array(\n            \'from\'          => $from,\n            \'separator\'     => $sep,\n            \'reply\'         => $reply,\n            \'charset\'       => $charset,\n            \'content_type\'  => \'text/plain\',\n            \'xfer_encoding\' => \'8bit\',\n        );\n\n        safe_update(\'txp_discuss_nonce\', \"used = \'1\', issue_time = \'$now_date\'\", \"nonce = \'$nonce\'\");\n\n        if (com_connect_deliver($to, $subject, $msg, $headers, $fields, array(\'isCopy\' => false))) {\n            $_POST = array();\n\n            if ($copysender && $com_connect_from) {\n                com_connect_deliver(com_connect_strip($com_connect_from), $subject, $msg, $headers, $fields, array(\'isCopy\' => true));\n            }\n\n            if ($redirect) {\n                while (@ob_end_clean());\n                $uri = hu.ltrim($redirect,\'/\');\n\n                if (empty($_SERVER[\'FCGI_ROLE\']) && empty($_ENV[\'FCGI_ROLE\'])) {\n                    txp_status_header(\'303 See Other\');\n                    header(\'Location: \'.$uri);\n                    header(\'Connection: close\');\n                    header(\'Content-Length: 0\');\n                } else {\n                    $uri = txpspecialchars($uri);\n                    $refresh = gTxt(\'com_connect_refresh\');\n                    echo <<<END\n<!DOCTYPE html>\n<html>\n<head>\n    <meta charset=\"utf-8\">\n    <title>$sitename</title>\n    <meta http-equiv=\"refresh\" content=\"0;url=$uri\">\n</head>\n<body>\n    <a href=\"$uri\">$refresh</a>\n</body>\n</html>\nEND;\n                }\n\n                exit;\n            } else {\n                return \'<div class=\"\' . $com_connect_flags[\'cls_thanks\'] . \'\" id=\"com\'.$com_connect_form_id.\'\">\' .\n                    ($thanks_form ? parse_form($thanks_form) : $thanks) .\n                    \'</div>\';\n            }\n        } else {\n            // Plugin modules may have set error messages: display if appropriate.\n            if ($com_connect_error) {\n                $out .= n.doWrap(array_unique($com_connect_error), \'ul\', \'li\', $com_connect_flags[\'cls_wrapper\']).n;\n            } else {\n                $out .= graf(gTxt(\'com_connect_mail_sorry\'));\n            }\n        }\n    }\n\n    if ($show_input && !$send_article || gps(\'com_connect_send_article\')) {\n        $contactForm = \'<form method=\"post\"\' . ((!$show_error && $com_connect_error) ? \'\' : \' id=\"com\' . $com_connect_form_id . \'\"\') .\n            ($class ? \' class=\"\' . $class . \'\"\' : \'\') .\n            ($browser_validate ? \'\' : \' novalidate\') .\n            \' action=\"\' . txpspecialchars(serverSet(\'REQUEST_URI\')) . \'#com\' . $com_connect_form_id . \'\">\' .\n            ($label ? n . \'<fieldset>\' : \'\') .\n            ($label ? n . \'<legend>\' . txpspecialchars($label) . \'</legend>\' : \'\') .\n            $out .\n            n . \'<input type=\"hidden\" name=\"com_connect_nonce\" value=\"\' . $com_connect_nonce . \'\" />\' .\n            n . \'<input type=\"hidden\" name=\"com_connect_form_id\" value=\"\' . $com_connect_form_id . \'\" />\' .\n            $form .\n            callback_event(\'comconnect.form\') .\n            ($label ? (n . \'</fieldset>\') : \'\') .\n            n . \'</form>\';\n\n        callback_event_ref(\'comconnect.render\', \'\', 0, $contactForm, $atts);\n\n        return $contactForm;\n    }\n\n    return \'\';\n}\n\n/**\n * Tag: Render a text input field.\n *\n * Many different $types are allowed. In fact, the $type attribute is not\n * restricted in any way to allow for any future types. This does bring some\n * developer responsibility if you wish your document to validate because\n * not all attributes will be valid for all input types, despite the plugin\n * faithfully rendering whatever attributes it is given.\n *\n * A lot of invalid situations are recognised (e.g. only permitting max/min/step\n * to occur on \'numeric\' input types) but some are not easily trappable so they\n * are left to user discretion.\n *\n * @param  array  $atts Tag attributes\n * @return string HTML\n */\nfunction com_connect_text($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_flags;\n\n    extract(com_connect_lAtts(array(\n        \'autocomplete\'   => \'\',\n        \'break\'          => br,\n        \'class\'          => \'comText\',\n        \'default\'        => \'\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'inputmode\'      => \'\',\n        \'label\'          => gTxt(\'com_connect_text\'),\n        \'label_position\' => \'before\',\n        \'max\'            => null,\n        \'min\'            => null,\n        \'name\'           => \'\',\n        \'pattern\'        => \'\',\n        \'placeholder\'    => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'size\'           => \'\',\n        \'step\'           => \'\',\n        \'type\'           => \'text\',\n    ), $atts));\n\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    $datetime_types = array(\n        \'date\',\n        \'datetime\',\n        \'datetime-local\',\n        \'month\',\n        \'time\',\n        \'week\',\n    );\n\n    $numeric_types = array(\n        \'number\',\n        \'range\',\n    );\n\n    $is_datetime = (in_array($type, $datetime_types));\n    $is_numeric = (in_array($type, $numeric_types));\n\n    // Dates / times get special treatment: no default min/max if not set by tag.\n    if (!$is_datetime && $min === null) {\n        $min = 0;\n    }\n\n    if (!$is_datetime && $max === null) {\n        $max = 100;\n    }\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    if ($com_connect_submit) {\n        $value = trim(ps($name));\n        $utf8len = preg_match_all(\"/./su\", $value, $utf8ar);\n        $hlabel = txpspecialchars($label);\n        $datetime_ok = true;\n\n        if ($is_datetime) {\n            $minval = $min;\n            $maxval = $max;\n            $cmpval = $value;\n\n            try {\n                $dt = new DateTime($cmpval);\n                $cmpval = $dt->format(\'U\');\n\n                if ($min) {\n                    $dt = new DateTime($min);\n                    $minval = $dt->format(\'U\');\n                }\n\n                if ($max) {\n                    $dt = new DateTime($max);\n                    $maxval = $dt->format(\'U\');\n                }\n            } catch (Exception $e) {\n                $datetime_ok = false;\n            }\n        }\n\n        if (strlen($value)) {\n            if (!$utf8len) {\n                $com_connect_error[] = gTxt(\'com_connect_invalid_utf8\', array(\'{field}\' => $hlabel));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($is_datetime && !$datetime_ok) {\n                $com_connect_error[] = gTxt(\'com_connect_format_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $value));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($min && !$is_numeric && !$is_datetime && $utf8len < $min) {\n                $com_connect_error[] = gTxt(\'com_connect_min_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $min));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($max && !$is_numeric && !$is_datetime && $utf8len > $max) {\n                $com_connect_error[] = gTxt(\'com_connect_max_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $max));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($min && $is_datetime && $cmpval < $minval) {\n                $com_connect_error[] = gTxt(\'com_connect_minval_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $min));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($max && $is_datetime && $cmpval > $maxval) {\n                $com_connect_error[] = gTxt(\'com_connect_maxval_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $max));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($min && $is_numeric && $value < $min) {\n                $com_connect_error[] = gTxt(\'com_connect_minval_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $min));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($max && $is_numeric && $value > $max) {\n                $com_connect_error[] = gTxt(\'com_connect_maxval_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $max));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($pattern and !preg_match(\'/^\'.$pattern.\'$/\', $value)) {\n                $com_connect_error[] = gTxt(\'com_connect_pattern_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $pattern));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } else {\n                com_connect_store($name, $label, $value);\n            }\n        } elseif ($required) {\n            $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => $hlabel));\n            $isError = $com_connect_flags[\'cls_element\'];\n        }\n    } else {\n        $value = $default;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'    => (isset($id) ? $id : $name),\n        \'name\'  => $name,\n        \'type\'  => $type,\n        \'value\' => $value,\n    ));\n\n    if ($size && !$is_numeric) {\n        $attr[\'size\'] = \'size=\"\' . intval($size) . \'\"\';\n    }\n\n    if ($min && !$is_numeric) {\n        $attr[\'minlength\'] = \'minlength=\"\' . intval($min) . \'\"\';\n    }\n\n    if ($max && !$is_numeric) {\n        $attr[\'maxlength\'] = \'maxlength=\"\' . intval($max) . \'\"\';\n    }\n\n    if ($doctype !== \'xhtml\' && ($is_numeric || $is_datetime)) {\n        // Not using intval() because min/max/step can be floating point values.\n        $attr += com_connect_build_atts(array(\n            \'min\'  => $min,\n            \'max\'  => $max,\n            \'step\' => $step,\n        ));\n    }\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'autocomplete\' => $autocomplete,\n            \'form\'         => $html_form,\n            \'inputmode\'    => $inputmode,\n            \'pattern\'      => $pattern,\n            \'placeholder\'  => $placeholder,\n            \'required\'     => $required,\n        ));\n\n        if ($isError) {\n            $attr += com_connect_build_atts(array(\n                \'aria-invalid\' => \'true\',\n            ));\n        }\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $name . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        \'<input\' . $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \' />\' .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Render an email input field.\n *\n * @param  array  $atts Tag attributes\n * @return string HTML\n */\nfunction com_connect_email($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_from, $com_connect_recipient, $com_connect_flags;\n\n    // TODO: \'multiple\' attribute?\n    $defaults = array(\n        \'autocomplete\'   => \'\',\n        \'break\'          => br,\n        \'class\'          => \'comEmail\',\n        \'default\'        => \'\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_email\'),\n        \'label_position\' => \'before\',\n        \'max\'            => 100,\n        \'min\'            => 0,\n        \'name\'           => \'\',\n        \'pattern\'        => \'\',\n        \'placeholder\'    => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'send_article\'   => 0,\n        \'size\'           => \'\',\n        \'type\'           => \'email\',\n    );\n\n    extract(com_connect_lAtts($defaults, $atts));\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    $email = $com_connect_submit ? trim(ps($name)) : $default;\n\n    if ($com_connect_submit && strlen($email)) {\n        if (!is_valid_email($email)) {\n            $com_connect_error[] = gTxt(\'com_connect_invalid_email\', array(\'{email}\' => txpspecialchars($email)));\n            $isError = $com_connect_flags[\'cls_element\'];\n        } else {\n            preg_match(\"/@(.+)$/\", $email, $match);\n            $domain = $match[1];\n\n            if (is_callable(\'checkdnsrr\') && checkdnsrr(\'textpattern.com.\',\'A\') && !checkdnsrr($domain.\'.\',\'MX\') && !checkdnsrr($domain.\'.\',\'A\')) {\n                $com_connect_error[] = gTxt(\'com_connect_invalid_host\', array(\'{host}\' => txpspecialchars($domain)));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } else {\n                if ($send_article) {\n                    $com_connect_recipient = $email;\n                } else {\n                    $com_connect_from = $email;\n                }\n            }\n        }\n    }\n\n    $passed_atts = array();\n\n    foreach ($defaults as $key => $value) {\n        $passed_atts[$key] = $$key;\n    }\n\n    $passed_atts[\'isError\'] = $isError;\n    unset ($passed_atts[\'send_article\']);\n\n    return com_connect_text($passed_atts);\n}\n\n/**\n * Tag: Render a textarea input field.\n *\n * @param  array  $atts Tag attributes\n * @return string HTML\n */\nfunction com_connect_textarea($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_flags;\n\n    extract(com_connect_lAtts(array(\n        \'autocomplete\'   => \'\',\n        \'break\'          => br,\n        \'class\'          => \'comTextarea\',\n        \'cols\'           => 58,\n        \'default\'        => \'\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_message\'),\n        \'label_position\' => \'before\',\n        \'max\'            => 10000,\n        \'min\'            => 0,\n        \'name\'           => \'\',\n        \'placeholder\'    => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'rows\'           => 8,\n        \'wrap\'           => \'\',\n    ), $atts));\n\n    $min = intval($min);\n    $max = intval($max);\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    if ($com_connect_submit) {\n        $value = preg_replace(\'/^\\s*[\\r\\n]/\', \'\', rtrim(ps($name)));\n        $utf8len = preg_match_all(\"/./su\", ltrim($value), $utf8ar);\n        $hlabel = txpspecialchars($label);\n\n        if (strlen(ltrim($value))) {\n            if (!$utf8len) {\n                $com_connect_error[] = gTxt(\'com_connect_invalid_utf8\', array(\'{field}\' => $hlabel));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($min && $utf8len < $min) {\n                $com_connect_error[] = gTxt(\'com_connect_min_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $min));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } elseif ($max && $utf8len > $max) {\n                $com_connect_error[] = gTxt(\'com_connect_max_warning\', array(\'{field}\' => $hlabel, \'{value}\' => $max));\n                $isError = $com_connect_flags[\'cls_element\'];\n            } else {\n                com_connect_store($name, $label, $value);\n            }\n        } elseif ($required) {\n            $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => $hlabel));\n            $isError = $com_connect_flags[\'cls_element\'];\n        }\n    } else {\n        $value = $default;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'        => (isset($id) ? $id : $name),\n        \'name\'      => $name,\n        \'cols\'      => intval($cols),\n        \'rows\'      => intval($rows),\n        \'maxlength\' => $max,\n    ));\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'autocomplete\' => $autocomplete,\n            \'form\'         => $html_form,\n            \'placeholder\'  => $placeholder,\n            \'required\'     => $required,\n        ));\n\n        if ($isError) {\n            $attr += com_connect_build_atts(array(\n                \'aria-invalid\' => \'true\',\n            ));\n        }\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $name . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        \'<textarea\' . $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \'>\' . txpspecialchars($value) . \'</textarea>\' .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Render a select/option input list.\n *\n * @param  array  $atts Tag attributes\n * @return string HTML\n */\nfunction com_connect_select($atts, $thing = null)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_flags;\n\n    // TODO: multiple attribute?\n    extract(com_connect_lAtts(array(\n        \'break\'          => br,\n        \'class\'          => \'comSelect\',\n        \'delimiter\'      => \',\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_option\'),\n        \'label_position\' => \'before\',\n        \'list\'           => \'\', // TODO: remove from here in favour of the global list attribute.\n        \'options\'        => gTxt(\'com_connect_general_inquiry\'),\n        \'name\'           => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'selected\'       => \'\',\n        \'size\'           => \'\',\n    ), $atts));\n\n    // Detect old-school use of the list attribute. Note that deprecated_function_with\n    // isn\'t strictly the correct error to throw, but it\'s close enough until core has\n    // a dedicated deprecated_attribute_with string.\n    if (!empty($list) && strpos($list, $delimiter) !== false) {\n        $options = $list;\n        trigger_error(gTxt(\'deprecated_function_with\', array(\'{name}\' => \'list\', \'{with}\' => \'options\')), E_USER_NOTICE);\n        unset($list, $atts[\'list\']);\n    }\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    $value = ($com_connect_submit) ? trim(ps($name)) : $selected;\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    if ($thing) {\n        com_connect_option(null, $value);\n        $out = parse($thing);\n        $options = com_connect_option(null, null);\n    } else {\n        $options = array_map(\'trim\', explode($delimiter, preg_replace(\'/[\\r\\n\\t\\s]+/\', \' \', $options)));\n        $out = \'\';\n\n        foreach ($options as $item) {\n            $out .= n.t.\'<option\' . ($item == $value ? \' selected\' . (($doctype === \'xhtml\') ? \'=\"selected\"\' : \'\') . \'>\' : \'>\') . (strlen($item) ? txpspecialchars($item) : \' \') . \'</option>\';\n        }\n    }\n\n    if ($com_connect_submit) {\n        if (strlen($value)) {\n            if (in_array($value, $options)) {\n                com_connect_store($name, $label, $value);\n            } else {\n                $com_connect_error[] = gTxt(\'com_connect_invalid_value\', array(\'{field}\' => txpspecialchars($label), \'{value}\' => txpspecialchars($value)));\n                $isError = $com_connect_flags[\'cls_element\'];\n            }\n        } elseif ($required) {\n            $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => txpspecialchars($label)));\n            $isError = $com_connect_flags[\'cls_element\'];\n        }\n    } else {\n        $value = $selected;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'   => (isset($id) ? $id : $name),\n        \'name\' => $name,\n    ));\n\n    if ($size && is_numeric($size)) {\n        $attr[\'size\'] = \'size=\"\' . intval($size) . \'\"\';\n    }\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'form\'     => $html_form,\n            \'required\' => $required,\n        ));\n\n        if ($isError) {\n            $attr += com_connect_build_atts(array(\n                \'aria-invalid\' => \'true\',\n            ));\n        }\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $name . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        n . \'<select\' . $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \'>\' .\n            $out .\n        n . \'</select>\' .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Render a set of select options.\n *\n * @param  array  $atts  Tag attributes\n * @param  string $thing Tag\'s container\n * @return string HTML\n */\nfunction com_connect_option($atts, $thing = null)\n{\n    static $options;\n    static $match;\n\n    if ($atts === null) {\n        if ($thing === null) {\n            return $options;\n        } else {\n            $match = $thing;\n            return;\n        }\n    }\n\n    global $com_connect_error, $com_connect_submit;\n\n    extract(com_connect_lAtts(array(\n        \'class\'    => \'comOption\',\n        \'label\'    => null,\n        \'selected\' => null,\n        \'value\'    => null,\n    ), $atts));\n\n    $val = ($value === null) ? (!empty($thing) ? $thing : (!empty($label) ? $label : null)) : $value;\n    $label = ($label === null) ? (!empty($thing) ? $thing : (!empty($value) ? $value : null)) : $label;\n\n    $attr = array();\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    if ($com_connect_submit) {\n        $options[] = $val;\n\n        if ($val !== null && ((string)$val === (string)$match)) {\n            $attr[] = \'selected\' . (($doctype === \'xhtml\') ? \'=\"selected\"\' : \'\');\n        }\n    } elseif ($selected || ($val !== null && ((string)$val === (string)$match))) {\n        $attr[] = \'selected\' . (($doctype === \'xhtml\') ? \'=\"selected\"\' : \'\');\n    }\n\n    // Core attributes.\n    $attr += com_connect_build_atts(array(\n        \'value\' => $val,\n    ));\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classStr = (($class) ? \' class=\"\' . $class . \'\"\' : \'\');\n\n    return \'<option\' . $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \'>\' . txpspecialchars($label) . \'</option>\';\n}\n\n/**\n * Tag: Render a checkbox.\n *\n * @param  array  $atts  Tag attributes\n * @return string HTML\n * @todo checkbox groups?\n */\nfunction com_connect_checkbox($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_flags;\n\n    extract(com_connect_lAtts(array(\n        \'break\'          => \' \',\n        \'checked\'        => 0,\n        \'class\'          => \'comCheckbox\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_checkbox\'),\n        \'label_position\' => \'after\',\n        \'name\'           => \'\',\n        \'required\'       => $com_connect_flags[\'required\'],\n        \'value\'          => null,\n    ), $atts));\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    if ($com_connect_submit) {\n        $theValue = (bool) ps($name);\n\n        if ($required && !$theValue) {\n            $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => txpspecialchars($label)));\n            $isError = $com_connect_flags[\'cls_element\'];\n        } else {\n            $toStore = (($value !== null && $theValue) ? $value : ($theValue ? gTxt(\'yes\') : gTxt(\'no\')));\n            com_connect_store($name, $label, $toStore);\n        }\n    } else {\n        $theValue = $checked;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'    => (isset($id) ? $id : $name),\n        \'name\'  => $name,\n    ));\n\n    if ($value !== null) {\n        $attr += com_connect_build_atts(array(\n            \'value\' => $value,\n        ));\n    }\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'form\'     => $html_form,\n            \'required\' => $required,\n        ));\n\n        if ($isError) {\n            $attr += com_connect_build_atts(array(\n                \'aria-invalid\' => \'true\',\n            ));\n        }\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $name . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        \'<input type=\"checkbox\"\' . $classStr .\n            ($theValue ? \' checked\' . (($doctype === \'xhtml\') ? \'=\"checked\"\' : \'\') : \'\') . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \' />\' .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Render a radio button.\n *\n * @param  array  $atts  Tag attributes\n * @return string HTML\n */\nfunction com_connect_radio($atts)\n{\n    global $com_connect_error, $com_connect_submit, $com_connect_values, $com_connect_flags, $com_connect_group;\n\n    extract(com_connect_lAtts(array(\n        \'break\'          => \' \',\n        \'checked\'        => 0,\n        \'class\'          => \'comRadio\',\n        \'group\'          => \'\',\n        \'html_form\'      => $com_connect_flags[\'this_form\'],\n        \'isError\'        => \'\',\n        \'label\'          => gTxt(\'com_connect_option\'),\n        \'label_position\' => \'after\',\n        \'name\'           => \'\',\n        \'required\'       => null,\n        \'value\'          => null,\n    ), $atts));\n\n    static $cur_name = array();\n    static $cur_req = array();\n    static $cur_group = null;\n\n    if (!$group) {\n        if ($cur_group === null) {\n            $group = gTxt(\'com_connect_radio\');\n        } else {\n            $group = $cur_group;\n        }\n    } else {\n        if (!$name) {\n            $name = $group;\n        }\n    }\n\n    if (isset($cur_name[$group])) {\n        if (!$name) {\n            $name = $cur_name[$group];\n        }\n    } else {\n        if ($name) {\n            $cur_name[$group] = $name;\n        } else {\n            $name = $cur_name[$group] = gTxt(\'com_connect_radio\');\n        }\n    }\n\n    if (isset($cur_req[$group])) {\n        if ($required === null) {\n            $required = $cur_req[$group];\n        }\n    } else {\n        if ($required === null) {\n            $required = $cur_req[$group] = $com_connect_flags[\'required\'];\n        } else {\n            $cur_req[$group] = $required;\n        }\n    }\n\n    $cur_group = $group;\n\n    $id = \'q\' . md5($name . \'=>\' . $label);\n    $name = com_connect_label2name($name);\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n    $com_connect_group[$name][$id][\'req\'] = $required;\n    $com_connect_group[$name][$id][\'label\'] = $group;\n\n    if ($com_connect_submit) {\n        $toCompare = ($value === null ? $id : $value);\n        $is_checked = (ps($name) == $toCompare);\n        $com_connect_group[$name][$id][\'isSet\'] = $is_checked;\n\n        if ($is_checked || $checked && !isset($com_connect_values[$name])) {\n            com_connect_store($name, $group, ($value !== null ? $value : $label));\n        }\n    } else {\n        $is_checked = $checked;\n    }\n\n    // Core attributes.\n    $attr = com_connect_build_atts(array(\n        \'id\'    => $id,\n        \'name\'  => $name,\n        \'value\' => ($value !== null ? $value : $id),\n    ));\n\n    // HTML5 attributes.\n    $required = ($required) ? \'required\' : \'\';\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'form\'     => $html_form,\n            \'required\' => $required,\n        ));\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classes = array();\n\n    foreach (array($class, ($required ? $com_connect_flags[\'cls_required\'] : \'\'), $isError) as $cls) {\n        if ($cls) {\n            $classes[] = $cls;\n        }\n    }\n\n    $classStr = ($classes ? \' class=\"\' . implode(\' \', $classes) . \'\"\' : \'\');\n    $labelStr = \'<label for=\"\' . $id . \'\"\' . $classStr . \'>\' . txpspecialchars($label) . \'</label>\';\n\n    return ($label_position === \'before\' ? $labelStr . $break : \'\') .\n        \'<input type=\"radio\"\'. $classStr . ($attr ? \' \' . implode(\' \', $attr) : \'\') .\n            ( $is_checked ? \' checked\' . (($doctype === \'xhtml\') ? \'=\"checked\"\' : \'\'). \' />\' : \' />\') .\n        ($label_position === \'after\' ? $break . $labelStr : \'\');\n}\n\n/**\n * Tag: Store server information in the payload.\n *\n * @param  array  $atts  Tag attributes\n */\nfunction com_connect_serverinfo($atts)\n{\n    global $com_connect_submit;\n\n    extract(com_connect_lAtts(array(\n        \'label\' => \'\',\n        \'name\'  => \'\',\n    ), $atts));\n\n    if (empty($name)) {\n        $name = com_connect_label2name($label);\n    }\n\n    if (strlen($name) && $com_connect_submit) {\n        if (!$label) {\n            $label = $name;\n        }\n\n        com_connect_store($name, $label, serverSet($name));\n    }\n}\n\n/**\n * Tag: Store a secret value in the payload.\n *\n * @param  array  $atts  Tag attributes\n */\nfunction com_connect_secret($atts, $thing = null)\n{\n    global $com_connect_submit;\n\n    extract(com_connect_lAtts(array(\n        \'label\' => gTxt(\'com_connect_secret\'),\n        \'name\'  => \'\',\n        \'value\' => \'\',\n    ), $atts));\n\n    $name = com_connect_label2name($name ? $name : $label);\n\n    if ($com_connect_submit) {\n        if ($thing) {\n            $value = trim(parse($thing));\n        }\n\n        com_connect_store($name, $label, $value);\n    }\n\n    return \'\';\n}\n\n/**\n * Tag: Render a submit button.\n *\n * If the container is used, a button tag is rendered, otherwise a\n * standard input type=submit button is rendered.\n *\n * @param  array  $atts  Tag attributes\n * @param  string $thing Tag\'s container\n * @return string HTML\n */\nfunction com_connect_submit($atts, $thing = null)\n{\n    global $com_connect_flags;\n\n    extract(com_connect_lAtts(array(\n        \'class\'     => \'comSubmit\',\n        \'html_form\' => $com_connect_flags[\'this_form\'],\n        \'label\'     => gTxt(\'com_connect_send\'),\n    ), $atts));\n\n    $label = txpspecialchars($label);\n    $doctype = get_pref(\'doctype\', \'xhtml\');\n\n    $attr = array();\n\n    // HTML5 attributes.\n    if ($doctype !== \'xhtml\') {\n        $attr += com_connect_build_atts(array(\n            \'form\' => $html_form,\n        ));\n    }\n\n    // Global attributes.\n    $attr += com_connect_build_atts($com_connect_globals, $atts);\n\n    $classStr = ($class ? \' class=\"\' . $class . \'\"\' : \'\');\n\n    if (strlen($thing)) {\n        return \'<button type=\"submit\"\' . $classStr . \' name=\"com_connect_submit\" value=\"\' . $label . \'\"\' . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \'>\' . ($thing ? trim(parse($thing)) : $label) . \'</button>\';\n    } else {\n        return \'<input type=\"submit\"\' . $classStr . \' name=\"com_connect_submit\" value=\"\' . $label . \'\"\' . ($attr ? \' \' . implode(\' \', $attr) : \'\') . \' />\';\n    }\n}\n\n/**\n * Tag: Render a link to allow someone to forward a page to a friend.\n *\n * @param  array  $atts  Tag attributes\n * @return string HTML\n */\nfunction com_connect_send_article($atts)\n{\n    if (!isset($_REQUEST[\'com_connect_send_article\'])) {\n        $linktext = (empty($atts[\'linktext\'])) ? gTxt(\'com_connect_send_article\') : $atts[\'linktext\'];\n        $join = (empty($_SERVER[\'QUERY_STRING\'])) ? \'?\' : \'&\';\n        $href = $_SERVER[\'REQUEST_URI\'] . $join . \'com_connect_send_article=yes\';\n\n        return \'<a href=\"\' . txpspecialchars($href) . \'\" rel=\"nofollow\">\' . txpspecialchars($linktext) . \'</a>\';\n    }\n\n    return;\n}\n\n/**\n * Perform post-processing for aggregate (group) controls like radio sets.\n *\n * @todo Can this be done any neater?\n * @todo Should this be exposed as a callback to allow plugins to extend the functionality?\n */\nfunction com_connect_group_validate()\n{\n    global $com_connect_group, $com_connect_error;\n\n    $flags = array();\n\n    if ($com_connect_group) {\n        foreach ($com_connect_group as $key => $grp) {\n            foreach ($grp as $id => $atts) {\n                $flags[$key][\'label\'] = $atts[\'label\'];\n\n                if (!empty($atts[\'req\']) && !isset($flags[$key][\'req\'])) {\n                    $flags[$key][\'req\'] = 1;\n                }\n\n                if (!empty($atts[\'isSet\']) && !isset($flags[$key][\'isSet\'])) {\n                    $flags[$key][\'isSet\'] = 1;\n                }\n            }\n        }\n\n        foreach ($flags as $key => $data) {\n            if (!empty($data[\'req\']) && !isset($data[\'isSet\'])) {\n                $com_connect_error[] = gTxt(\'com_connect_field_missing\', array(\'{field}\' => txpspecialchars($data[\'label\'])));\n            }\n        }\n    }\n}\n\n/**\n * Process tag attributes and set defaults.\n *\n * Handles HTML \'global\' attributes too, though what we consider global differs slightly\n * from the W3C: any attributes that apply to most of the <input> tags is considered\n * global. Just saves repetitive code in each tag handler.\n *\n * Note that \'class\' is omitted, because that has different default values per input\n * widget, so it is dealt with explicitly in each tag handler.\n *\n * @param  array $arr  Acceptable tag attributes\n * @param  array $atts User-submitted attribute values\n * @return array\n */\nfunction com_connect_lAtts($pairs, $atts)\n{\n    lAtts(array(\'label\' => \'\'), $atts, false);\n\n    foreach (array(\'button\', \'copysender\', \'checked\', \'required\', \'send_article\', \'show_input\', \'show_error\') as $key) {\n        if (isset($atts[$key])) {\n            $atts[$key] = ($atts[$key] === \'yes\' || intval($atts[$key])) ? 1 : 0;\n        }\n    }\n\n    if (isset($atts[\'break\']) && $atts[\'break\'] === \'br\') {\n        $atts[\'break\'] = \'<br />\';\n    }\n\n    $com_connect_globals = array(\n        \'accesskey\'  => \'\',\n        \'autofocus\'  => \'\',\n        \'dir\'        => \'\',\n        \'disabled\'   => \'\',\n        \'hidden\'     => \'\',\n        \'id\'         => \'\',\n        \'lang\'       => \'\',\n        \'list\'       => \'\',\n        \'readonly\'   => \'\',\n        \'spellcheck\' => \'\',\n        \'style\'      => \'\',\n        \'tabindex\'   => \'\',\n        \'title\'      => \'\',\n        \'translate\'  => \'\',\n    );\n\n    foreach ($atts as $name => $value) {\n        if (array_key_exists($name, $pairs) || substr($name, 0, 2) === \'on\' || array_key_exists($name, $com_connect_globals)) {\n            $pairs[$name] = $value;\n        } elseif (get_pref(\'production_status\') != \'live\') {\n            trigger_error(gTxt(\'unknown_attribute\', array(\'{att}\' => $name)));\n        }\n    }\n\n    $pairs[\'com_connect_globals\'] = $com_connect_globals;\n\n    return ($pairs) ? $pairs : false;\n}\n\n/**\n * Build a sanitized, unique attribute array from the given pairs.\n *\n * If called multiple times, earlier values supersede later values\n * if the keys clash.\n *\n * @param  array $pairs    Attribute tuples to compile\n * @param  array $defaults Attribute values to use if the $pairs tuple value is empty\n * @return array\n */\nfunction com_connect_build_atts($pairs, $defaults = array())\n{\n    $attr = array();\n\n    $booleans = array(\n        \'autofocus\',\n        \'checked\',\n        \'disabled\',\n        \'hidden\',\n        \'multiple\',\n        \'readonly\',\n        \'required\',\n        \'selected\',\n        );\n\n    foreach ($pairs as $key => $value) {\n        if ($value !== \'\' && $value !== null) {\n            $attr[$key] = $key . (in_array($key, $booleans) ? \'\' : \'=\"\' . txpspecialchars($value) . \'\"\');\n        } else {\n            if (isset($defaults[$key])) {\n                $attr[$key] = $key . (in_array($key, $booleans) ? \'\' : \'=\"\' . txpspecialchars($defaults[$key]) . \'\"\');\n            }\n        }\n    }\n\n    return $attr;\n}\n\n/**\n * Replace nulls and newlines in content with spaces.\n *\n * @param string $str    String to sanitize\n * @param bool   $header Whether the string is a header or not\n */\nfunction com_connect_strip($str, $header = true)\n{\n    if ($header) {\n        // TODO: strip_rn will be deprecated in 4.6.0.\n        $str = strip_rn($str);\n    }\n\n    return preg_replace(\'/[\\x00]/\', \' \', $str);\n}\n\n/**\n * Handle content delivery of payload.\n *\n * Triggers a \'comconnect.deliver\' callback event to override or augment the\n * delivery mechanism. Third party plugins can make alterations to the $payload,\n * then return one of the strings:\n *  -> \"comconnect.send\" (or no return value) to allow the plugin to continue mailing after the 3rd party plugin completes\n *  -> \"comconnect.skip\" to skip com_connect\'s mailing (i.e. the 3rd party handles mailing) and return \'success\'\n *  -> \"comconnect.fail\" to skip com_connect\'s mailing and return \'fail\'\n *\n * By hooking into the callback\'s step you can target either the main \'send\'\n * process or the \'copysender\' process. Examples of things you could do:\n * -> Add Multi-part MIME headers for HTML emails.\n * -> Add CC: or BCC: fields.\n * -> Subscribe people to mailing lists.\n * -> Handle the mailing process independently of Textpattern.\n *\n * @param string $to      Delivery address\n * @param string $subject Subject of message\n * @param string $body    Message content\n * @param array  $headers Message headers as tuples\n * @param array  $fields  Message field names and content as tuples\n * @param array  $flags   Signals to govern delivery / callback behaviour\n */\nfunction com_connect_deliver($to, $subject, $body, $headers, $fields, $flags)\n{\n    $payload = array(\n        \'to\'      => $to,\n        \'subject\' => $subject,\n        \'headers\' => $headers,\n        \'body\'    => $body,\n        \'fields\'  => $fields,\n    );\n\n    $flavour = ($flags[\'isCopy\'] === true) ? \'copysender\' : \'send\';\n\n    // Allow plugins to override or alter default action (mail) if required.\n    // TODO: use has_handler() from 4.6.0+.\n    $ret = callback_event_ref(\'comconnect.deliver\', $flavour, 0, $payload);\n\n    if (in_array(\'comconnect.fail\', $ret)) {\n        return false;\n    } elseif (in_array(\'comconnect.skip\', $ret)) {\n        return true;\n    }\n\n    extract($payload);\n\n    $smtp_from = get_pref(\'smtp_from\');\n\n    if (!is_callable(\'mail\')) {\n        return (get_pref(\'production_status\') === \'live\')\n            ? gTxt(\'com_connect_mail_sorry\')\n            : gTxt(\'warn_mail_unavailable\');\n    }\n\n    $sep = (!empty($headers[\'separator\'])) ? $headers[\'separator\'] : (IS_WIN ? \"\\r\\n\" : \"\\n\");\n    $xfer_encoding = (!empty($headers[\'xfer_encoding\'])) ? $headers[\'xfer_encoding\'] : \'8bit\';\n    $content_type = (!empty($headers[\'content_type\'])) ? $headers[\'content_type\'] : \'text/plain\';\n    $reply = (!empty($headers[\'reply\'])) ? $headers[\'reply\'] : \'\';\n    $charset = (!empty($headers[\'charset\'])) ? $headers[\'charset\'] : \'UTF-8\';\n    $x_mailer = (!empty($headers[\'x_mailer\'])) ? $headers[\'x_mailer\'] : \'Textpattern (com_connect)\';\n\n    $header_string = \'From: \' . $headers[\'from\'] .\n        ($reply ? ($sep . \'Reply-To: \' . $reply) : \'\') .\n        $sep . \'X-Mailer: \' . $x_mailer .\n        $sep . \'X-Originating-IP: \' . com_connect_strip((!empty($_SERVER[\'HTTP_X_FORWARDED_FOR\']) ? $_SERVER[\'HTTP_X_FORWARDED_FOR\'] . \' via \' : \'\') . $_SERVER[\'REMOTE_ADDR\']) .\n        $sep . \'Content-Transfer-Encoding: \' . $xfer_encoding .\n        $sep . \'Content-Type: \' . $content_type . \'; charset=\"\' . $charset . \'\"\';\n\n    // Remove all the header entries that have already been handled.\n    unset(\n        $headers[\'separator\'],\n        $headers[\'xfer_encoding\'],\n        $headers[\'content_type\'],\n        $headers[\'reply\'],\n        $headers[\'charset\'],\n        $headers[\'x_mailer\'],\n        $headers[\'from\']\n    );\n\n    // Any remaining headers set by plugins are appended as-is.\n    foreach ($headers as $name => $value) {\n        $header_string .= $sep . $name . \': \' . $value;\n    }\n\n    if (is_valid_email($smtp_from)) {\n        if (IS_WIN) {\n            ini_set(\'sendmail_from\', $smtp_from);\n        } elseif (!ini_get(\'safe_mode\')) {\n            return mail($to, $subject, $body, $header_string, \'-f\'.$smtp_from);\n        }\n    }\n\n    return mail($to, $subject, $body, $header_string);\n}\n\n/**\n * Evaluate return values from plugins.\n */\nclass comconnect_evaluation\n{\n    private $status;\n\n    /**\n     * Constructor.\n     */\n    function __construct()\n    {\n        $this->status = 0;\n    }\n\n    /**\n     * Append the given status to the counter.\n     */\n    function add_comconnect_status($check)\n    {\n        $this->status += $check;\n    }\n\n    /**\n     * Fetch the current evaluator status.\n     */\n    function get_comconnect_status()\n    {\n        return $this->status;\n    }\n}\n\n/**\n * Evaluator singleton for checking return values from plugins.\n */\nfunction &get_comconnect_evaluator()\n{\n    static $instance;\n\n    if (!isset($instance)) {\n        $instance = new comconnect_evaluation();\n    }\n\n    return $instance;\n}\n\n/**\n * Convert the given label to a suitably unique name.\n *\n * @param string $label Label to convert to name.\n */\nfunction com_connect_label2name($label)\n{\n    $label = trim($label);\n\n    if (strlen($label) == 0) {\n        return \'invalid\';\n    }\n\n    if (strlen($label) <= 32 && preg_match(\'/^[a-zA-Z][A-Za-z0-9:_-]*$/\', $label)) {\n        return $label;\n    } else {\n        return \'q\' . md5($label);\n    }\n}\n\n/**\n * Store the given names/values in the global arrays.\n *\n * @param  string $name  Parameter name\n * @param  string $label Parameter label\n * @param  string $value Parameter value\n */\nfunction com_connect_store($name, $label, $value)\n{\n    global $com_connect_form, $com_connect_labels, $com_connect_values;\n\n    $com_connect_form[$label] = $value;\n    $com_connect_labels[$name] = $label;\n    $com_connect_values[$name] = $value;\n}\n\n/**\n * Override the language strings if necessary.\n *\n * @param  string $lang Language designator (e.g. fr-fr)\n * @return array        Partial language array to merge with $textarray\n */\nfunction com_connect_load_lang($lang = LANG)\n{\n    $out = array();\n\n    if ($lang != LANG) {\n\n        $rs = safe_rows(\"name, data\", \'txp_lang\', \"lang = \'\" . doSlash($lang) . \"\' AND name like \'com\\_connect\\_%\'\");\n\n        if (!empty($rs)) {\n            foreach ($rs as $a) {\n                $out[$a[\'name\']] = $a[\'data\'];\n            }\n        }\n    }\n\n    return $out;\n}\n\n/**\n * Return the value of the given attribute, by name or its label.\n *\n * @param  array $atts Attribute to return\n * @return string\n */\nfunction com_connect_value($atts)\n{\n    global $com_connect_values, $com_connect_form;\n\n    extract(lAtts(array(\n        \'label\' => \'\',\n        \'name\'  => \'\',\n    ), $atts));\n\n    $str = \'\';\n\n    if ($name) {\n        $str = isset($com_connect_values[$name]) ? $com_connect_values[$name] : \'\';\n    } elseif ($label) {\n        $str = isset($com_connect_form[$name]) ? $com_connect_form[$name] : \'\';\n    }\n\n    return trim($str);\n}\n\n/**\n * Return the label for the given attribute.\n *\n * @param  array $atts Attribute name to return\n * @return string\n */\nfunction com_connect_label($atts)\n{\n    global $com_connect_labels;\n\n    extract(lAtts(array(\n        \'name\' => \'\',\n    ), $atts));\n\n    if ($name) {\n        return isset($com_connect_labels[$name]) ? $com_connect_labels[$name] : \'\';\n    }\n\n    return \'\';\n}\n\n/**\n * Conditional tag for checking variable conditions.\n *\n * @param  array  $atts  Attributes to check\n * @param  string $thing Tag\'s container content\n * @return string\n */\nfunction com_connect_if($atts, $thing = null)\n{\n    extract(lAtts(array(\n        \'label\' => \'\',\n        \'name\'  => \'\',\n        \'value\' => null,\n    ), $atts));\n\n    $val = com_connect_value(array(\n        \'label\' => $label,\n        \'name\'  => $name,\n    ));\n\n    if ($val) {\n        $cond = ($value === null || $val == $value);\n    } else {\n        $cond = false;\n    }\n\n    return parse(EvalElse($thing, $cond));\n}', '13ce587331a18f42753f81bb56a80f85', '#@public\ncom_connect_checkbox => Checkbox\ncom_connect_contact => Contact\ncom_connect_email => Email\ncom_connect_email_subject => {site} > Enquiry\ncom_connect_email_thanks => Thank you, your message has been sent.\ncom_connect_field_missing => Required field <strong>{field}</strong> is missing.\ncom_connect_format_warning => Value {value} in <strong>{field}</strong> is not of the expected format.\ncom_connect_form_expired => The form has expired, please try again.\ncom_connect_form_used => The form was already submitted, please fill out a new form.\ncom_connect_general_inquiry => General enquiry\ncom_connect_invalid_email => <strong>{email}</strong> is not a valid email address.\ncom_connect_invalid_host => <strong>{host}</strong> is not a valid email host.\ncom_connect_invalid_utf8 => <strong>{field}</strong> contains invalid UTF-8 characters.\ncom_connect_invalid_value => Invalid value for <strong>{field}</strong>: <strong>{value}</strong> is not one of the available options.\ncom_connect_mail_sorry => Sorry, unable to send email.\ncom_connect_maxval_warning => <strong>{field}</strong> must not exceed {value}.\ncom_connect_max_warning => <strong>{field}</strong> must not contain more than {value} characters.\ncom_connect_message => Message\ncom_connect_minval_warning => <strong>{field}</strong> must be at least {value}.\ncom_connect_min_warning => <strong>{field}</strong> must contain at least {value} characters.\ncom_connect_name => Name\ncom_connect_option => Option\ncom_connect_pattern_warning => <strong>{field}</strong> does not match the pattern {value}.\ncom_connect_radio => Radio\ncom_connect_recipient => Recipient\ncom_connect_refresh => Follow this link if the page does not refresh automatically.\ncom_connect_secret => Secret\ncom_connect_send => Send\ncom_connect_send_article => Send article\ncom_connect_spam => We do not accept spam, thank you!\ncom_connect_text => Text\ncom_connect_to_missing => <strong>To</strong> email address is missing.\n#@public\n#@language es-es\ncom_connect_checkbox => Casilla de verificación\ncom_connect_contact => Contacto\ncom_connect_email => Correo electrónico\ncom_connect_email_subject => {site} > Consulta\ncom_connect_email_thanks => Gracias, tu mensaje ha sido enviado.\ncom_connect_field_missing => falta el campo obligatorio <strong>{field}</strong>.\ncom_connect_format_warning => El valor {value} en <strong>{field}</strong> no está en el formato esperado.\ncom_connect_form_expired => El formulario ha caducado, por favor inténtalo de nuevo.\ncom_connect_form_used => El formulario ya había sido enviado, por favor rellena el formulario de nuevo.\ncom_connect_general_inquiry => Consulta general\ncom_connect_invalid_email => La dirección de correo electrónico <strong>{email}</strong> no es válida.\ncom_connect_invalid_host => El dominio de correo electrónico <strong>{host}</strong> no es válido.\ncom_connect_invalid_utf8 => <strong>{field}</strong> contiene caracteres UTF-8 no válidos.\ncom_connect_invalid_value => Valor incorrecto para <strong>{field}</strong>: <strong>{value}</strong> no es una de las opciones disponibles.\ncom_connect_mail_sorry => Lo siento, el correo electrónico no pudo ser enviado.\ncom_connect_maxval_warning => <strong>{field}</strong> no debe exceder {value}.\ncom_connect_max_warning => <strong>{field}</strong> no debe contener más de {value} caracteres.\ncom_connect_message => Mensaje\ncom_connect_minval_warning => <strong>{field}</strong> debe tener al menos {value}.\ncom_connect_min_warning => <strong>{field}</strong> debe contener al menos {value} caracteres.\ncom_connect_name => Nombre\ncom_connect_option => Opción\ncom_connect_pattern_warning => <strong>{field}</strong> no encaja con el patrón {value}.\ncom_connect_radio => Botón de opción\ncom_connect_recipient => Destinatario\ncom_connect_refresh => Siga este enlace si la página no se recarga automáticamente.\ncom_connect_secret => Secreto\ncom_connect_send => Enviar\ncom_connect_send_article => Enviar artículo\ncom_connect_spam => Gracias, ¡pero no aceptamos correo basura!\ncom_connect_text => Texto\ncom_connect_to_missing => Falta la dirección de correo electrónico del <strong>destinatario</strong>.\n#@public\n#@language fr-fr\ncom_connect_checkbox => Case à cocher\ncom_connect_contact => Contact\ncom_connect_email => Email\ncom_connect_email_subject => {site} > Demande\ncom_connect_email_thanks => Merci, votre message a bien été envoyé.\ncom_connect_field_missing => Champ obligatoire <strong>{field}</strong> manquant.\ncom_connect_form_expired => Le délai du formulaire vient d’expirer. Veuillez recommencer.\ncom_connect_form_used => Le formulaire a déjà été soumis. Veuillez en remplir un nouveau.\ncom_connect_general_inquiry => Demande d’ordre général\ncom_connect_invalid_email => <strong>{email}</strong> n’est pas une adresse email valide.\ncom_connect_invalid_host => <strong>{host}</strong> n’est pas correctement rédigé.\ncom_connect_invalid_utf8 => <strong>{field}</strong> contient des caractères invalides.\ncom_connect_invalid_value => Cette valeur : <strong>{value}</strong> n’est pas correcte pour <strong>{field}</strong>.\ncom_connect_mail_sorry => Désolé, impossible d’envoyer votre message dans l’immédiat.\ncom_connect_maxval_warning => <strong>{field}</strong> ne peux pas être plus grand que {value}.\ncom_connect_max_warning => <strong>{field}</strong> dépasse {value} caractères.\ncom_connect_message => Message\ncom_connect_minval_warning => <strong>{field}</strong> doit être au moins {value}.\ncom_connect_min_warning => <strong>{field}</strong> doit contenir au moins {value} caractères.\ncom_connect_name => Nom\ncom_connect_option => Option\ncom_connect_pattern_warning =><strong>{field}</strong> doit correspondre à ce modèle {value}.\ncom_connect_radio => Bouton radio\ncom_connect_recipient => Destinataire\ncom_connect_refresh => Cliquez sur ce lien si la page ne se rafraîchissait pas automatiquement.\ncom_connect_secret => Secret\ncom_connect_send => Envoyer\ncom_connect_send_article => Envoyer l’article\ncom_connect_spam => Nous refusons catégoriquement les spam. Bien à vous!\ncom_connect_text => Texte\ncom_connect_to_missing => l’adresse mail <strong>To</strong> est manquante.\n#@public\n#@language nl-nl\ncom_connect_checkbox => Keuze\ncom_connect_contact => Contact\ncom_connect_email => E-mail adres\ncom_connect_email_subject => {site} > bericht via de site\ncom_connect_email_thanks => Hartelijk dank, je bericht is verzonden.\ncom_connect_field_missing => Je hebt bij <strong>{field}</strong> nog niets ingevuld.\ncom_connect_format_warning => De invoer {value} in <strong>{field}</strong> heeft niet de juiste vorm.\ncom_connect_form_expired => Het formulier is verlopen, probeer het opnieuw.\ncom_connect_form_used => Het formulier is reeds verstuurd, vul het opnieuw in.\ncom_connect_general_inquiry => Bericht van de site\ncom_connect_invalid_email => <strong>{email}</strong> is geen geldig e-mail addres.\ncom_connect_invalid_host => <strong>{host}</strong> is geen geldige e-mail host.\ncom_connect_invalid_utf8 => <strong>{field}</strong> bevat ongeldige lettertekens.\ncom_connect_invalid_value => Ongeldige invoer bij <strong>{field}</strong>, <strong>{value}</strong> is niet mogelijk.\ncom_connect_mail_sorry => Sorry, er kan geen e-mail verzonden worden.\ncom_connect_maxval_warning => <strong>{field}</strong> mag niet groter zijn dan {value}.\ncom_connect_max_warning => <strong>{field}</strong> mag niet meer dan {value} lettertekes bevatten.\ncom_connect_message => Berichttekst\ncom_connect_minval_warning => <strong>{field}</strong> moet minstens {value} zijn.\ncom_connect_min_warning => <strong>{field}</strong> moet minstens {value} lettertekens bevatten.\ncom_connect_name => Naam\ncom_connect_option => Optie\ncom_connect_pattern_warning => <strong>{field}</strong> komt niet overeen met de volgorde {value}.\ncom_connect_radio => Keuzeknop\ncom_connect_recipient => Ontvanger\ncom_connect_refresh => Je kunt deze link gebruiken als de pagina niet automatisch ververst.\ncom_connect_secret => Geheim\ncom_connect_send => Verzenden\ncom_connect_send_article => Artikel verzenden\ncom_connect_spam => We accepteren geen spam!\ncom_connect_text => Tekst\ncom_connect_to_missing => <strong>Aan</strong> e-mailadres ontbreekt.\n#@public\n#@language pt-br\ncom_connect_checkbox => Checkbox\ncom_connect_contact => Contato\ncom_connect_email => Email\ncom_connect_email_subject => {site} > Contato\ncom_connect_email_thanks => Obrigado, sua mensagem foi enviada.\ncom_connect_field_missing => Faltou preencher o campo requerido <strong>{field}</strong>.\ncom_connect_format_warning => O valor {value} em <strong>{field}</strong> não está formato esperado.\ncom_connect_form_expired => O formulário expirou, por favor tente novamente.\ncom_connect_form_used => O formulário já foi enviado, por favor preencha o formulário novamente.\ncom_connect_general_inquiry => Assuntos gerais\ncom_connect_invalid_email => <strong>{email}</strong> não é um endereço de email válido.\ncom_connect_invalid_host => <strong>{host}</strong> não é um domínio de email válido.\ncom_connect_invalid_utf8 => <strong>{field}</strong> contém caracteres UTF-8 inválidos.\ncom_connect_invalid_value => Valor incorreto para <strong>{field}</strong>, <strong>{value}</strong> não é uma das opções disponíveis.\ncom_connect_mail_sorry => Desculpe, não foi possível enviar o email.\ncom_connect_maxval_warning => <strong>{field}</strong> não pode exceder {value}.\ncom_connect_max_warning => <strong>{field}</strong> não pode conter mais que {value} caracteres.\ncom_connect_message => Mensagem\ncom_connect_minval_warning => <strong>{field}</strong> deve ter ao menos {value}.\ncom_connect_min_warning => <strong>{field}</strong> deve conter ao menos {value} caracteres.\ncom_connect_name => Nome\ncom_connect_option => Opção\ncom_connect_pattern_warning => <strong>{field}</strong> não se encaixa no formato {value}.\ncom_connect_radio => Rádio\ncom_connect_recipient => Destinatário\ncom_connect_refresh => Clique neste link caso a página não se atualize automaticamente.\ncom_connect_secret => Secreto\ncom_connect_send => Enviar\ncom_connect_send_article => Enviar artigo\ncom_connect_spam => Não aceitamos spam, obrigado!\ncom_connect_text => Texto\ncom_connect_to_missing => <strong>To</strong> falta o endereço de email.\n#@public\n#@language de-de\ncom_connect_checkbox => Checkbox\ncom_connect_contact => Kontakt\ncom_connect_email => E-Mail\ncom_connect_email_subject => {site} > Anfrage\ncom_connect_email_thanks => Vielen Dank, Ihre Nachricht wurde gesendet.\ncom_connect_field_missing => Erforderliche Eingabe im Feld <strong>{field}</strong> fehlt.\ncom_connect_format_warning => Eingabe {value} im Feld <strong>{field}</strong> entspricht nicht dem erwarteten Format.\ncom_connect_form_expired => Dieses Formular ist abgelaufen, bitte versuchen Sie es erneut.\ncom_connect_form_used => Dieses Formular wurde bereits gesendet. Bitte laden Sie das Formular noch einmal.\ncom_connect_general_inquiry => Allgemeine Anfrage\ncom_connect_invalid_email => <strong>{email}</strong> ist keine gültige E-Mailadresse.\ncom_connect_invalid_host => <strong>{host}</strong> ist kein gültiger E-Mail-Server.\ncom_connect_invalid_utf8 => <strong>{field}</strong> enthält ungültige UTF-8-Zeichen.\ncom_connect_invalid_value => Ungültiger Wert für <strong>{field}</strong>, <strong>{value}</strong> ist keine verfügbare Option.\ncom_connect_mail_sorry => Leider kann keine E-Mail gesendet werden.\ncom_connect_maxval_warning => <strong>{field}</strong> darf {value} nicht überschreiten.\ncom_connect_max_warning => <strong>{field}</strong> darf nicht länger als {value} Zeichen sein.\ncom_connect_message => Nachricht\ncom_connect_minval_warning => <strong>{field}</strong> darf {value} nicht unterschreiten.\ncom_connect_min_warning => <strong>{field}</strong> darf nicht kürzer als {value} Zeichen sein.\ncom_connect_name => Name\ncom_connect_option => Option\ncom_connect_pattern_warning => <strong>{field}</strong> entspricht nicht dem Muster {value}.\ncom_connect_radio => Radio\ncom_connect_recipient => Empfänger\ncom_connect_refresh => Bitte folgen Sie diesem Link, falls die Seite icht automatisch neu geladen wird.\ncom_connect_secret => Geheimnis\ncom_connect_send => Senden\ncom_connect_send_article => Artikel senden\ncom_connect_spam => Danke, wir brauchen keinen Spam!\ncom_connect_text => Text\ncom_connect_to_missing => <strong>To</strong> E-Mail-Adresse fehlt.\n', '', 0, 5, 0);
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `textpack`, `data`, `type`, `load_order`, `flags`) VALUES
('wet_haystack', 0, 'Robert Wetzlmayr', 'http://awasteofwords.com/software/wet_haystack-textpattern-plugin', '0.6', 'Custom full text index builder', '<h3>Purpose</h3>\n\n<p>Textpattern&#8217;s full text index uses the articles’ body and title contents to find proper matches for site-internal searches.</p>\n\n<p><strong>wet_haystack</strong> allows site publishers to modify this default behaviour by adding additional article fields to the set of indexed content.</p>\n\n<h3>Usage</h3>\n\n<p>NB: This plugin is intended for Textpattern 4.2.0+. It will not work with earlier versions.  You need to acquire publisher privileges to change a site&#8217;s fulltext search settings with this plugin.</p>\n\n<h4>Building a custom fulltext index</h4>\n\n<ul>\n	<li>Go to the <a href=\"https://wave.gud.one/?event=plugin_prefs.wet_haystack\" rel=\"nofollow\">wet_haystack options</a> panel.</li>\n	<li>Check all article fields you want to include in the full text index.</li>\n	<li>Hit “Save” and wait for the database to reindex you current site&#8217;s content. Depending on the number and length of your articles, this may take a while.</li>\n</ul>\n\n<p><strong>wet_haystack</strong> needs not be kept installed once the custom fulltext index has been created.</p>\n\n<h4>Removing an obsolete custom fulltext index</h4>\n\n<p><strong>wet_haystack</strong> needs one additional database index for its operation, which it continues to use as long as you modify Textpattern&#8217;s default fulltext index settings. This custom fulltext index will be deleted when the customised setting matches Textpattern&#8217;s default setting (i.e. “Title” and “Body” are the solely checked properties).</p>\n\n<h3>Additional information</h3>\n\n<p>Visit the <a href=\"http://awasteofwords.com/software/wet_haystack-textpattern-plugin\" rel=\"nofollow\">wet_haystack documentation page</a>.</p>\n\n<h3>Licence</h3>\n\n<p>This plugin is released under the <a href=\"http://www.gnu.org/licenses/gpl.txt\" rel=\"nofollow\">Gnu General Public Licence</a>.</p>', '\nglobal $textarray;\n\n/**\n *  User-editable strings\n */\n$textarray[\'wet_haystack\'] = \'Fulltext Index\';\n$textarray[\'wet_haystack_select_columns\'] = \'Select searchable columns\';\n$textarray[\'wet_haystack_please_wait\'] = \'Please wait…\';\n\n// == STOP EDITING! ==\n\n// Hook into the admin side of highly privileged users\nadd_privs(\'wet_haystack\', \'1\');\nadd_privs(\'plugin_prefs.wet_haystack\', \'1\');\nregister_callback(\'wet_haystack_ui\', \'plugin_prefs.wet_haystack\');\nregister_callback(\'wet_haystack_style\', \'admin_side\', \'head_end\');\nregister_callback(\'wet_haystack_bhvr\', \'admin_side\', \'body_end\');\n\n/**\n * Admin side UI\n */\nfunction wet_haystack_ui($event, $step)\n{\n  	global $prefs;\n\n	// new fulltext index\'s key name\n	$wet_haystack_key_name = \'wet_haystack\';\n\n  	// default TXP fulltext index\n  	$default_cols = array(\'Title\', \'Body\');\n\n  	$customfields = array();\n  	$max_cf = get_pref(\'max_custom_fields\', 10);\n	for ($i = 1; $i <= $max_cf; $i++) {\n  		if (!empty($prefs[\'custom_\'.$i.\'_set\'])) {\n			$customfields[\'custom_\'.$i] = $prefs[\'custom_\'.$i.\'_set\'];\n		}\n	}\n\n  	// a map of db columns which may participate in a fulltext index, and their UI names.\n  	$columns = array(\n  		\'AuthorID\' 	=> \'login_name\',\n  		\'Title\' 	=> \'title\',\n  		\'Body\'		=> \'body\',\n  		\'Excerpt\'	=> \'excerpt\',\n  		\'Keywords\'	=> \'keywords\',\n  		\'Category1\'	=> \'category1\',\n  		\'Category2\'	=> \'category2\',\n  		\'Section\'	=> \'section\'\n  	) + $customfields;\n\n  	// controller: build new fulltext index if requested\n	$saved = false;\n	if (gps(\'save\')) {\n  		$new_cols = gps(\'col\');\n  		if (!empty($new_cols)) {\n  			$new_cols = array_keys($new_cols);\n	  		$current_cols = wet_haystacked_cols($wet_haystack_key_name);\n	  		if (!empty($current_cols)) safe_alter(\"textpattern\", \"DROP INDEX $wet_haystack_key_name\");\n\n	  		$doit = array_diff($new_cols, $default_cols) + array_diff($default_cols, $new_cols);\n	  		if (!empty($doit)) {\n	  			@set_time_limit(0);\n	  			safe_alter(\"textpattern\", \"ADD FULLTEXT $wet_haystack_key_name (`\" . join(\'`, `\', doSlash($new_cols)) .\"`)\");\n	  		}\n  			update_lastmod();\n	  		set_pref(\'searchable_article_fields\', join(\',\', $new_cols), \'publish\', 2);\n	  		$saved = true;\n  		}\n  	}\n\n  	// model: retrieve current fulltext index from database\n  	$indexed_cols = wet_haystacked_cols($wet_haystack_key_name);\n  	if (empty($indexed_cols)) $indexed_cols = $default_cols;\n\n	// view: show current fulltext index, request new columns\n  	pagetop(gTxt(\'wet_haystack\'), ($saved ? gTxt(\'preferences_saved\') : \'\'));\n\n	foreach ($columns as $column => $label) {\n		$checked = in_array($column, $indexed_cols);\n		$check[] = graf(checkbox(\"col[$column]\", 1, $checked, \'\', \"col-$column\").\"<label for=\'col-$column\'>\".gTxt($label).\"</label>\");\n	}\n\n	echo \"<div class=\'wet_modal\'></div>\".\n		form(\n		n.hed(gTxt(\'wet_haystack_select_columns\'), 3).\n		n.\'<div id=\"wet_haystack_columns\">\'. // sorry, no fieldset. could not make overflow: auto work on fieldsets\n		n.join(n, $check).\n		n.\'</div>\'.\n		n.eInput(\'plugin_prefs.wet_haystack\').\n		n.graf(\n			fInput(\'submit\', \'save\', gTxt(\'save\'), \'smallerbox\', \'\', \'\', \'\', \'\', \'wet_haystack_save\').\n			href(gTxt(\'cancel\'), \'?event=plugin\', \' id=\"wet_haystack_cancel\"\'),\n			\' id=\"wet_haystack_buttons\"\'),\n        \'\', \'\', \'post\', \'wet_modal\'\n	);\n}\n\n/**\n *  Return array of currently indexed column names\n */\nfunction wet_haystacked_cols($key_name)\n{\n	$indexed_cols = array();\n	$indexes = safe_show(\'INDEX\', \'textpattern\');\n	foreach ($indexes as $index) {\n		if(($index[\'Index_type\'] == \'FULLTEXT\') && ($index[\'Key_name\'] == $key_name)) {\n			$indexed_cols[] = $index[\'Column_name\'];\n		}\n	}\n	return $indexed_cols;\n}\n\n/**\n * Beautfying...\n */\nfunction wet_haystack_style($event, $step)\n{\n	echo n.\'<style type=\"text/css\">\ndiv.wet_modal{background-color:black;opacity:0.2;position:absolute;top:0;left:0;width:100%;height:100%}\nform.wet_modal{z-index:1000;width:20em;position:absolute;top:30px;left:50%;margin-left:-10em;background-color:white;padding:20px;border:2px solid #fc3;}\nform.wet_modal h3{border-bottom: 1px solid #ddd;padding-bottom:2px}\n#wet_haystack_save.busy{color:#999;}\n#wet_haystack_cancel{margin-left: 1em;}\n#wet_haystack_buttons{border-top: 1px solid #ddd;padding-top:5px;margin-top:5px;}\n#wet_haystack_columns{border:none;padding:0;overflow:auto;max-height:300px;}\n</style>\n<!--[if gte IE 5]>\n<style type=\"text/css\">div.wet_modal{filter: alpha(opacity = 20);}</style>\n<![endif]-->\'\n	.n;\n}\n\nfunction wet_haystack_bhvr($event, $step)\n{\n	$script = <<<EOS\n\n<script type=\"text/javascript\">\n$(document).ready(function() {\n	$(\"input#wet_haystack_save\").click( function() {\n		$(this).attr(\"value\", \"%wet_haystack_please_wait%\");\n		$(this).addClass(\"busy\");\n		return true;\n	});\n});\n</script>\n\nEOS;\n\n	echo str_replace(\'%wet_haystack_please_wait%\', gTxt(\'wet_haystack_please_wait\'), $script);\n}\n', '\nglobal $textarray;\n\n/**\n *  User-editable strings\n */\n$textarray[\'wet_haystack\'] = \'Fulltext Index\';\n$textarray[\'wet_haystack_select_columns\'] = \'Select searchable columns\';\n$textarray[\'wet_haystack_please_wait\'] = \'Please wait…\';\n\n// == STOP EDITING! ==\n\n// Hook into the admin side of highly privileged users\nadd_privs(\'wet_haystack\', \'1\');\nadd_privs(\'plugin_prefs.wet_haystack\', \'1\');\nregister_callback(\'wet_haystack_ui\', \'plugin_prefs.wet_haystack\');\nregister_callback(\'wet_haystack_style\', \'admin_side\', \'head_end\');\nregister_callback(\'wet_haystack_bhvr\', \'admin_side\', \'body_end\');\n\n/**\n * Admin side UI\n */\nfunction wet_haystack_ui($event, $step)\n{\n  	global $prefs;\n\n	// new fulltext index\'s key name\n	$wet_haystack_key_name = \'wet_haystack\';\n\n  	// default TXP fulltext index\n  	$default_cols = array(\'Title\', \'Body\');\n\n  	$customfields = array();\n  	$max_cf = get_pref(\'max_custom_fields\', 10);\n	for ($i = 1; $i <= $max_cf; $i++) {\n  		if (!empty($prefs[\'custom_\'.$i.\'_set\'])) {\n			$customfields[\'custom_\'.$i] = $prefs[\'custom_\'.$i.\'_set\'];\n		}\n	}\n\n  	// a map of db columns which may participate in a fulltext index, and their UI names.\n  	$columns = array(\n  		\'AuthorID\' 	=> \'login_name\',\n  		\'Title\' 	=> \'title\',\n  		\'Body\'		=> \'body\',\n  		\'Excerpt\'	=> \'excerpt\',\n  		\'Keywords\'	=> \'keywords\',\n  		\'Category1\'	=> \'category1\',\n  		\'Category2\'	=> \'category2\',\n  		\'Section\'	=> \'section\'\n  	) + $customfields;\n\n  	// controller: build new fulltext index if requested\n	$saved = false;\n	if (gps(\'save\')) {\n  		$new_cols = gps(\'col\');\n  		if (!empty($new_cols)) {\n  			$new_cols = array_keys($new_cols);\n	  		$current_cols = wet_haystacked_cols($wet_haystack_key_name);\n	  		if (!empty($current_cols)) safe_alter(\"textpattern\", \"DROP INDEX $wet_haystack_key_name\");\n\n	  		$doit = array_diff($new_cols, $default_cols) + array_diff($default_cols, $new_cols);\n	  		if (!empty($doit)) {\n	  			@set_time_limit(0);\n	  			safe_alter(\"textpattern\", \"ADD FULLTEXT $wet_haystack_key_name (`\" . join(\'`, `\', doSlash($new_cols)) .\"`)\");\n	  		}\n  			update_lastmod();\n	  		set_pref(\'searchable_article_fields\', join(\',\', $new_cols), \'publish\', 2);\n	  		$saved = true;\n  		}\n  	}\n\n  	// model: retrieve current fulltext index from database\n  	$indexed_cols = wet_haystacked_cols($wet_haystack_key_name);\n  	if (empty($indexed_cols)) $indexed_cols = $default_cols;\n\n	// view: show current fulltext index, request new columns\n  	pagetop(gTxt(\'wet_haystack\'), ($saved ? gTxt(\'preferences_saved\') : \'\'));\n\n	foreach ($columns as $column => $label) {\n		$checked = in_array($column, $indexed_cols);\n		$check[] = graf(checkbox(\"col[$column]\", 1, $checked, \'\', \"col-$column\").\"<label for=\'col-$column\'>\".gTxt($label).\"</label>\");\n	}\n\n	echo \"<div class=\'wet_modal\'></div>\".\n		form(\n		n.hed(gTxt(\'wet_haystack_select_columns\'), 3).\n		n.\'<div id=\"wet_haystack_columns\">\'. // sorry, no fieldset. could not make overflow: auto work on fieldsets\n		n.join(n, $check).\n		n.\'</div>\'.\n		n.eInput(\'plugin_prefs.wet_haystack\').\n		n.graf(\n			fInput(\'submit\', \'save\', gTxt(\'save\'), \'smallerbox\', \'\', \'\', \'\', \'\', \'wet_haystack_save\').\n			href(gTxt(\'cancel\'), \'?event=plugin\', \' id=\"wet_haystack_cancel\"\'),\n			\' id=\"wet_haystack_buttons\"\'),\n        \'\', \'\', \'post\', \'wet_modal\'\n	);\n}\n\n/**\n *  Return array of currently indexed column names\n */\nfunction wet_haystacked_cols($key_name)\n{\n	$indexed_cols = array();\n	$indexes = safe_show(\'INDEX\', \'textpattern\');\n	foreach ($indexes as $index) {\n		if(($index[\'Index_type\'] == \'FULLTEXT\') && ($index[\'Key_name\'] == $key_name)) {\n			$indexed_cols[] = $index[\'Column_name\'];\n		}\n	}\n	return $indexed_cols;\n}\n\n/**\n * Beautfying...\n */\nfunction wet_haystack_style($event, $step)\n{\n	echo n.\'<style type=\"text/css\">\ndiv.wet_modal{background-color:black;opacity:0.2;position:absolute;top:0;left:0;width:100%;height:100%}\nform.wet_modal{z-index:1000;width:20em;position:absolute;top:30px;left:50%;margin-left:-10em;background-color:white;padding:20px;border:2px solid #fc3;}\nform.wet_modal h3{border-bottom: 1px solid #ddd;padding-bottom:2px}\n#wet_haystack_save.busy{color:#999;}\n#wet_haystack_cancel{margin-left: 1em;}\n#wet_haystack_buttons{border-top: 1px solid #ddd;padding-top:5px;margin-top:5px;}\n#wet_haystack_columns{border:none;padding:0;overflow:auto;max-height:300px;}\n</style>\n<!--[if gte IE 5]>\n<style type=\"text/css\">div.wet_modal{filter: alpha(opacity = 20);}</style>\n<![endif]-->\'\n	.n;\n}\n\nfunction wet_haystack_bhvr($event, $step)\n{\n	$script = <<<EOS\n\n<script type=\"text/javascript\">\n$(document).ready(function() {\n	$(\"input#wet_haystack_save\").click( function() {\n		$(this).attr(\"value\", \"%wet_haystack_please_wait%\");\n		$(this).addClass(\"busy\");\n		return true;\n	});\n});\n</script>\n\nEOS;\n\n	echo str_replace(\'%wet_haystack_please_wait%\', gTxt(\'wet_haystack_please_wait\'), $script);\n}\n', 'fcb9d1ddc245f94c78932cb9220ab88d', '', '', 3, 5, 1);
